INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (403, ' ', 'En relación con el numeral 9 del presente artículo bajo ninguna circunstancia el ejercicio del grafiti, justificará por sí solo, el uso de la fuerza, ni la incautación de los instrumentos para su realización.', 1, 292, 136, '29/11/17', 'In relation to numeral 9 of this article under any circumstance the exercise of graffiti will justify, by itself, the use of force, nor the seizure of the instruments for its realization.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (405, 'Ciclorrutas y carriles exclusivos para bicicletas', 'Los alcaldes distritales o municipales promoverán el uso de medios alternativos de transporte que permitan la movilidad, estableciendo un sistema de ciclo rutas y carriles exclusivos de bicicletas, como una alternativa permanente de movilidad urbana o rural teniendo en cuenta en especial los corredores más utilizados en el origen y destino diario de los habitantes del municipio.', 1, 184, 138, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (406, 'Reglamentación de ciclorrutas y carriles exclusivos para bicicletas', 'Los alcaldes distritales y municipales podrán reglamentar el uso de ciclo rutas y carriles exclusivos para bicicletas, en su jurisdicción. En los casos de municipios que conurben, los alcaldes podrán acordar una reglamentación conjunta para el desplazamiento entre los respectivos municipios.', 1, 185, 138, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
