INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (475, ' ', 'Para materializar la medida correctiva de que trata el presente artículo, la Policía Nacional podrá trasladar de inmediato al infractor al lugar destinado para tal efecto.', 1, 291, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (477, 'Expulsión de domicilio', 'Consiste en expulsar del domicilio por solicitud de su morador, poseedor o tenedor, a quien reside en el mismo, en contra de su voluntad, y que haya ingresado bajo su consentimiento, haya permanecido gratuitamente y no tenga derecho legítimo de permanecer en él.', 1, 219, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (479, 'Decomiso', 'Es la privación de manera definitiva de la tenencia o la propiedad de bienes muebles no sujetos a registro, utilizados por una persona en comportamientos contrarios a las normas de convivencia, mediante acto motivado.', 1, 221, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (481, ' ', 'Cuando se trate de bebidas, comestibles y víveres en general que se encuentren en mal estado, o adulterados, o medicamentos vencidos o no autorizados por las autoridades de salud, o elementos peligrosos, el inspector de Policía ordenará su destrucción, sin perjuicio de lo que disponga la ley penal.', 1, 290, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (483, 'Multas', 'Es la imposición del pago de una suma de dinero en moneda colombiana, cuya graduación depende del comportamiento realizado, según la cual varía el monto de la multa. Así mismo, la desobediencia, resistencia, desacato, o reiteración del comportamiento contrario a la convivencia, incrementará el valor de la multa, sin perjuicio de los intereses causados y el costo del cobro coactivo.\nLas multas se clasifican en generales y especiales.\nLas multas generales se clasifican de la siguiente manera:', 1, 222, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (484, ' ', 'Las multas serán consignadas en la cuenta que para el efecto disponga las administraciones distritales y municipales, y se destinarán a proyectos pedagógicos y de prevención en materia de seguridad, así como al cumplimiento de aquellas medidas correctivas impuestas por las autoridades de Policía cuando su materialización deba ser inmediata, sin perjuicio de las acciones que deban adelantarse contra el infractor, para el cobro de la misma. En todo caso, mínimo el sesenta por ciento (60%) del Fondo deberá ser destinado a la cultura ciudadana, pedagogía y prevención en materia de seguridad.\nCuando los Uniformados de la Policía Nacional tengan conocimiento de la ocurrencia de un comportamiento, que admita la imposición de multa general, impondrán orden de comparendo al infractor, evidenciando el hecho.\nEs deber de toda persona natural o jurídica, sin perjuicio de su condición económica y social, pagar las multas, salvo que cumpla la medida a través de la participación en programa comunitario o actividad pedagógica de convivencia, de ser aplicable. A la persona que pague la multa durante los cinco (5) días hábiles siguientes a la expedición del comparendo, se le disminuirá el valor de la multa en un cincuenta (50%) por ciento, lo cual constituye un descuento por pronto pago.\nA cambio del pago de la Multa General tipo 1 y 2 la persona podrá, dentro de un plazo máximo de cinco (5) días hábiles siguientes a la expedición del comparendo, solicitar a la autoridad de Policía que se conmute la multa por la participación en programa comunitario o actividad pedagógica de convivencia.\nSi la persona no está de acuerdo con la aplicación de la multa señalada en la orden de comparendo o con el cumpl imiento de la medida de participación en programa comunitario o actividad pedagógica de convivencia, cuando este aplique, podrá presentarse dentro de los tres (3) días hábiles siguientes ante la autoridad competente, para objetar la medida mediante el procedimiento establecido en este Código.\nUna vez liquidadas y comunicadas, si las multas no fueren pagadas dentro del mes siguiente, el funcionario competente deberá reportar la existencia de la deuda al Boletín de Responsables Fiscales de la Contraloría General de la República y así mismo deberá reportar el pago de la deuda.\nLa administración distrital o municipal podrá reglamentar la imposición de la medida correctiva de participación en programa pedagógico para los comportamientos contrarios a la convivencia que admitan multa tipo 1 y 2, en reemplazo de la multa.', 1, 287, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
