INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (499, 'Inutilización de bienes', 'Consiste en la inhabilitación total de los bienes empleados para actividades ilícitas que atenten contra los recursos naturales, o ingresen, permanezcan, operen, en áreas protegidas y de especial importancia ecológica.\nLo anterior no implica que el infractor, propietario, tenedor o poseedor, impute cualquier responsabilidad patrimonial por acción o por omisión al Estado o a sus agentes.\nPara la aplicación de esta medida se documentará la actuación policial y después de la inutilización, se infor mará a las autoridades competentes.', 1, 233, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
