INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (559, 'Alcance penal', 'El que desacate, sustraiga u omita el cumplimiento de las decisiones u órdenes de las autoridades de Policía, dispuestas al finalizar el proceso verbal abreviado o inmediato, incurrirá en conducta punible de conformidad con la legislación penal.', 1, 267, 145, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (560, 'Recuperación especial de predios', 'En los procesos administrativos que adelante la Agencia Nacional de Tierras, o quien haga sus veces, encaminados a la recuperación de bienes baldíos y bienes fiscales patrimoniales, así como en aquellos que concluyan con la orden de restitución administrativa de bienes a víctimas o beneficiarios de programas de la Agencia, la ejecución del acto administrativo correspondiente será efectuada por la autoridad de Policía dentro de los diez (10) días siguientes al recibo de la solicitud de la Agencia Nacional de Tierras, una vez el acto se encuentre ejecutoriado y en firme.', 1, 268, 145, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (561, 'Caducidad y prescripción', 'Cuando se trate de hechos de perturbación de bienes de uso público, bienes fiscales, zonas de reserva forestal, bienes de propiedad privada afectados al espacio público, bienes de las empresas de servicios públicos, o bienes declarados de utilidad pública o de interés social, cultural, arquitectónico o histórico, no existe caducidad de la acción policiva. La autoridad de Policía comunicará la iniciación de la actuación al personero, quien podrá pedir directamente, o por intermedio de delegado, que se le tenga como interesado en el proceso.\nLas medidas correctivas prescribirán en cinco (5) años, a partir de la fecha en que quede en firme la decisión de las autoridades de Policía en el proceso único de Policía.', 1, 269, 145, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (563, 'Nulidades', 'Los intervinientes en el proceso podrán pedir únicamente dentro de la audiencia, la nulidad del mismo por violación del debido proceso consagrado en el artículo 29 de la Constitución Política, solicitud que se resolverá de plano. Contra esta decisión solo procederá el recurso de reposición, que se resolverá dentro de la misma audiencia.', 1, 271, 145, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
