INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (567, 'Costas', 'En los procesos de Policía no habrá lugar al pago de costas.', 1, 273, 145, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (569, 'Conciliación', 'La conciliación en materia de convivencia procederá ante la autoridad de Policía que conozca del caso, en cualquier etapa del trámite del procedimiento o en el momento en que se presente el desacuerdo o conflicto de convivencia.\nUna vez escuchados quienes se encuentren en desacuerdo o conflicto, la autoridad de Policía o el conciliador, propondrá fórmulas de solución que aquellos pueden acoger o no. De realizarse el acuerdo, se suscribirá el acta de conciliación, donde se consignarán las obligaciones a cargo de cada uno de los interesados, lo cual hará tránsito a cosa juzgada y prestará mérito ejecutivo ante las autoridades judiciales competentes.\nLas medidas correctivas de competencia de los comandantes de estación, subestación o centro de atención inmediata de Policía, no son susceptibles de conciliación.\nNo son conciliables los comportamientos que infringen o resultan contrarios a las normas urbanísticas, ambientales, sanitarias, del uso del espacio público, del ejercicio de la actividad económica, de la libertad de circulación, de las interacciones entre las personas y las autoridades, los que afectan la integridad de niños, niñas y adolescentes, del ejercicio de la prostitución, y del derecho de reunión.', 1, 275, 146, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (571, ' ', 'En los procedimientos a que hace referencia el Título VII del Libro II, será obligatorio la invitación a conciliar.', 1, 287, 146, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (572, 'Conciliadores y mediadores', 'Además de las autoridades de Policía, pueden ser conciliadores o mediadores en el sector urbano o rural, los jueces de paz, las personerías, los centros de conciliación de universidades, las cámaras de comercio del país y demás centros de conciliación del sector privado, siempre que el servicio sea gratuito.', 1, 277, 146, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (574, 'Programa de educación y promoción del Código', 'El Gobierno nacional, a través de las autoridades competentes, deberá diseñar programas, actividades y campañas de promoción en todo el territorio nacional, de las disposiciones más relevantes contenidas en el presente Código, especialmente de los comportamientos contrarios a la convivencia y las consecuencias que se derivan de su realización, con el fin de que la ciudadanía conozca y se actualice en torno a los aspectos trascendentales de esta ley.\nAsí mismo deberá adelantar jornadas de capacitación y formación del nuevo Código de Policía y convivencia a las autoridades de Policía, a partir de su promulgación.\nDe igual forma, a través del Ministerio de Educación, desarrollará programas para el fomento de competencias que fortalezcan la cultura ciudadana y la convivencia así como el respeto por las normas y las autoridades, en concordancia con los lineamientos definidos en la Ley 1013 de 2006 y la Ley 1732 de 2015.\nEstos programas serán implementados por las Instituciones Educativas en el marco de su autonomía escolar y su contenido.', 1, 279, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (575, 'Integración de sistemas de vigilancia', 'La información, imágenes, y datos de cualquier índole captados y/o almacenados por los sistemas de video o los medios tecnológicos que estén ubicados en el espacio público, o en lugares abiertos al público, serán considerados como públicos y de libre acceso, salvo que se trate de información amparada por reserva legal.\nLos sistemas de video y medios tecnológicos, o los que hagan sus veces, de propiedad privada o pública, a excepción de los destinados para la Defensa y Seguridad Nacional, que se encuentren instalados en espacio público, áreas comunes, lugares abiertos al público o que siendo privados trasciendan a lo público, se enlazará de manera permanente o temporal a la red que para tal efecto disponga la Policía Nacional, de acuerdo con la reglamentación que para tal efecto expida el Gobierno nacional.', 1, 280, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (576, ' ', 'En tratándose de sistemas instalados en áreas comunes, lugares abiertos al público o que siendo privados trasciendan a lo público, se requerirá para el enlace a que hace referencia el presente artículo, la autorización previa por parte de quien tenga la legitimidad para otorgarla.', 1, 287, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (578, 'Aplicación de la ley', 'Los procedimientos por contravenciones al régimen de Policía, así como los procedimientos administrativos sustituidos por la presente ley, que a la fecha de la entrada en vigencia de la misma se estén surtiendo, serán adelantados hasta su finalización, conforme a la legislación vigente a la fecha de ocurrencia de los hechos que motivaron su iniciación.', 1, 282, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (579, 'Concordancias', 'Las disposiciones en materia de circulación o movilidad, infancia y adolescencia, salud, ambiente, minería, recursos naturales y protección animal establecidas en el presente Código, serán aplicadas en concordancia con la legislación vigente.', 1, 283, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (581, 'Derogatorias', 'El presente Código deroga todas las disposiciones que le sean contrarias, en especial el Decreto-ley 1355 de 1970, la Ley 1356 de 2009 excepto los artículos 4° y del 218A al 218L; el Decreto número 522 de 1971; la Ley 232 de 1995; el artículo 108 de la Ley 388 de 1997; los artículos 1° y 2° de la Ley 810 de 2003; artículo 12 numeral 2, artículo 17, 18, 21, 22, 23, 24, 25, 26, 27, 28, 29, 31, 33, 35 y 36 del Decreto número 2876 de 1984; artículo 26 y último inciso o párrafo del artículo 10 de la Ley 679 de 2001, en razón a que se aplicará el proceso verbal abreviado establecido en el presente Código; artículos 5°, 6°, 7° y 12 de la Ley 1259 de 2008; Ley 746 de julio 19 de 2002; artículo 24, 29 e inciso final del artículo 31 de la Ley 1335 de 2009; y los artículos 12 y 13 de la Ley 140 de 1994.\nEste Código no modifica ni deroga ninguna norma del Código Penal.', 1, 285, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (582, 'Vigencia', 'La presente ley regirá seis (6) meses después de su promulgación.', 1, 286, 147, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (398, ' ', 'Para efectos de este Código se entiende por bienes de uso público los que permanentemente están al uso, goce, disfrute de todos los habitantes de un territorio, como por ejemplo los parques, caminos o vías públicas y las aguas que corren.', 1, 290, 136, '29/11/17', 'For the purposes of this Code, public goods are those that are permanently used and enjoyed by all the inhabitants of a territory, such as parks, roads or public roads and running water.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (400, ' ', 'Las empresas de servicios públicos pueden ocupar de manera temporal el espacio público para la instalación o mantenimiento de redes y equipamientos, con el respeto de las calidades ambientales y paisajísticas del lugar, y la respectiva licencia de intervención expedida por la autoridad competente.', 1, 289, 136, '29/11/17', 'Public service companies may occupy temporarily the public space for the installation or maintenance of networks and equipment, with respect for the environmental and landscape qualities of the place, and the respective intervention license issued by the competent authority.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (227, 'Atención y control de incendios en actividades que involucran aglomeraciones de público complejas', 'Las instituciones de bomberos del país, definirán las condiciones de los planes contra incendios, con fundamento en los siguientes factores:', 1, 108, 118, '29/11/17', 'The country''s fire-fighting institutions will define the conditions of fire-fighting plans, based on the following factors:', 'Attention and control of fires in activities that involve complex public gatherings');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (228, ' ', 'En el evento en que estas instituciones no dispongan de los recursos humanos y logísticos para el cubrimiento del acto o evento, el servicio de operación de atención y control de incendios podrá ser prestado por una persona jurídica de derecho privado o público, previamente certificada ante dicha unidad.', 1, 287, 118, '29/11/17', 'In the event that these institutions do not have the human and logistical resources to cover the act or event, the operation service of attention and fire control may be provided by a private or public legal entity, previously certified before that unit.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (230, ' ', 'Si una de las variables generadoras de riesgo para alguna reunión descrita en el presente artículo, cambia, se deberá inscribir en el medio destinado para tal efecto.', 1, 289, 118, '29/11/17', 'If one of the risk-generating variables for any meeting described in this article changes, it must be registered in the medium designated for that purpose.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (231, ' ', 'El plan de emergencia y contingencia adecuado para dicha actividad exige su registro, para ser evaluado y aprobado, so pena de la aplicación de las medidas correctivas a que haya lugar.', 1, 290, 118, '29/11/17', 'The adequate emergency and contingency plan for this activity requires its registration, to be evaluated and approved, under penalty of the application of the corrective measures that may be necessary.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (232, 'Lugares donde se realizan actividades que involucran aglomeraciones de público complejas', 'La autoridad que haya expedido el permiso para la realización de la actividad que involucra aglomeraciones de público complejas, podrá impedirlo cuando el recinto o el lugar donde vaya a realizarse no cumpla con los requisitos exigidos en las normas vigentes, si así lo solicitan las autoridades de Policía respectivas, una vez comprobado el incumplimiento de tales exigencias.', 1, 110, 118, '29/11/17', 'The authority that issued the permit for the realization of the activity that involves complex public gatherings, will be able to prevent it when the venue or place where it is to be carried out does not comply with the requirements demanded in the current regulations, if requested by the authorities of Police, once verified the breach of such requirements.', 'Places where activities that involve complex public gatherings are carried out');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (234, 'Supervisión de las actividades que involucran aglomeraciones de público complejas', 'Toda actividad que involucre aglomeraciones de público que requiera permiso, será supervisado e inspeccionado por la autoridad municipal, distrital o competente, con el fin de verificar el cumplimiento de las condiciones establecidas y su correcto desarrollo.', 1, 112, 118, '29/11/17', 'Any activity that involves groups of public that require permission, will be supervised and inspected by the municipal, district or competent authority, in order to verify the fulfillment of the established conditions and their correct development.', 'Supervision of activities that involve complex public gatherings');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (235, 'Ingreso del cuerpo de Policía', 'El personal uniformado de la Policía Nacional, podrá ingresar a los lugares en que se desarrollen actividades que involucren aglomeraciones de públicos complejas o no complejas, en cualquier momento y solamente para cumplir con su función.', 1, 113, 118, '29/11/17', 'The uniformed personnel of the National Police will be able to enter the places in which activities that involve agglomerations of complex or non-complex audiences are developed, at any time and only to fulfill their function.', 'Entry of the police force');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (237, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas:', 1, 287, 118, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (239, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas:', 1, 287, 118, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (240, 'Requisitos para la realización de actos o eventos en escenarios habilitados y no habilitados', 'Para todas las clases de aglomeraciones de público definidas en esta ley, la definición de escenarios habilitados y no habilitados, así como la autoridad pública competente para realizar la habilitación del escenario y el procedimiento previsto para el efecto de dicha habilitación serán los previstos en la Ley 1493 de 2011, sus disposiciones reglamentarias y las leyes que la modifiquen, adicionen o sustituyan.\nPara la realización de actos o eventos que involucren aglomeraciones de público complejas o no complejas, se deberá cumplir con los requisitos establecidos en el presente Código, además de los establecidos en la Ley 1493 de 2011 para el reconocimiento de los escenarios habilitados o no habilitados.\nEn todo caso, por los comportamientos contrarios a la convivencia señalados en el presente título, se impondrán las medidas correctivas establecidas en el presente Código mediante el procedimiento establecido en el mismo.', 1, 116, 118, '29/11/17', 'For all classes of public agglomerations defined in this law, the definition of enabled and unlicensed scenarios, as well as the competent public authority to carry out the enabling of the scenario and the procedure foreseen for the effect of said authorization shall be those foreseen in the Law 1493 of 2011, its regulatory provisions and the laws that modify, add or replace it.\r\nFor the performance of events or events involving complex or non-complex public gatherings, the requirements established in this Code must be complied with, in addition to those established in Law 1493 of 2011 for the recognition of scenarios enabled or not enabled.\r\nIn any case, for the behaviors contrary to the coexistence indicated in this title, the corrective measures established in this Code will be imposed through the procedure established therein. ', 'Requirements for the performance of acts or events in scenarios enabled and not enabled');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (242, 'Comportamientos contrarios a la posesión y mera tenencia de bienes inmuebles', 'Son aquellos contrarios a la posesión, la mera tenencia de los bienes inmuebles de particulares, bienes fiscales, bienes de uso público, bienes de utilidad pública o social, bienes destinados a prestación de servicios públicos. Estos son los siguientes:', 1, 118, 119, '29/11/17', 'They are those contrary to the possession, the mere possession of the real estate of individuals, fiscal goods, goods of public use, goods of public or social utility, goods destined to the rendering of public services. These are the following:', 'Behaviors contrary to the possession and mere possession of real estate');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (243, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 287, 119, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following corrective measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (244, 'Comportamientos co ntrarios al derecho de servidumbres', 'Los siguientes comportamientos son contrarios al derecho de servidumbre y por lo tanto no deben efectuarse:', 1, 119, 119, '29/11/17', 'The following behaviors are contrary to the right of easement and therefore should not be carried out:', 'Behaviors against easements right');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (245, ' ', 'Quien incurra en uno o más de los comportamientos señalados, será objeto de la aplicación de las siguientes medidas:', 1, 287, 119, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (246, 'Ejercicio de las acciones de protección de los bienes inmuebles', ' Para el ejercicio de la acción de Policía en el caso de la perturbación de los derechos de que trata este título, las siguientes personas, podrán instaurar querella ante el inspector de Policía, mediante el procedimiento único estipulado en este Código:', 1, 120, 119, '29/11/17', 'For the exercise of police action in the case of the disturbance of the rights referred to in this title, the following persons may file a complaint with the police inspector, through the sole procedure stipulated in this Code:', 'Exercise of actions to protect real estate');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (248, ' ', 'En estos procedimientos se deberá comunicar al propietario inscrito la iniciación de ellos sin perjuicio de que se lleve a cabo la diligencia prevista.', 1, 290, 119, '29/11/17', 'In these procedures, the registered owner must be informed of the initiation thereof, without prejudice to the diligence envisaged.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (250, ' ', 'Cuando por caso fortuito o fuerza mayor demostrados, excepcionalmente deba suspenderse la audiencia pública, la autoridad competente decretará el statu quo sobre los bienes objeto de la misma, dejando constancia y registro documental, fijando fecha y hora para su reanudación.', 1, 292, 119, '29/11/17', 'When by fortuitous case or force majeure demonstrated, exceptionally the public audience should be suspended, the competent authority will decree the status quo on the goods object of the same, leaving record and documentary record, fixing date and time for its resumption.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (252, ' ', 'La acción policial de protección a la posesión, la mera tenencia y servidumbres de los inmuebles de los particulares, caducará dentro de los cuatro (4) meses siguientes a la perturbación por ocupación ilegal.', 1, 287, 119, '29/11/17', 'The police action to protect possessions, the mere possession and easement of the private property, will expire within four (4) months following the disturbance due to illegal occupation.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (253, 'Acción preventiva por perturbación', 'Cuando se ejecuten acciones con las cuales se pretenda o inicie la perturbación de bienes inmuebles sean estos de uso público o privado ocupándolos por vías de hecho, la Policía Nacional lo impedirá o expulsará a los responsables de ella, dentro de las cuarenta y ocho (48) horas siguientes a la ocupación.\nEl querellante realizará las obras necesarias, razonables y asequibles para impedir sucesivas ocupaciones o intentos de hacerlas por vías de hecho, de conformidad con las órdenes que impartan las autoridades de Policía.', 1, 122, 119, '29/11/17', 'When actions are carried out with which the disturbance of immovable property is intended or initiated, whether for public or private use, by de facto means, the National Police shall prevent or expel those responsible for it, within forty-eight ( 48) hours following the occupation.\r\nThe complainant will perform the necessary, reasonable and affordable works to prevent successive occupations or attempts to make them by de facto means, in accordance with the orders issued by the police authorities. ', 'Preventive action due to disturbance');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (255, ' ', 'La medida de que trata el presente artículo garantizará el statu quo físico y jurídico del bien y deberá ser reportada a la oficina de registro de instrumentos públicos de la jurisdicción del inmueble.', 1, 287, 119, '29/11/17', 'The measure dealt with in this article will guarantee the physical and legal status quo of the property and must be reported to the registry office of public instruments of the jurisdiction of the property.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (256, 'Actividad económica', 'Es la actividad lícita, desarrollada por las personas naturales y jurídicas, en cualquier lugar y sobre cualquier bien, sea comercial, industrial, social, de servicios, de recreación o de entretenimiento; de carácter público o privado o en entidades con o sin ánimo de lucro, o similares o que siendo privados, sus actividades trasciendan a lo público.', 1, 124, 120, '29/11/17', 'It is the legal activity, developed by natural and legal persons, anywhere and on any property, be it commercial, industrial, social, service, recreation or entertainment; of public or private nature or in entities with or without profit motive, or similar or that being private, their activities transcend to the public.', 'Economic activity');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (257, ' ', 'Los alcaldes fijarán horarios para el ejercicio de la actividad económica en los casos en que esta actividad pueda afectar la convivencia, y en su defecto lo hará el gobernador.', 1, 287, 120, '29/11/17', 'The mayors will set schedules for the exercise of economic activity in cases where this activity may affect coexistence, and failing that will the governor will do it.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (259, ' ', 'Para el caso de los establecimientos de prestación de servicio de videojuegos, estos deberán cumplir lo dispuesto por la Ley 1554 de 2012 en su artículo 3°, o por las normas que la modifiquen o adicionen.', 1, 289, 120, '29/11/17', 'In the case of video game service provision establishments, they must comply with the provisions of Law 1554 of 2012 in their article 3, or by the regulations that modify or add them.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (260, ' ', 'Se respetarán los derechos adquiridos de los establecimientos legalmente constituidos.', 1, 290, 120, '29/11/17', 'The acquired rights of legally constituted establishments will be respected.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (261, 'Informe de registro en Cámaras de Comercio', 'Las Cámaras de Comercio permitirán el acceso permanente en tiempo real a la administración municipal o distrital correspondiente y a la Policía Nacional a las matrículas mercantiles registradas o modificadas.\nCorresponde a la administración municipal o distrital verificar que las actividades económicas estén autorizadas por la reglamentación de las normas de uso del suelo y las que la desarrollen o complementen, de la respectiva jurisdicción.', 1, 126, 120, '29/11/17', 'The Chambers of Commerce will allow permanent access in real time to the corresponding municipal or district administration and to the National Police to commercial registrations registered or modified.\r\nIt is the responsibility of the municipal or district administration to verify that the economic activities are authorized by the regulations of the land use and those that develop or complement it, of the respective jurisdiction. ', 'Registration report in Chambers of Commerce');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (374, 'Control de caninos potencialmente peligrosos en zonas comunales', 'En los conjuntos cerrados, urbanizaciones y edificios con régimen de propiedad horizontal, podrá prohibirse la permanencia de ejemplares caninos potencialmente peligrosos, a solicitud de cualquiera de los copropietarios o residentes y por decisión calificada de tres cuartas partes de las asambleas o de las juntas directivas de la copropiedad.', 1, 171, 134, '29/11/17', 'In the closed complexes, urbanizations and buildings with horizontal property regime, the permanence of potentially dangerous canine specimens may be prohibited, at the request of any of the co-owners or residents and by a qualified decision of three quarters of the assemblies or of the boards of directors of the co-ownership.', 'Control of potentially dangerous canines in communal areas');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (375, 'Albergues para caninos potencialmente peligrosos', 'Las instalaciones de albergues para los ejemplares de razas potencialmente peligrosas, deben tener las siguientes ca racterísticas: las paredes y vallas ser suficientemente altas y consistentes y estar fijadas a fin de soportar el peso y la presión del animal; puertas de las instalaciones resistentes y efectivas como el resto del contorno y con un diseño que evite que los animales puedan desencajar o abrir ellos mismos los mecanismos de seguridad. El recinto estará convenientemente señalizado con la advertencia que hay un perro peligroso en el lugar.', 1, 172, 134, '29/11/17', 'Shelter facilities for potentially dangerous breed specimens should have the following characteristics: the walls and fences should be sufficiently tall and consistent and be fixed in order to support the weight and pressure of the animal; doors of the facilities resistant and effective as the rest of the contour and with a design that avoids that the animals can open themselves the mechanisms of security. The enclosure will be conveniently marked with the warning that there is a dangerous dog in the place.', 'Shelters for potentially dangerous canines');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (376, 'Cesión de la propiedad de caninos potencialmente peligrosos', 'Toda compra, venta, traspaso, donación o cualquier cesión del derecho de propiedad, sobre el ejemplar canino clasificado como potencialmente peligroso, se anotará en el registro del censo de caninos potencialmente peligrosos, y en caso de cambio de distrito, municipio o localidad del ejemplar se inscribirá nuevamente donde se ubique la nueva estancia, con la copia del registro anterior.', 1, 173, 134, '29/11/17', 'Any purchase, sale, transfer, donation or any assignment of the right of ownership, on the dog specimen classified as potentially dangerous, shall be recorded in the register of the census of potentially dangerous canines, and in case of change of district, municipality or locality of the specimen It shall be entered again where the new residence is located, with the copy of the previous record.', 'Ownership of potentially dangerous canines');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (378, ' ', 'Lo dispuesto en el presente artículo no aplica para los animales utilizados en la prestación de los servicios de vigilancia privada y en labores de seguridad propias de la fuerza pública, cuyo manejo se regirá por las normas especiales sobre la materia.', 1, 287, 134, '29/11/17', 'The provisions of this article do not apply to animals used in the provision of private security services and security tasks of the public force, whose management will be governed by special rules on the matter.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (379, 'Tasas del registro de caninos potencialmente peligrosos', 'Autorízase a los municipios para definir las tasas que se cobrarán a los propietarios por el registro en el censo de caninos potencialmente peligrosos, la expedición del permiso correspondiente, así como las condiciones por las cuales se suspenda o cancele el permiso para poseer ejemplares caninos potencialmente peligrosos.', 1, 175, 134, '29/11/17', 'The municipalities are authorized to define the fees that will be charged to the owners for the registration in the census of potentially dangerous canines, the issuance of the corresponding permit, as well as the conditions for which the permit to possess potentially dangerous canine specimens is suspended or canceled. .', 'Registration fees for potentially dangerous canines');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (381, ' ', 'A quien incurra en uno o más de los comportamientos señalados en el presente artículo, se le aplicarán las siguientes medidas correctivas:', 1, 289, 134, '29/11/17', 'Whoever incurs one or more of the behaviors indicated in this article, the following corrective measures will be applied:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (383, ' ', 'Si un ejemplar canino potencialmente peligroso ataca a una persona infligiéndole lesiones permanentes de cualquier tipo, su propietario será sancionado por la autoridad municipal competente con Multa General tipo 4 y estará obligado a pagar por todos los daños causados a la persona. Si el animal es reincidente se procederá al decomiso, siendo un veterinario, preferiblemente etólogo, el que determine el tratamiento a seguir.', 1, 291, 134, '29/11/17', 'If a potentially dangerous canine specimen attacks a person inflicting permanent injuries of any kind, its owner will be sanctioned by the competent municipal authority with General Type 4 fine and will be obliged to pay for all the damages caused to the person. If the animal is a repeated offender, confiscation will be carried out, with a veterinarian, preferably an ethologist, determining the treatment to be followed.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (385, 'Comportamientos contrarios a la integridad urbanística', 'Los siguientes comportamientos, relacionados con bienes inmuebles de particulares, bienes fiscales, bienes de uso público y el espacio público, son contrarios a la convivencia pues afectan la integridad urbanística y por lo tanto no deben realizarse, según la modalidad señalada:', 1, 177, 135, '29/11/17', 'The following behaviors, related to real estate of individuals, fiscal goods, goods for public use and public space, are contrary to coexistence as they affect the urban integrity and therefore should not be carried out, according to the modality indicated:', 'Behaviors contrary to urban integrity');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (389, ' ', 'En el caso de demolición o intervención de los bienes de interés cultural, de uno colindante, uno ubicado en su área de influencia o un bien arqueológico, previo a la expedición de la licencia, se deberá solicitar la autorización de intervención de la autoridad competente.', 1, 292, 135, '29/11/17', 'In the case of demolition or intervention of cultural assets of an adjacent one, located in its area of influence or an archaeological asset, prior to the issuance of the license, the intervention authority of the competent authority must be requested.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (390, ' ', 'Cuando el infractor incumple la orden de demolición, mantenimiento o reconstrucción, una vez agotados todos los medios de ejecución posibles, la administración realizará la actuación urbanística omitida a costa del infractor.', 1, 293, 135, '29/11/17', 'When the violator fails to comply with the demolition, maintenance or reconstruction order, once all possible means of execution have been exhausted, the administration will carry out the urban action omitted at the expense of the offender.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (264, ' ', 'Como consecuencia de lo anterior, los alcaldes distritales o municipales podrán establecer horarios de funcionamiento para los establecimientos antes mencionados, y determinar las medidas correctivas por su incumplimiento, de conformidad con lo previsto en el presente Código.', 1, 289, 120, '29/11/17', 'As a consequence of the foregoing, the district or municipal mayors may establish operating hours for the aforementioned establishments, and determine corrective measures for non-compliance, in accordance with the provisions of this Code.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (265, ' ', 'Facúltese a las autoridades de Policía y Comandantes de Estación de Policía para ingresar a los establecimientos mencionados en el presente artículo con el fin de verificar el cumplimiento de horarios dispuestos por los alcaldes distritales o municipales y para imponer las medidas correctivas que correspondan.', 1, 290, 120, '29/11/17', 'Approve the Police authorities and Police Station Commanders to enter the establishments mentioned in this article in order to verify the compliance of schedules arranged by the district or municipal mayors and to impose corrective measures that correspond.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (266, 'Requisitos para cumplir actividades económicas', 'Es obligatorio, para el ejercicio de cualquier actividad: comercial, industrial, de servicios, social, cultural, de recreación, de entretenimiento, de diversión; con o sin ánimo de lucro, o que siendo privadas, trasciendan a lo público; que se desarrolle o no a través de establecimientos abiertos o cerrados al público, además de los requisitos previstos en normas especiales, cumplir previamente a la iniciación de la actividad económica los siguientes requisitos:', 1, 128, 120, '29/11/17', 'It is mandatory, for the exercise of any activity: commercial, industrial, service, social, cultural, recreational, entertainment, fun; with or without profit motive, or that being private, transcends to the public thing; whether or not it is developed through establishments that are open or closed to the public, in addition to the requirements set forth in special regulations, to comply with the following requirements prior to the initiation of economic activity:', 'Requirements to fulfill economic activities');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (267, ' ', 'Los anteriores requisitos podrán ser verificados por las autoridades de Policía en cualquier momento, para lo cual estas podrán ingresar por iniciativa propia a los lugares señalados, siempre que estén en desarrollo de sus actividades económicas.', 1, 289, 120, '29/11/17', 'The above requirements may be verified by the police authorities at any time, for which they may enter on their own initiative to the places indicated, provided they are developing their economic activities.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (271, ' ', 'Los estacionamientos o parqueaderos ubicados en inmuebles de uso público, como parques, zonas verdes y escenarios deportivos o culturales, sólo podrán ser utilizados para el estacionamiento de vehículos con fines relativos a la destinación de tales bienes.', 1, 287, 121, '29/11/17', 'Parking lots or parking spaces located in buildings for public use, such as parks, green areas and sports or cultural venues, may only be used for the parking of vehicles for purposes related to the destination of such goods.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (273, 'Comportamientos que afectan la actividad económica', 'Los comportamientos que afectan la activid ad económica comprenden comportamientos relacionados con el cumplimiento de la normatividad, comportamientos relacionados con la seguridad y la tranquilidad, comportamientos relacionados con el ambiente y la salud pública.', 1, 132, 122, '29/11/17', 'Behaviors that affect economic activity include behavior related to compliance with regulations, behaviors related to safety and tranquility, behaviors related to the environment and public health.', 'Behaviors that affect economic activity');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (275, ' ', 'En los comportamientos señalados en los numerales 7 y 11, se impondrán las medidas correctivas y se pondrá en conocimiento de manera inmediata a la autoridad competente para aplicar lo establecido en las Leyes 679 de 2001, 1236 de 2008, 1329 de 2009 y las normas que las adicionen o modifiquen.', 1, 289, 122, '29/11/17', 'In the behavior indicated in numerals 7 and 11, corrective measures will be imposed and the competent authority will be immediately informed to apply the provisions of Laws 679 of 2001, 1236 of 2008, 1329 of 2009 and the regulations that add or modify them.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (277, ' ', 'Se mantendrá la aplicación de las medidas correctivas si se continúa desarrollando en el lugar la misma actividad económica que dio lugar a su imposición, aun cuando se modifique o cambie la no menclatura, el nombre del establecimiento, su razón social, propietario, poseedor o tenedor del mismo.', 1, 291, 122, '29/11/17', 'The application of the corrective measures will be maintained if the same economic activity that led to its imposition continues to be developed in the place, even if the nomenclature is modified or changed, the name of the establishment, its social reason, owner, possessor or holder of the same.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (279, ' ', 'Cuando se aplique la medida de suspensión temporal de actividad porque el responsable del establecimiento no permitió el ingreso de autoridades de Policía, la medida se extenderá hasta tanto se autorice el ingreso de la autoridad que en ejercicio de su función o actividad de Policía, requiera hacerlo.', 1, 293, 122, '29/11/17', 'When the measure of temporary suspension of activity is applied because the person in charge of the establishment did not allow the entry of police authorities, the measure will be extended until the authorization of the authority that, in the exercise of its function or activity of the Police, requires it.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (281, 'Comportamientos relacionados con la seguridad y tranquilidad que afectan la actividad económica', 'Los siguientes comportamientos relacionados con la seguridad y tranquilidad afectan la actividad económica y por lo tanto no deben realizarse:', 1, 134, 122, '29/11/17', 'The following behaviors related to safety and tranquility affect economic activities and therefore should not be carried out:', 'Behaviors related to security and tranquility that affect economic activity');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (283, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas:', 1, 290, 122, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subjected to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (285, ' ', 'Las medidas correctivas mencionadas para el incumplimiento de los anteriores comportamientos, se aplicarán, sin perjuicio de lo establecido en la legislación especial que regula esas materias.', 1, 292, 122, '29/11/17', 'The aforementioned corrective measures for non-compliance with the above behaviors will be applied, without prejudice to what is established in the special legislation that regulates these matters.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (287, ' ', 'Quien en el término de un año contado a partir de la aplicación de la medida incurra nuevamente en alguno de los comportamientos prohibidos en el presente artículo que dan lugar a la medida de suspensión temporal de actividad, será objeto de suspensión definitiva de la actividad.', 1, 294, 122, '29/11/17', 'Who within the term of one year from the application of the measure incurs again in any of the behaviors prohibited in this article that result in the measure of temporary suspension, will be the subject of a definitive suspension of the activity.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (288, 'Comportamientos relacionados con la salud pública que afectan la actividad económica', 'Los siguientes comportamientos relacionados con la salud pública afectan la actividad económica y por lo tanto no deben realizarse:', 1, 135, 122, '29/11/17', 'The following behaviors related to public health affect economic activity and therefore should not be carried out:', 'Behaviors related to public health that affect economic activity');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (289, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas:', 1, 289, 122, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (290, ' ', 'Se mantendrá la aplicación de las medidas correctivas si se continúa desarrollando en el lugar la misma actividad económica que dio lugar a su imposición, aun cuando se cambie la nomenclatura, el nombre del establecimiento, su razón social, propietario, poseedor o tenedor del mismo.', 1, 290, 122, '29/11/17', 'The application of the corrective measures will be maintained if the same economic activity that led to its imposition continues to be developed in the place, even if the nomenclature, the name of the establishment, its social reason, owner, possessor or holder thereof is changed.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (292, ' ', 'Cuando se aplique la medida de suspensión temporal de actividad porque el responsable del establecimiento no permitió el ingreso de autoridades de Policía, la medida se extenderá hasta tanto se autorice el ingreso de la autoridad que en ejercicio de su función o actividad de Policía, requiera hacerlo.', 1, 292, 122, '29/11/17', 'When the measure of temporary suspension of activity is applied because the person in charge of the establishment did not allow the entry of police authorities, the measure will be extended until the authorization of the authority that, in the exercise of its function or activity of the Police, requires it.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (294, 'Comportamientos que afectan la seguridad de las personas y sus bienes relacionados con equipos terminales móviles', 'Los siguientes comportamientos afectan la seguridad de las personas y sus bienes, y por lo tanto no deben realizarse:', 1, 136, 123, '29/11/17', 'The following behaviors affect the safety of people and their property, and therefore should not be carried out:', 'Behaviors that affect the safety of people and their assets related to mobile terminal equipment');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (296, ' ', 'Corresponde a las personas naturales o jurídicas verificar que el equipo a comprar, alquilar, usar, distribuir, almacenar o vender, no se encuentre reportado como hurtado y/o extraviado en la base de datos negativa de que trata el artículo 106 de la Ley 1453 de 2011 y proceder al registro en base de datos positiva. La consulta pública de la base de datos negativa estará disponible a través del sitio web que indique la Comisión de Regulación de Comunicaciones y para el registro de los equipos terminales móviles en la base de datos positiva los proveedores deberán disponer de los medios definidos en la regulación.', 1, 290, 123, '29/11/17', 'It is up to natural or legal persons to verify that the equipment to be purchased, rented, used, distributed, stored or sold is not reported as stolen and / or lost in the negative database referred to in article 106 of Law 1453 of 2011 and proceed to registration in positive database. The public consultation of the negative database will be available through the website indicated by the Communications Regulatory Commission and for the registration of mobile terminal equipment in the positive database, suppliers must have the means defined in the regulation.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (298, ' ', 'Si el número de equipos móviles comprometido en los comportamientos es superior a uno se aplicará la medida correctiva señalada por cada equipo móvil. La autoridad policial deberá judicializar a las personas de conformidad con la ley penal.', 1, 292, 123, '29/11/17', 'If the number of mobile terminals engaged in the behaviors is greater than one, the corrective action indicated by each mobile device will be applied. The police authority must prosecute people in accordance with the criminal law.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (300, ' ', 'Las medidas correctivas mencionadas para el incumplimiento de los anteriores comportamientos, se aplicarán, sin perjuicio de lo establecido en la legislación especial que regula esas materias.', 1, 294, 123, '29/11/17', 'The aforementioned corrective measures for non-compliance with the above behaviors will be applied, without prejudice to what is established in the special legislation that regulates these matters.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (302, ' ', 'Quien en el término de un año contado a partir de la aplicación de la medida incurra nuevamente en alguno de los comportamientos prohibidos en el presente capítulo que dan lugar a la medida de suspensión temporal, será objeto de suspensión definitiva de la actividad.', 1, 296, 123, '29/11/17', 'Who within the term of one year from the application of the measure incurs again in any of the behaviors prohibited in this article that result in the measure of temporary suspension, will be the subject of a definitive suspension of the activity.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (305, 'Definiciones', 'Los vocablos utilizados en el presente título deberán ser interpretados a la luz de las disposiciones contenidas en el régimen ambiental.', 1, 139, 125, '29/11/17', 'The terms used in this title should be interpreted in light of the provisions contained in the environmental regime.', 'Definitions');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (306, 'Facultades particulares', 'La autoridad ambiental competente del área protegida, de conformidad con el régimen ambiental, podrá reglamentar la introducción, transporte, porte, almacenamiento, tenencia o comercialización de bienes, servicios, p roductos o sustancias, con el fin de prevenir comportamientos que deterioren el ambiente.', 1, 140, 125, '29/11/17', 'The competent environmental authority of the protected area, in accordance with the environmental regime, may regulate the introduction, transportation, carrying, storage, possession or commercialization of goods, services, products or substances, in order to prevent behaviors that deteriorate the environment.', 'Particular powers');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (308, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 287, 126, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following corrective measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (311, ' ', 'Se mantendrá la aplicación de las medidas correctivas si se continúa desarrollando en el lugar la misma actividad económica que dio lugar a su imposición, aun cuando se cambie la nomenclatura, el nombre del establecimiento, su razón social, propietario, poseedor o tenedor del mismo.', 1, 290, 126, '29/11/17', 'The application of the corrective measures will be maintained if the same economic activity that led to its imposition continues to be developed in the place, even if the nomenclature, the name of the establishment, its social reason, owner, possessor or holder thereof is changed.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (312, ' ', 'Las medidas correctivas mencionadas para el incumplimiento de los anteriores comportamientos, se aplicarán, sin perjuicio de lo establecido en la legislación especial que regula esas materias y de las competencias de las autoridades ambientales.', 1, 291, 126, '29/11/17', 'The aforementioned corrective measures for non-compliance with the above behaviors will be applied, without prejudice to the provisions of the special legislation that regulates these matters and of the competences of the environmental authorities.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (313, ' ', 'Cuando se aplique la medida de suspensión temporal de actividad porque el responsable del establecimiento no permitió el ingreso de autoridades de Policía, la medida se extenderá hasta tanto se autorice el ingreso de la autoridad que en ejercicio de su función o actividad de Policía, requiera hacerlo.', 1, 292, 126, '29/11/17', 'When the measure of temporary suspension of activity is applied because the person in charge of the establishment did not allow the entry of police authorities, the measure will be extended until the authorization of the authority that, in the exercise of its function or activity of the Police, requires it.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (315, 'Comportamientos que afectan el aire', 'Los siguientes comportamientos afectan el aire y por lo tanto no se deben efectuar:', 1, 143, 126, '29/11/17', 'The following behaviors affect the air and therefore should not be carried out:', 'Behaviors that affect the air');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (317, 'Comportamientos que afectan las áreas protegidas del Sistema Nacional de áreas Protegidas (SINAP) y áreas de especial importancia ecológica', 'Los siguientes comportamientos afectan las áreas protegidas del Sistema Nacional de áreas Protegidas (SINAP) y áreas de especial importancia ecológica y por lo tanto no se deben efectuar:', 1, 144, 150, '29/11/17', 'The following behaviors affect the protected areas of the National System of Protected Areas  (SNAP) and areas of special ecological importance and therefore should not be carried out:', 'Behaviors that affect the protected areas of the National System of Protected Areas ( (SNAP) and areas of special ecological importance');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (320, 'Actividades que son objeto de control en el desarrollo de la minería', 'Las siguientes actividades son contrarias a la minería y por lo tanto no deben efectuarse. Su realización dará lugar a medidas correctivas o a la imposición de medidas preventivas de que trata la Ley 1333 de 2009, según sea el caso y sin perjuicio de las de carácter penal o civil que de ellas se deriven:', 1, 146, 127, '29/11/17', 'The following activities are contrary to mining and therefore should not be carried out. Its implementation will result in corrective measures or the imposition of preventive measures referred to in Law 1333 of 2009, as the case may be and without prejudice to those of a criminal or civil nature derived from them:', 'Activities that are subject to control in the development of mining');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (321, ' ', 'Quien incurra en una o más de las actividades antes señaladas, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 289, 127, '29/11/17', 'Anyone who commits one or more of the aforementioned activities will be subject to the application of the following corrective measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (322, ' ', 'Cuando se aplique cualquiera de las medidas enunciadas en el presente artículo, la autoridad de Policía deberá informar dentro de los tres (3) días siguientes a las autoridades competentes.', 1, 290, 127, '29/11/17', 'When any of the measures set forth in this article is applied, the police authority must inform the competent authorities within three (3) days.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (323, ' ', 'Sin perjuicio de las medidas correctivas establecidas en el parágrafo primero del presente artículo, en caso que algunas de las actividades descritas se realicen directa o indirectamente por organizaciones criminales o grupos al margen de la ley o en beneficio de los mismos, procederá la inutilización o destrucción del bien.', 1, 291, 127, '29/11/17', 'Notwithstanding the corrective measures established in the first paragraph of this article, in the event that some of the activities described are carried out directly or indirectly by criminal organizations or groups outside the law or for the benefit thereof, the destruction of the assets will proceed.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (324, ' ', 'En tratándose de la actividad prevista en el numeral 10 del presente artículo, durante los doce (12) meses siguientes a la expedición de esta ley, será admisible para demostrar su licita procedencia un medio de prueba distinto al certificado.', 1, 288, 127, '29/11/17', 'In the case of the activity foreseen in numeral 10 of this article, during the twelve (12) months following the issuance of this law, it will be admitted to prove its bid by means of proof to the other.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (326, 'Control de insumos utilizados en actividad minera', 'El Gobierno nacional establecerá controles para el ingreso al territorio nacional, transporte, almacenamiento, comercialización, producción, uso, y disposición final, entre otros, de los insumos y sustancias químicas utilizados en la actividad minera.', 1, 148, 127, '29/11/17', 'The national government will establish controls for the entry into the national territory, transportation, storage, commercialization, production, use, and final disposal, among others, of the inputs and chemical substances used in the mining activity.', 'Control of inputs used in mining activity');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (327, 'Competencia en materia minero-ambiental', 'La Policía Nacional, a efectos de proteger y salvaguardar la salud humana y preservar los recursos naturales renovables, no renovables y el ambiente, deberá incautar sustancias y químicos como el zinc, bórax, cianuro y mercurio utilizados en el proceso de exploración, explotación y extracción de la minería ilegal.', 1, 149, 127, '29/11/17', 'The National Police, in order to protect and safeguard human health and preserve renewable, non-renewable natural resources and the environment, must seize substances and chemicals such as zinc, borax, cyanide and mercury used in the exploration, exploitation and extraction process of illegal mining.', 'Competition in mining-environmental matters');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (329, 'Alcance. El presente capítulo tiene por objeto la regulación de comportamientos que puedan poner en peligro la salud pública por el consumo de alimentos', 'Las Secretarías de Salud de las entidades territoriales y el Instituto Nacional de Vigilancia de Alimentos y Medicamentos (INVIMA) serán las encargadas de ejercer las facultades de conformidad con sus competencias de inspección, vigilancia y control.', 1, 150, 128, '29/11/17', 'The Health Secretariats of the territorial entities and the National Institute for Drug and Food Surveillance (INVIMA) will be responsible for exercising the powers in accordance with their powers of inspection, monitoring and control.', 'Scope. The purpose of this chapter is to regulate behaviors that may endanger public health through the consumption of food');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (330, 'Comportamientos que atentan contra la salud pública en materia de consumo', 'Los siguientes comportamientos atentan contra la salud pública en materia de consumo y por lo tanto no deben efectuarse:', 1, 151, 128, '29/11/17', 'The following behaviors threaten public health in terms of consumption and therefore should not be carried out:', 'Behaviors that threaten public health in terms of consumption');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (332, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 290, 128, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following corrective measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (333, ' ', 'Se mantendrá la aplicación de las medidas correctivas si se continúa desarrollando en el lugar la misma actividad económica que dio lugar a su imposición, aun cuando se cambie la nomenclatura, el nombre del establecimiento, su razón social, propietario, poseedor o tenedor del mismo.', 1, 291, 128, '29/11/17', 'The application of the corrective measures will be maintained if the same economic activity that led to its imposition continues to be developed in the place, even if the nomenclature, the name of the establishment, its social reason, owner, possessor or holder thereof is changed.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (334, ' ', 'Las medidas correctivas mencionadas para el incumplimiento de los anteriores comportamientos, se aplicarán, sin perjuicio de lo establecido en la legislación especial que regula esas materias.', 1, 292, 128, '29/11/17', 'The aforementioned corrective measures for non-compliance with the above behaviors will be applied, without prejudice to what is established in the special legislation that regulates these matters.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (336, ' ', 'Quien en el término de un año contado a partir de la aplicación de la medida incurra nuevamente en alguno de los comportamientos prohibidos en el presente artículo que dan lugar a la medida de suspensión temporal de actividad, será objeto de suspensión definitiva de la actividad.', 1, 294, 128, '29/11/17', 'Who within the term of one year from the application of the measure incurs again in any of the behaviors prohibited in this article that result in the measure of temporary suspension of activity, will be subject to a definitive suspension of the activity.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (337, 'Comportamientos contrarios a la limpieza y recolección de residuos y escombros y malas prácticas habitacionales', 'Los siguientes comportamientos son contrarios a la habitabilidad, limpieza y recolección de residuos y escombros y por lo tanto no deben efectuarse:', 1, 152, 129, '29/11/17', 'The following behaviors are contrary to the habitability, cleanliness and collection of waste and debris and therefore should not be carried out:', 'Behaviors contrary to the cleaning and collection of waste and debris and bad housing practices');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (340, ' ', 'Frente al comportamiento descrito en el numeral 8 del presente artículo respecto a la disposición final de las llantas, los productores y/o comercializadores en coordinación con las autoridades locales y ambientales deberán crear un sistema de recolección selectiva y gestión ambiental de llantas usadas.', 1, 291, 129, '29/11/17', 'In view of the behavior described in number 8 of this article regarding the final disposal of the tires, the producers and / or traders, in coordination with the local and environmental authorities, must create a system of selective collection and environmental management of used tires.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (341, 'Obligaciones de las personas que poseen bienes de interés cultural o ejercen tenencia de bienes arqueológicos', 'Las personas naturales o jurídicas pueden poseer bienes de interés cultural y ejercer la tenencia de patrimonio arqueológico de manera excepcional y especial siempre y cuando cumplan con las siguientes obligaciones adicionales:', 1, 153, 130, '29/11/17', 'Individuals or legal entities may possess assets of cultural interest and exercise the possession of archaeological heritage in an exceptional and special way provided they comply with the following additional obligations:', 'Obligations of people who possess assets of cultural interest or exercise possession of archaeological assets');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (345, 'Uso de bienes de interés cultural', 'Las Asambleas Departamentales, los Concejos Distritales y Concejos Municipales reglamentarán las normas generales de uso de los bienes de interés cultural de su respectivo territorio, de conformidad con lo dispuesto en las normas especializadas de orden nacional sobre la materia y lo dispuesto en planes de ordenamiento territorial y en las normas que lo desarrollen o complementen.', 1, 154, 130, '29/11/17', 'The Departmental Assemblies, the District Councils and Municipal Councils will regulate the general norms of use of the goods of cultural interest of their respective territory, in accordance with the dispositions in the specialized norms of national order on the matter and the arranged thing in plans of territorial ordering and in the norms that develop or complement it.', 'Use of goods of cultural interest');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (347, 'Comportamientos contrarios a la protección y conservación del patrimonio cultural', ' Además de lo establecido en el artículo 15 de la Ley 397 de 1997 modificado por el artículo 10 de la Ley 1185 de 2008, los siguientes comportamientos atentan contra el patrimonio cultural y por lo tanto no deben efectuarse:', 1, 156, 130, '29/11/17', 'In addition to the provisions of Article 15 of Law 397 of 1997 modified by Article 10 of Law 1185 of 2008, the following behaviors violate the cultural heritage and therefore should not be carried out:', 'Behaviors contrary to the protection and conservation of cultural heritage');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (348, ' ', 'La autoridad de Policía que conozca la situación remitirá el caso a la autoridad cultural que haya realizado la declaratoria de Bien de Interés Cultural, la cual impondrá y ejecutará las medidas establecidas en las normas vigentes que regulan la materia, las que las adicionen o modifiquen, y sus Decretos Reglamentarios.', 1, 289, 130, '29/11/17', 'The police authority that knows the situation will refer the case to the cultural authority that has made the declaration of Cultural Interest, which will impose and execute the measures established in the current regulations that regulate the matter, those that add or modify them, and its Regulatory Decrees.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (350, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas, sin perjuicio de las medidas establecidas en la normatividad específica:', 1, 291, 130, '29/11/17', 'Anyone who incurs in one or more of the aforementioned behaviors will be subject to the application of the following corrective measures, without prejudice to the measures established in the specific regulations:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (352, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 289, 131, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following corrective measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (354, ' ', 'Se prohíbe usar animales cautivos como blanco de tiro, con objetos susceptibles de causarles daño o muerte con armas de cualquier clase, exceptúese la casa deportiva.', 1, 291, 131, '29/11/17', 'It is forbidden to use captive animals as target, with objects likely to cause damage or death with weapons of any kind, except the sports house.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (355, 'Tenencia de animales domésticos o mascotas', 'Solo podrán tenerse como mascotas los animales así autorizados por la normatividad vigente. Para estos animales el ingreso o permanencia en cualquier lugar, se sujetará a la reglamentación de los lugares públicos, abiertos al público o edificaciones públicas.\nEn las zonas comunes de propiedades horizontales o conjuntos residenciales, los ejemplares caninos deberán ir sujetos por medio de traílla y, en el caso de los caninos potencialmente peligrosos, además irán provistos de bozal y el correspondiente permiso, de conformidad con la ley.', 1, 158, 132, '29/11/17', 'May only be taken as pets, animals allowed by current regulations. For these animals, entry or stay in any place shall be subject to the regulation of public places, open to the public or public buildings.\r\nIn the common areas of horizontal properties or residential complexes, the dogs must be restrained by means of a leash and, in the case of potentially dangerous dogs, they shall also be provided with a muzzle and the corresponding permit, in accordance with the law. ', 'Possession of domestic pets');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (356, ' ', 'Siempre se permitirá la presencia de ejemplares caninos que, como guías, acompañen a su propietario o tenedor.', 1, 289, 132, '29/11/17', 'The presence of canine dogs will always be allowed, as guides, accompanying their owner or holder.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (358, 'Caninos y felinos domésticos o mascotas en el espacio público', 'En el espacio público, en las vías públicas, en los lugares abiertos al público, y en el transporte público en el que sea permitida su estancia, todos los ejemplares caninos deberán ser sujetos por su correspondiente traílla y con bozal debidamente ajustado en los casos señalados en la presente ley para los ejemplares caninos potencialmente peligrosos y los felinos en maletines o con collares especiales para su transporte.', 1, 159, 132, '29/11/17', 'In the public space, on public roads, in places open to the public, and on public transport where these are allowed to stay, all canine specimens must be secured by their corresponding leash and with muzzle properly adjusted in the cases indicated in the present law for potentially dangerous canine specimens and felines in briefcases or with special collars for transport.', 'Domestic canines and felines or pets in the public space');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (361, 'Información', 'Es deber de la Alcaldía Distrital o Municipal establecer un mecanismo para informar de manera suficiente a la ciudadanía el lugar a donde se llevan los animales que sean sorprendidos en predios ajenos o vagando en el espacio público y establecer un sistema donde se pueda solicitar información y buscar los animales en caso de extravío. La administración distrital o municipal podrá establecer una tarifa diaria de público conocimiento, correspondiente al costo del cuidado y alimentación temporal del animal. En las ciudades capitales y en municipios con población mayor a cien mil habitantes deberá además establecerse un vínculo o un sitio en la página web de la Alcaldía en donde se registre la fotografía de cada animal encontrado para facilitar su búsqueda. La entrega de dichos animales s erá reglamentada por la Administración Municipal correspondiente.\nLa información publicada en la página web cumplirá con el estándar dispuesto por el Ministerio de Tecnología de la Información y las Comunicaciones, de datos abiertos y de lenguaje para el intercambio de la misma.', 1, 162, 132, '29/11/17', 'It is the duty of the District or Municipal Mayor''s Office to establish a mechanism to sufficiently inform the public of the place where the animals are taken that are caught in other people''s premises or wandering in the public space and establish a system where information can be requested and search for the animals in case of loss. The district or municipal administration may establish a daily rate of public knowledge, corresponding to the cost of care and temporary feeding of the animal In the capital cities and municipalities with a population greater than one hundred thousand inhabitants, also it should establish a link on the Mayor''s website where the photograph of each animal found is registered to facilitate its search. The delivery of said animals will be regulated by the corresponding Municipal Administration.\r\nThe information published on the website will comply with the standard set by the Ministry of Information Technology and Communications, open data and language for the exchange of the same. ', 'information');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (362, 'Estancia de caninos o felinos domésticos o mascotas en zonas de recreo', 'Los concejos distritales y municipales, mediante acuerdos, regularán el ingreso de caninos o felinos domésticos o mascotas a las zonas de juegos infantiles ubicados en las plazas y parques del área de su jurisdicción.', 1, 163, 132, '29/11/17', 'The district and municipal councils, through agreements, will regulate the entry of canines or domestic felines or pets into the playgrounds located in the squares and parks of the area of their jurisdiction.', 'Stay of domestic dogs, cats or pets in recreational areas');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (364, 'Comportamientos que ponen en riesgo la convivencia por la tenencia de animales', 'Los siguientes comportamientos ponen en riesgo la convivencia por la tenencia de animales y por lo tanto no deben efectuarse:', 1, 166, 133, '29/11/17', 'The following behaviors put at risk the coexistence by the possession of animals and therefore should not be carried out:', 'Behaviors that put at risk the coexistence by the possession of animals');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (366, ' ', 'A quien incurra en uno o más de los comportamientos señalados en el presente artículo, se le aplicarán las siguientes medidas correctivas:', 1, 290, 133, '29/11/17', 'Whoever incurs one or more of the behaviors indicated in this article, the following corrective measures will be applied:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (368, 'Prohibición de peleas caninas', 'Las peleas de ejemplares caninos como espectáculo quedan prohibidas en todo el territorio nacional.', 1, 167, 133, '29/11/17', 'The fights of canine specimens as a show are prohibited throughout the national territory.', 'Prohibition of dogfights');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (371, ' ', 'El Gobierno reglamentará en un término de seis (6) meses lo relacionado con la expedición de las pólizas de responsabilidad civil extracontractual que cubrirán este tipo de contingencias.', 1, 287, 134, '29/11/17', 'The Government will regulate in a term of six (6) months what is related to the issuance of non-contractual civil liability policies that will cover this type of contingencies.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (372, 'Registro de los ejemplares potencialmente peligrosos', 'Las categorías señaladas en los artículos anteriores de este capítulo, deben ser registrados en el censo de caninos potencialmente peligrosos que se establecerá en las alcaldías, para obtener el respectivo permiso. En este registro debe constar necesariamente:', 1, 170, 134, '29/11/17', 'The categories indicated in the previous articles of this chapter, must be registered in the census of potentially dangerous canines that will be established in the mayoralties, to obtain the respective permit. In this record it must necessarily be included:', 'Registration of potentially dangerous specimens');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (100, 'Objeto', 'Las disposiciones previstas en este Código son de carácter preventivo y buscan establecer las condiciones para la convivencia en el territorio nacional al propiciar el cumplimiento de los deberes y obligaciones de las personas naturales y jurídicas, así como determinar el ejercicio del poder, la función y la actividad de Policía, de conformidad con la Constitución Política y el ordenamiento jurídico vigente.', 1, 41, 100, '29/11/17', 'The provisions set forth in this Code are of a preventive nature and seek to establish the conditions for coexistence in the national territory by promoting the fulfillment of the duties and obligations of natural and legal persons, as well as determining the exercise of power, function and Police activity in accordance with the Constitution and the existing legislation.', 'Purpose');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (101, 'Objetivos específicos', 'Con el fin de mantener las condiciones necesarias para la convivencia en el territorio nacional, los objetivos específicos de este Código son los siguientes:', 1, 42, 100, '29/11/17', 'In order to maintain the necessary conditions for coexistence in the national territory, the specific objectives of this Code are the following:', 'Specific Objectives');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (102, 'ámbito de aplicación del Derecho de Policía', 'El derecho de Policía se aplicará a todas las personas naturales o jurídicas, de conformidad con este Código. Las autoridades de Policía sujetarán sus actuaciones al procedimiento único de Policía, sin perjuicio de las competencias que les asistan en procedimientos regulados por leyes especiales.', 1, 43, 100, '29/11/17', 'The Police Law shall apply to all natural or legal persons, in accordance with this Code. The police authorities will subject their actions to the single police procedure, without prejudice to the competences that assist them in procedures regulated by special laws.', 'Scope of the police law');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (104, 'Definición', 'Para los efectos de este Código, se entiende por convivencia, la interacción pacífica, respetuosa y armónica entre las personas, con los bienes, y con el ambiente, en el marco del ordenamiento jurídico.', 1, 45, 101, '29/11/17', 'For the purposes of this Code, coexistence is understood as the peaceful, respectful and harmonious interaction between people, property and the environment, within the framework of the legal system.', 'Definition');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (105, 'Categorías jurídicas', 'Las categorías de convivencia son: seguridad, tranquilidad, ambiente y salud pública, y su alcance es el siguiente:', 1, 46, 101, '29/11/17', 'The categories of coexistence are: security, tranquility, environment and public health, and its scope is as follows:', 'Legal Categories');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (106, 'Finalidades de la convivencia', 'Son fines esenciales de las normas de convivencia social previstas en este Código:', 1, 47, 101, '29/11/17', 'The essential purposes of the rules of social coexistence foreseen in this Code are:', 'Purposes of coexistence');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (109, 'Ejercicio de la libertad y de los derechos de los asociados', 'Las autoridades garantizarán a las personas que habitan o visitan el territorio colombiano, el ejercicio legítimo de los derechos y las libertades constitucionales, con fundamento en su autonomía personal, autorregulación individual y social.', 1, 49, 101, '29/11/17', 'The authorities will guarantee to the people who live or visit the Colombian territory, the legitimate exercise of constitutional rights and liberties, based on their personal autonomy, individual and social self-regulation.', 'Exercise of freedom and the rights of associates');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (112, 'Poder de Policía', 'El poder de Policía es la facultad de expedir las normas en materia de Policía, que son de carácter general, impersonal y abstracto, ejercido por el Congreso de la República para regular el ejercicio de la libertad, los derechos y los deberes constitucionales, para la convivencia y establecer los medios y las medidas correctivas en caso de su incumplimiento.', 1, 51, 102, '29/11/17', 'The power of the Police is the power to issue the norms on Police matters, which are general, impersonal and abstract, exercised by the Congress of the Republic to regulate the exercise of freedom, rights and constitutional duties, for the coexistence and establish means and corrective measures in case of non-compliance.', 'Police Power');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (114, ' ', 'El Concejo Distrital de Bogotá podrá establecer formas de control policial sobre las normas de ordenamiento territorial, usos del suelo y defensa del patrimonio ecológico y cultural.', 1, 289, 102, '29/11/17', 'The District Council of Bogotá may establish forms of police control over the rules of territorial ordering, land use and defense of ecological and cultural heritage.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (115, ' ', 'Las normas de Policía y convivencia expedidas por el Concejo del Distrito Capital de Bogotá no están subordinadas a las ordenanzas.', 1, 290, 102, '29/11/17', 'The rules of police and coexistence issued by the Council of the Capital District of Bogotá are not subordinated to the ordinances.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (117, ' ', 'Los Concejos Municipales y Distritales podrán establecer formas de control policial sobre las normas de ordenamiento territorial, usos del suelo y defensa del patrimonio ecológico y cultural.', 1, 287, 102, '29/11/17', 'The Municipal and District Councils will be able to establish forms of police control over the rules of territorial ordering, land use and defense of the ecological and cultural heritage.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (118, 'Poder extraordinario para prevención del riesgo o ante situaciones de emergencia, seguridad y calamidad.', 'Los gobernadores y los alcaldes, podrán disponer acciones transitorias de Policía, ante situaciones extraordinarias que puedan amenazar o afectar gravemente a la población, con el propósito de prevenir las consecuencias negativas ante la materialización de un evento amenazante o mitigar los efectos adversos ante la ocurrencia de desastres, epidemias, calamidades, o situaciones de seguridad o medio ambiente; así mismo, para disminuir el impacto de sus posibles consecuencias, de conformidad con las leyes que regulan la materia.', 1, 54, 102, '29/11/17', 'The governors and mayors may order transitory actions of the Police, in the face of extraordinary situations that may threaten or seriously affect the population, with the purpose of preventing the negative consequences before the materialization of a threatening event or mitigate the adverse effects before the occurrence of disasters, epidemics, calamities, or situations of safety or environment; likewise, to reduce the impact of its possible consequences, in accordance with the laws that regulate the matter.', 'Extraordinary power for risk prevention in emergency, security and calamity situations.');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (122, 'Competencia para expedir reglamentos', 'En el ámbito nacional corresponde al Presidente de la República reglamentar las leyes sobre materias de Policía. Cuando las disposiciones de las asambleas o los concejos en asuntos de Policía, requieran reglamentación para aplicarlas, los gobernadores o los alcaldes podrán, según el caso, dictar reglamentos sólo con ese fin. Las autoridades que expiden reglamentos no podrán regular comportamientos, imponer medidas correctivas o crear procedimientos distintos a los establecidos en la norma reglamentada, salvo que esta les otorgue dicha competencia.', 1, 57, 103, '29/11/17', 'At the national level, it is the responsibility of the President of the Republic to regulate the laws on matters of Police. When the dispositions of the assemblies or the councils in matters of Police, require regulation to apply them, the governors or the mayors may, as the case may be, dictate regulations only for that purpose. The authorities that issue regulations may not regulate behavior, impose corrective measures or create procedures other than those established in the regulated norm, unless it grants them such competence.', 'Competence to issue regulations');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (123, 'Coordinación', 'La coordinación entre las autoridades de Policía debe ser permanente, adecuada, eficiente, eficaz y oportuna, con el fin de asegurar las condiciones necesarias para la convivencia.', 1, 58, 103, '29/11/17', 'The coordination between the police authorities must be permanent, adequate, efficient, effective and timely, in order to ensure the necessary conditions for coexistence.', 'Coordination');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (126, 'Carácter público de las actividades de Policía', 'Todo procedimiento policivo podrá ser grabado mediante cualquier medio de las tecnologías de información y comunicación, por lo que le está prohibido a cualquier persona, salvo las restricciones expresas de ley, impedir que sean realizadas dichas grabaciones. La autoridad de Policía que impida la grabación de que trata este artículo sin la justificación legal correspondiente incurrirá en causal de mala conducta.', 1, 61, 103, '29/11/17', 'Any police procedure may be recorded by any means of information and communication technologies, so it is forbidden to anyone, except the express restrictions of law, to prevent such recordings being made. The Police authority that prevents the recording of this article without the corresponding legal justification will incur cause of misconduct.', 'Public nature of Police activities');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (127, 'Titular del uso de la fuerza policial', 'La utilización de la fuerza legítima corresponde de manera exclusiva, en el marco de este Código, a los miembros uniformados de la Policía Nacional, de conformidad con el marco jurídico vigente, salvo en aquellos casos en los que de manera excepcional se requiera la asistencia militar.', 1, 62, 103, '29/11/17', 'The use of legitimate force corresponds exclusively, within the framework of this Code, to the uniformed members of the National Police, in accordance with the current legal framework, except in those cases in which military assistance is exceptionally required.', 'Holder of the use of police force');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (131, ' ', 'En atención a los comportamientos relacionados en el presente Código, corresponde a las autoridades de Policía dentro del ámbito de su competencia adelantar las acciones que en derecho correspondan respetando las garantías constitucionales.', 1, 289, 148, '29/11/17', 'In attention to the behaviors related in this Code, it is up to the police authorities within the scope of their competence to advance the actions that in law correspond respecting the constitutional guarantees.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (133, 'Deberes de convivencia', 'Es deber de todas las personas en el territorio nacional comportarse de manera favorable a la convivencia. Para ello, además de evitar comportamientos contrarios a la misma, deben regular sus comportamientos a fin de respetar a las demás personas, en el ejercicio de sus derechos y deberes ciudadanos, en su vida, honra y bienes, de conformidad con la Constitución Política y las leyes, y especialmente con lo dispuesto en la presente ley.', 1, 67, 105, '29/11/17', 'It is the duty of all people in the national territory to behave in a favorable manner to coexistence. To do this, in addition to avoiding behavior contrary to it, must regulate their behavior in order to respect other people, in the exercise of their rights and duties citizens, their lives, honor and property, in accordance with the Constitution and the laws, and especially with the provisions of this law.', 'Duties of coexistence');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (134, 'Comportamientos que ponen en riesgo la vida e integridad', 'Los siguientes comportamientos ponen en riesgo la vida e integridad de las personas, y, por lo tanto, son contrarios a la convivencia:', 1, 68, 106, '29/11/17', 'The following behaviors put at risk the life and integrity of people, and, therefore, are contrary to coexistence:', 'Behaviors that put life and integrity at risk');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (136, ' ', 'En todos los comportamientos señalados en el presente artículo, se deberá utilizar la mediación policial como medio para intentar resolver el conflicto.', 1, 290, 106, '29/11/17', 'In all the behaviors indicated in this article, police mediation should be used as a means to try to resolve the conflict.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (138, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 287, 107, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following corrective measures:', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (140, ' ', 'En esta materia se aplican las disposiciones y sanciones previstas en la Ley 670 de 2001 o la que haga sus veces, sin perjuicio de la aplicación de las normas pertinentes en este Código.', 1, 287, 108, '29/11/17', 'In this matter, the provisions and sanctions provided for in Law 670 of 2001 or the one that acts as such, without prejudice to the application of the pertinent norms in this Code.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (141, 'Comportamientos que afectan la seguridad e integridad de las personas en materia de artículos pirotécnicos y sustancias peligrosas', 'Los siguientes comportamientos o actividades afectan la seguridad de las personas y la de sus bienes y por lo tanto no deben realizarse:', 1, 71, 108, '29/11/17', 'The following behaviors or activities affect the safety of people and their property and therefore should not be carried out:', 'Behaviors that affect the safety and integrity of people in the matter of pyrotechnic articles and dangerous substances');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (143, ' ', 'El alcalde distrital o municipal reglamentará en su jurisdicción las condiciones para la realización de actividades peligrosas y los requisitos para la prevención y atención de incendios, referidos a los comportamientos señalados en el presente artículo, de conformidad con las normas, regulaciones, e instructivos nacionales.', 1, 290, 108, '29/11/17', 'The district or municipal mayor will regulate in his jurisdiction the conditions for the realization of dangerous activities and the requirements for the prevention and attention of fires, referred to the behaviors indicated in the present article, in accordance with the norms, regulations, and national instructions.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (145, ' ', 'La medida de destrucción mencionada en el presente artículo sólo operará en los casos en que quien incurra en algunos de los comportamientos descritos en los numerales 1, 2, 3 y 4, no cumpla con la totalidad de los requisitos que exige la ley.', 1, 292, 108, '29/11/17', 'The destruction measure mentioned in this article will only operate in those cases in which someone who engages in some of the behaviors described in numerals 1, 2, 3 and 4 does not comply with all the requirements required by law.', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (147, 'Definición de privacidad', 'Para efectos de este Código, se entiende por privacidad de las personas el derecho de ellas a satisfacer sus necesidades y desarrollar sus actividades en un ámbito que le sea exclusivo y por lo tanto considerado como privado.\nNo se consideran lugares privados:', 1, 73, 110, '29/11/17', 'For the purposes of this Code, people''s privacy is understood as their right to satisfy their needs and develop their activities in an area that is exclusive and therefore considered private.\nThese are not considered private places: ', 'Definition of privacy');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (149, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas:', 1, 289, 110, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following corrective measures:', ' ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (151, 'Comportamientos que afectan la convivencia en los establecimientos educativos relacionados con consumo de sustancias', 'Los siguientes comportamientos afectan la convivencia en los establecimientos educativos y por lo tanto no deben efectuarse:', 1, 75, 111, '29/11/17', 'The following behaviors affect coexistence in educational establishments and therefore should not be carried out:', 'Behaviors that affect coexistence in educational establishments related to substance abuse');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (153, ' ', 'La persona mayor de edad que incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas correctivas, sin perjuicio de lo establecido en los reglamentos internos de cada establecimiento educativo:', 1, 290, 111, '29/11/17', 'The person of legal age who incurs in one or more of the aforementioned behaviors will be subject to the application of the following corrective measures, without prejudice to what is established in the internal regulations of each educational establishment:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (155, ' ', 'El comportamiento esperado por parte de los habitantes del territorio nacional para con las autoridades exige un comportamiento recíproco. Las autoridades y en particular el personal uniformado de la Policía, deben dirigirse a los habitantes con respeto y responder a sus inquietudes y llamado con la mayor diligencia. Los habitantes del territorio nacional informarán a la autoridad competente en caso de que no sea así.', 1, 289, 111, '29/11/17', 'The expected behavior on the part of the inhabitants of the national territory towards the authorities demands a reciprocal behavior. The authorities and in particular the uniformed personnel of the Police, should address the inhabitants with respect and respond to their concerns and call with the utmost diligence. The inhabitants of the national territory shall inform the competent authority if this is not the case.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (157, ' ', 'Las multas impuestas por la ocurrencia de los comportamientos señalados en el numeral 7 del presente artículo se cargarán a la factura de cobro del servicio de la línea telefónica de donde se generó la llamada. La empresa operadora del servicio telefónico trasladará mensualmente a las entidades y administraciones territoriales respectivas las sumas recaudadas por este concepto según lo establecido en la reglamentación de la presente ley.', 1, 291, 111, '29/11/17', 'The fines imposed for the occurrence of the behaviors indicated in paragraph 7 of this article will be charged to the bill for the service of the telephone line from which the call was generated. The operating company of the telephone service will transfer monthly to the entities and territorial administrations the sums collected for this concept as established in the regulations of this law.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (159, 'Facultades de los alcaldes para la restricción de la movilidad o permanencia de niños, niñas y adolescentes en el espacio público o en lugares abiertos al público', 'Con el fin de prevenir la ocurrencia de eventos que puedan poner en peligro o afectar la vida, la integridad o la salud de los niños, niñas y adolescentes, excepcionalmente el alcalde podrá restringir su movilidad o permanencia en el espacio público o en lugares abiertos al público, de manera temporal y en forma motivada.', 1, 77, 112, '29/11/17', 'In order to prevent the occurrence of events that may endanger or affect the life, integrity or health of children and adolescents, exceptionally the mayor may restrict their mobility or permanence in public space or in places open to the public, temporarily and in a motivated form.', 'Powers of mayors for the restriction of mobility or permanence of children and adolescents in public space or in places open to the public');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (161, 'Reglamentación para la protección de niños, niñas y adolescentes', 'El alcalde distrital o municipal determinará mediante acto motivado las actividades peligrosas, las fiestas o eventos similares, a los que se prohíbe el acceso o parti cipación a los niños, niñas y adolescentes.\nAsí mismo, los niños, niñas y adolescentes deberán cumplir lo establecido en la Ley 1554 de 2012 con respecto a la edad permitida para participar o ingresar a establecimientos que prestan el servicio de videojuegos.', 1, 78, 112, '29/11/17', 'The district or municipal mayor shall determine by means of a motivated act dangerous activities, parties or similar events, to which access or participation of children and adolescents is prohibited. Likewise, children and adolescents must comply with the provisions of Law 1554 of 2012 with respect to the age allowed to participate or enter establishments that provide video game service. ', 'Regulations for the protection of children and adolescents');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (162, 'Comportamientos que afectan la integridad de niños, niñas y adolescentes', 'Los siguientes comportamientos afectan la integridad de los niños, niñas y adolescentes y por lo tanto no deben realizarse. Su incumplimiento da lugar a medidas correctivas, sin perjuicio de lo establecido por la normatividad vigente sobre la materia y de la responsabilidad penal a que haya lugar:', 1, 79, 112, '29/11/17', 'The following behaviors affect the integrity of children and adolescents and therefore should not be carried out. Failure to comply results in corrective measures, without prejudice to the provisions of the current regulations on the matter and the criminal liability that may arise:', 'Behaviors that affect the integrity of children and adolescents');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (163, ' ', 'En los comportamientos señalados en el literal b) del numeral 1 del presente artículo, se impondrá solo la medida correctiva de suspensión temporal de actividad y se pondrá en conocimiento de manera inmediata a la autoridad competente para aplicar lo establecido en la Ley 1554 de 2012 y las normas que la adicionen o modifiquen.', 1, 289, 112, '29/11/17', 'In the behaviors indicated in paragraph b) of numeral 1 of this article, only the corrective measure of temporary suspension of activity will be imposed and the competent authority will immediately be informed to apply the provisions of Law 1554 of 2012 and the norms that add or modify it.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (165, ' ', 'En los comportamientos señalados en el literal d) del numeral 1 y en el numeral 8, se impondrán las medidas correctivas en el presente Código y se pondrá en conocimiento de manera inmediata a la autoridad competente para aplicar lo establecido en las Leyes 679 de 2001, 1236 de 2008, 1329 de 2009 y las normas que las adicionen o modifiquen.', 1, 291, 112, '29/11/17', 'In the behaviors indicated in paragraph d) of numeral 1 and numeral 8, corrective measures in this Code shall be imposed and shall be reported immediately to the competent authority to implement the provisions of Laws 679 of 2001, 1236 of 2008, 1329 of 2009 and the norms that add or modify them.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (167, ' ', 'En los casos en los que los derechos de los niños, niñas y adolescentes se encuentren amenazados, inobservados o vulnerados se aplicará lo dispuesto en la Ley 1098 de 2006.', 1, 293, 112, '29/11/17', 'In cases in which the rights of children and adolescents are threatened, not observed or violated, the provisions of Law 1098 of 2006 shall apply.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (169, ' ', 'Al menor de 18 años que cometa cualquiera de los anteriores comportamientos se le aplicarán las medidas previstas en el Código de Infancia y Adolescencia.', 1, 295, 112, '29/11/17', 'Children under 18 years of age who commit any of the above behaviors will be subject to the measures set forth in the Code of Childhood and Adolescence.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (171, 'Prohibiciones a niños, niñas y adolescentes', 'Además de los comportamientos prohibidos en el presente Código y en las normas vigentes, se prohíbe a niños, niñas y adolescentes:', 1, 80, 112, '29/11/17', 'In addition to the behaviors prohibited in this Code and current regulations, it is forbidden to children and adolescents:', 'Prohibitions for children and adolescents');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (173, ' ', 'El niño, niña o adolescente que incurra en el comportamiento antes descrito será objeto de protección y restablecimiento de sus derechos de conformidad con la ley.', 1, 290, 112, '29/11/17', 'The child or adolescent who engages in the behavior described above will be subject to protection and restoration of their rights in accordance with the law.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (175, 'Comportamientos que afectan a los grupos sociales de especial protección constitucional', 'Los siguientes comportamientos afectan a los grupos sociales de especial protección (personas en condiciones de vulnerabilidad, discapacidad, niños, adultos mayores, mujeres en estado de embarazo) y por lo tanto no deben realizarse:', 1, 81, 113, '29/11/17', 'The following behaviors affect social groups of special protection (people in conditions of vulnerability, disability, children, elderly, women in pregnancy) and therefore should not be carried out:', 'Behaviors that affect social groups of special constitutional protection');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (177, 'Atención Integral a la Población Habitante de y en Calle', 'De conformidad a la Ley 1641 de 2013, establézcase un modelo de atención integral por ciclo vital y diferencial a la población habitante de y en calle, orientada a promover, prevenir, atender, proteger y restablecer derechos, modelo que tendrá como principios la igualdad, diversidad, equidad, universalidad y reconocimiento del individuo, la familia y la comunidad como sujetos de atención y que procure el diálogo y reconocimiento de realidades sociales del territorio y contribuya al bienestar y desarrollo integral del ser.', 1, 82, 113, '29/11/17', 'In accordance with Law 1641 of 2013, establish a model of comprehensive care by life cycle and differential to the homeless population, aimed at promoting, preventing, addressing, protecting and restoring rights, this model will have as principles equality, diversity, equity, universality and recognition of the individual, family and community as subjects of attention and that seek dialogue and recognition of social realities of the territory and contribute to the welfare and integral development of the being.', 'Integral Attention to the homeless population ');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (180, ' ', 'La Policía Nacional deberá trasladar en el término de la distancia a los hogares o centros de atención que el ente territorial tenga dispuesto para dicho efecto, a los ciudadanos habitantes de y en calle que se encuentren bajo el efecto de sustancias psicoactivas que les vulneren su voluntad y que generen alteración de la convivencia afectando los derechos de los demás ciudadanos.', 1, 291, 113, '29/11/17', 'The National Police must move to homes or care centers that the territorial entity has available for this purpose, the citizens of and on the street who are under the effect of psychoactive substances that violate their will and that generate alteration of coexistence affecting the rights of other citizens.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (183, 'Comportamientos en el ejercicio de la prostitución', 'Los siguientes comportamientos afectan la convivencia y por lo tanto no deben ser realizados por las personas que ejercen la prostitución:', 1, 85, 114, '29/11/17', 'The following behaviors affect coexistence and therefore should not be carried out by people who practice prostitution:', 'Behaviors in the practice of prostitution');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (184, ' ', 'Quien incurra en uno o más de los comportamientos señalados será objeto de la aplicación de las siguientes medidas:', 1, 289, 114, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (185, ' ', 'Quien en el término de un (1) año contado a partir de la aplicación de la medida incurra nuevamente en alguno de los comportamientos prohibidos en el presente capítulo que dan lugar a la medida de suspensión temporal, será objeto de suspensión definitiva de la actividad.', 1, 290, 114, '29/11/17', 'Who within the term of one (1) year from the application of the measure incurred again in any of the behaviors prohibited in this chapter that result in the measure of temporary suspension, will be subject to a definitive suspension of the activity.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (187, 'Comportamientos de quienes soliciten servicios de prostitución', 'Los siguientes comportamientos no deben ser realizados por quienes solicitan los servicios de prostitución en tanto afectan a quienes prestan dicho servicio:', 1, 86, 114, '29/11/17', 'The following behaviors should not be carried out by those who request prostitution services, as they affect those who provide said service:', 'Behavior of those who request prostitution services');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (189, 'Comportamientos de los propietarios, tenedores, administradores o encargados de los establecimientos, inmuebles o lugares donde se ejerza la prostitución', 'Los siguientes comportamientos afectan la convivencia y por lo tanto no deben ser realizados por propietarios, tenedores, administradores o encargados de los establecimientos, inmuebles o lugares donde se ejerza la prostitución, así como las personas que organizan la provisión de ese tipo de actividad:', 1, 87, 114, '29/11/17', 'The following behaviors affect coexistence and therefore should not be carried out by owners, holders, administrators or managers of establishments, real estate or places where prostitution is practiced, as well as the people who organize the provision of this type of activity:', 'Behavior of the owners, holders, administrators or managers of establishments, real estate or places where prostitution is practiced');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (192, ' ', 'El Gobierno nacional determinará, dentro del año siguiente de la expedición de este Código, las variables tales como: aforo, tipo de evento, clasificación de edad para el ingreso, lugar donde se desarrolla, infraestructura a utilizar, entorno del lugar, características del público, frecuencia, características de la presentación, carácter de la reunión, que determinarán la clasificación del evento como uno de los señalados en los numerales 2 y 3 del presente artículo. En concordancia con las características de cada uno de los municipios del país en cuanto a condiciones operativas y funcionales de los concejos municipales o distritales de gestión de riesgo de desastre.', 1, 287, 115, '29/11/17', 'The national government will determine, within the year following the issuance of this Code, the variables such as: capacity, type of event, age classification for admission, place where it is developed, infrastructure to be used, environment of the place, characteristics of the public , frequency, characteristics of the presentation, nature of the meeting, which will determine the classification of the event as one of those indicated in numerals 2 and 3 of this article. In accordance with the characteristics of each of the municipalities of the country in terms of operational and functional conditions of the municipal or district disaster risk management councils.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (194, 'Consumo controlado de bebidas alcohólicas en lugares habilitados para aglomeraciones', 'En los lugares habilitados para aglomeraciones, se autoriza el consumo de bebidas alcohólicas de acuerdo con las siguientes disposiciones:', 1, 90, 115, '29/11/17', 'In the places authorized for agglomerations, the consumption of alcoholic beverages is authorized in accordance with the following provisions:', 'Controlled consumption of alcoholic beverages in places enabled for agglomerations');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (195, ' ', 'Se prohíbe el expendio y/o consumo de bebidas alcohólicas en el espacio público que esté alrededor del recinto donde se lleva a cabo el espectáculo público, sin que la prohibición se extienda a los espacios privados, situados dentro del área mencionada, en los cuales operen establecimientos de comercio que expendan bebidas alcohólicas, conforme las reglas que regulan dicha actividad. La respectiva administración municipal determinará el perímetro de prohibición.', 1, 289, 115, '29/11/17', 'The sale and / or consumption of alcoholic beverages in the public space that is around the premises where the public show takes place is prohibited, without the prohibition extending to the private spaces, located within the aforementioned area, in which operate commercial establishments that sell alcoholic beverages, according to the rules that regulate said activity. The respective municipal administration will determine the prohibition perimeter.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (197, 'Cuidado del espacio público', 'Al terminar el uso del espacio público para el desarrollo de actividades que generen aglomeraciones de público, el lugar utilizado se debe dejar aseado y en las mismas condiciones en que se encontraba antes de su uso.', 1, 91, 115, '29/11/17', 'At the end of the use of public space for the development of activities that generate agglomerations of people, the place used should be left clean and in the same condition as it was before its use.', 'Care of public space');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (200, ' ', 'De manera excepcional la Policía Nacional podrá prestar servicios de vigilancia y seguridad dentro y fuera de actividades que involucren aglomeraciones de público complejas, cuando existan razones de fuerza mayor, seguridad u orden público que lo aconsejen, y no descuiden o distraigan esfuerzos relacionados con la seguridad y convivencia del municipio o departamento.', 1, 289, 115, '29/11/17', 'Exceptionally, the National Police may provide surveillance and security services in and out of activities that involve complex public gatherings, when there are reasons of force majeure, security or public order that advise it, and do not neglect or distract from efforts related to security. and coexistence of the municipality or department.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (202, ' ', 'Las actividades que involucren aglomeraciones de público complejas y no complejas, convocadas por las entidades públicas, contarán con la presencia de la Policía Nacional, siempre y cuando las situaciones lo exijan, para garantizar la seguridad y el orden público.', 1, 291, 115, '29/11/17', 'The activities that involve complex and non-complex public gatherings, convened by public entities, will have the presence of the National Police, as long as the situations demand it, to guarantee security and public order.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (204, ' ', 'Las reuniones y manifestaciones espontáneas de una parte de la población no se considerarán por sí mismas como alteraciones a la convivencia.', 1, 289, 116, '29/11/17', 'The meetings and spontaneous manifestations of a part of the population will not be considered by themselves as alterations to coexistence.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (205, ' ', 'El que irrespete las manifestaciones y reuniones de las personas en el espacio público, en razón a su etnia, raza, edad, género, orientación sexual, creencias religiosas, preferencias políticas y apariencia personal, será objeto de aplicación de medida correctiva correspondiente a Multa General Tipo 4.', 1, 290, 116, '29/11/17', 'Anyone who disrespects the demonstrations and meetings of people in the public space, by reason of their ethnicity, race, age, gender, sexual orientation, religious beliefs, political preferences and personal appearance, will be subject to the application of a corrective measure corresponding to a General Fine. Type 4', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (206, 'Uso de vías para el ejercicio del derecho de reunión o manifestación pública y pacífica en el espacio público', 'Los alcaldes distritales o municipales, salvo circunstancias excepcionales o de fuerza mayor, deberán autorizar el uso temporal de vías dentro de su jurisdicción para actos o eventos de ejercicio del derecho de reunión o manifestación pública y pacífica en el espacio público. En el caso de las vías arterias principales o corredores de transporte público colectivo deberán establecer un plan efectivo de desvíos para la movilización de los ciudadanos que no participan del acto o evento, como medida de protección de los derechos de los demás ciudadanos.', 1, 95, 116, '29/11/17', 'The district or municipal mayors, except in exceptional circumstances or force majeure, must authorize the temporary use of roads within their jurisdiction for acts or events to exercise the right of assembly or public and peaceful demonstration in public space. In the case of the main avenues or corridors of collective public transport, they must establish an effective deviation plan for the mobilization of citizens who do not participate in the act or event, as a measure of protection of the rights of other citizens.', 'Use of avenues to exercise the right of assembly or public and peaceful demonstration in public space');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (209, 'Acompañamiento a las movilizaciones', 'Los alcaldes distritales o municipales con el apoyo de funcionarios de los entes de control encargados de velar por la protección de los Derechos Humanos, acompañarán el ejercicio del derecho a la movilización pacífica. Cuando se presenten amenazas graves e inminentes a otros derechos, los alcaldes podrán intervenir, por medio de gestores de convivencia de naturaleza civil, para garantizar el goce efectivo de los derechos de la ciudadanía durante el desarrollo de la movilización.\nCuando se haya agotado la intervención de los gestores de convivencia y persistan graves amenazas pa ra los derechos a la vida y la integridad, la Policía Nacional podrá intervenir.', 1, 98, 116, '29/11/17', 'The district or municipal mayors, with the support of officials of the control bodies responsible for ensuring the protection of Human Rights, will accompany the exercise of the right to a peaceful mobilization. When serious and imminent threats to other rights arise, the mayors will be able to intervene, through coexistence managers of a civil nature, to guarantee the effective enjoyment of citizens'' rights during the development of the mobilization. When the intervention of the coexistence managers has been exhausted and serious threats to the rights to life and integrity persist, the National Police may intervene. ', 'Accompaniment to the mobilizations');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (211, ' ', 'La seguridad interna y externa en actividades que involucran aglomeraciones de público no complejas será responsabilidad de los organizadores, quienes en caso de ser necesario deberán contratarla con empresas de vigilancia y seguridad privada y/o empresas de logística legalmente constituidas.\nLos organizadores o las empresas de seguridad y vigilancia privada y/o empresas de logística podrán designar de manera específica a los encar gados de informar de manera inmediata a las autoridades de Policía sobre aquellas personas que estén contrariando la ley y las normas de convivencia dentro de las actividades que involucran aglomeraciones de público no complejas.\nEn casos excepcionales de riesgo grave a la convivencia y a la seguridad ciudadana, la Policía Nacional podrá, sin descuidar sus responsabilidades frente al resto de la población, complementar la seguridad privada en las actividades que involucran aglomeraciones de público no complejas.\nLa Policía Nacional podrá, sin embargo, ingresar a las actividades que involucran aglomeraciones de público no complejas, en el cumplimiento de sus funciones.', 1, 289, 117, '29/11/17', 'The internal and external security in activities that involve non-complex public gatherings will be the responsibility of the organizers, who if necessary must contract with private security and surveillance companies and / or legally constituted logistics companies.\r\nThe organizers or private security and surveillance companies and / or logistics companies may designate specifically those in charge of informing immediately the police authorities about those people who are contrary to the law and the rules of coexistence within the activities that involve non-complex public agglomerations.\r\nIn exceptional cases of serious risk to coexistence and citizen security, the National Police may, without neglecting its responsibilities towards the rest of the population, complement private security in activities involving non-complex public gatherings.\r\nThe National Police may, however, enter activities involving non-complex public gatherings in the performance of their duties. ', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (212, ' ', 'El Gobierno nacional reglamentará las condiciones especiales de operación de las empresas de vigilancia y seguridad privada y/o empresas de logística, que pretendan prestar el servicio de vigilancia en actividades que involucran aglomeraciones de público no complejas.', 1, 290, 117, '29/11/17', 'The national government will regulate the special operating conditions of the private security and surveillance companies and / or logistics companies that intend to provide the surveillance service in activities that involve non-complex public agglomerations.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (213, 'Comportamientos que ponen en riesgo la vida e integridad de las personas en las actividades que involucran aglomeraciones de público no complejas', 'Los siguientes comportamientos ponen en riesgo la vida e integridad de las personas en las actividades que involucran aglomeraciones de público no complejas y por tanto no deben realizarse:', 1, 100, 117, '29/11/17', 'The following behaviors put at risk the life and integrity of people in activities that involve non-complex public gatherings and therefore should not be carried out:', 'Behaviors that put at risk the life and integrity of people in activities that involve non-complex public gatherings');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (214, ' ', 'Quien incurra en uno o más de los comportamientos antes señalados, será objeto de la aplicación de las siguientes medidas:', 1, 287, 117, '29/11/17', 'Anyone who commits one or more of the aforementioned behaviors will be subject to the application of the following measures:', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (215, 'Definición de las actividades que involucran aglomeraciones de público complejas', ' Las actividades que involucran aglomeraciones de público complejas son aquellas que de acuerdo con variables tales como: aforo, tipo de evento, clasificación de edad para el ingreso, lugar donde se desarrolla, infraestructura a utilizar, entorno del lugar, características del público, frecuencia, características de la presentación, carácter de la reunión, den lugar a riesgos de afectación a la comunidad o a los bienes, generando una alta afectación de la dinámica normal del municipio, distrito o del área específica en que se realizan, y que por ello requieren condiciones especiales para su desarrollo, determinadas por el Gobierno nacional de conformidad con el parágrafo del artículo 46 del presente Código.', 1, 101, 118, '29/11/17', 'The activities that involve complex public agglomerations are those that according to variables such as: capacity, type of event, age classification for the entrance, place where it is developed, infrastructure to be used, surroundings of the place, characteristics of the public, frequency, characteristics of the presentation, nature of the meeting, give rise to risks of affectation to the community or to the property, generating a high affectation of the normal dynamics of the municipality, district or of the specific area in which they are carried out, and for that reason they require special conditions for its development, determined by the national Government in accordance with the paragraph of article 46 of this Code.', 'Definition of activities that involve complex public gatherings');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (216, ' ', 'Toda actividad que involucra la aglomeración de público compleja exige la emisión de un permiso, por parte del alcalde o su delegado. El permiso para el desarrollo de actividades que involucran aglomeraciones de público complejas en escenarios habilitados o no habilitados se concederá previo cumplimiento de los requisitos y las normas vigentes para cada tipo de escenario.', 1, 289, 118, '29/11/17', 'Any activity that involves the agglomeration of a complex public requires the issuance of a permit, by the mayor or his delegate. Permission for the development of activities that involve complex public gatherings in scenarios enabled or not enabled will be granted prior compliance with the requirements and regulations in force for each type of scenario.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (218, 'Actividades que involucran aglomeraciones de público complejas en establecimientos que desarrollen actividades económicas', 'El alcalde distrital o municipal reglamentará las actividades que involucran aglomeraciones de público complejas en establecimientos abiertos al público.', 1, 102, 118, '29/11/17', 'The district or municipal mayor will regulate the activities that involve complex public gatherings in establishments open to the public.', 'Activities that involve complex public gatherings in establishments that develop economic activities');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (220, ' ', 'El Gobierno nacional reglamentará las condiciones especiales de operación de las empresas de vigilancia y seguridad privada y/o empresas de logística, que pretendan prestar el servicio de vigilancia en las actividades que involucran aglomeraciones de público complejas.', 1, 287, 118, '29/11/17', 'The national government will regulate the special operating conditions of the private security and surveillance companies and / or logistics companies that intend to provide the surveillance service in the activities that involve complex public agglomerations.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (223, ' ', 'Los Alcaldes definirán el perímetro de ubicación de las aglomeraciones, teniendo en cuenta la existencia de las estaciones de servicio, depósitos de líquidos, químicos o sustancias inflamables, clínicas u hospitales.', 1, 290, 118, '29/11/17', 'The Mayors will define the perimeter of the location of the agglomerations, taking into account the existence of service stations, liquids, chemical or flammable substances, clinics or hospitals.', '');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (224, 'De los planes de emergencia y contingencia', 'Se entiende por plan de emergencia y contingencia el documento básico que prepara el organizador de espectáculos, actividades culturales en el espacio público, mediante el cual se señalan los lineamientos generales para proyectar, presentar y cumplir su realización. En este se analizan integralmente los riesgos para responder a las situaciones perturbadoras o de desorden, desastres, calamidades o emergencias generadas por hechos o fenómenos naturales o humanos, y se determinan las medidas de prevención, mitigación y respuesta, de conformidad con la forma y condiciones que para tales efectos establezca la entidad respectiva de prevenc ión y atención de emergencias.', 1, 105, 118, '29/11/17', 'Emergency and contingency plan is understood as the basic document prepared by the organizer of shows, cultural activities in the public space, through which the general guidelines for projecting, presenting and fulfilling its realization are indicated. In this the risks are fully analyzed to respond to disturbing or disorder situations, disasters, calamities or emergencies generated by natural or human events or phenomena, and preventive, mitigation and response measures are determined, in accordance with the form and conditions that for such effects establish the respective entity of prevention and attention of emergencies.', 'Of emergency and contingency plans');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (225, 'Registro de los planes de emergencia y contingencia', 'Los planes de emergencia y contingencia serán registrados y aprobados con anterioridad a la realización de la actividad que implique la reunión de personas, en los tiempos, términos y condiciones señalados en la presente norma y deberán considerar las siguientes variables:', 1, 106, 118, '29/11/17', 'The emergency and contingency plans will be registered and approved prior to the realization of the activity that implies the meeting of persons, in the times, terms and conditions indicated in this regulation and should consider the following variables:', 'Registration of emergency and contingency plans');
INSERT INTO ARTICULO (ID, TITULO_ESP, ARTICULO_ESP, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA, ARTICULO_ENG, TITULO_ENG) VALUES (496, 'Reparación de daños materiales por perturbación a la posesión y tenencia de inmuebles o muebles', 'Es la orden de Policía por medio de la cual se exige a una persona, reparar un daño material causado en un bien inmueble o mueble, sin perjuicio de los procedimientos y las acciones civiles a las que haya lugar.\nQuien sea objeto de esta medida deberá probar su cumplimiento a la autoridad que la ordenó.', 1, 230, 141, '29/11/17', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.');
