
CREATE TABLE IF NOT EXISTS NIVEL (
  ID INT NOT NULL,
  NIVEL_ESP TEXT NULL,
  NIVEL_ENG TEXT NULL,
  VIGENTE BIT NULL,
  FECHA DATE NULL,
  PRIMARY KEY (ID));

CREATE TABLE IF NOT EXISTS LIBRO (
  ID INT NOT NULL,
  LIBRO_ESP TEXT NULL,
  LIBRO_ENG TEXT NULL,
  VIGENTE BIT NULL,
  NIVEL_ID INT NOT NULL,
  FECHA DATE NULL,
  PRIMARY KEY (ID),
  CONSTRAINT FK_LIBRO_NIVEL
    FOREIGN KEY (NIVEL_ID)
    REFERENCES NIVEL (ID)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION);

CREATE INDEX IX_LIBRO_NIVEL ON LIBRO (NIVEL_ID ASC);

