INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (412, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En el marco de la regulación del servicio público de transporte masivo, las empresas públicas, privadas o mixtas que presten el servicio público de transporte masivo de pasajeros deberán implementar cámaras de vigilancia dentro de los vehículos destinados a la prestación del servicio, so pena de incurrir en Multa General Tipo 4 e inmovilización del vehículo.\r\nEsta medida solo será exigible a los vehículos destinados a la prestación del servicio que entren en circulación a partir de la expedición de la presente ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 139, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (416, 'Orden de Policía', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La orden de Policía es un mandato claro, preciso y conciso dirigido en forma individual o de carácter general, escrito o verbal, emanado de la autoridad de Policía, para prevenir o superar comportamientos o hechos contrarios a la convivencia, o para restablecerla.\r\nLas órdenes de Policía son de obligatorio cumplimiento. Las personas que las desobedezcan serán obligadas a cumplirlas a través, si es necesario, de los medios, medidas y procedimientos establecidos en este Código. Si la orden no fuere de inmediato cumplimiento, la autoridad conminará a la persona para que la cumpla en un plazo determinado, sin perjuicio de las acciones legales pertinentes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 192, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (419, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Solicitado el permiso, este deberá concederse o negarse por escrito, y ser motivado. Si se concede, debe expresar con claridad las condiciones de tiempo, modo y lugar, su vigencia y las causales de suspensión o revocación. Cuando se expida en atención a las calidades individuales de su titular, así debe constar en el permiso y será personal e intransferible. De tal permiso se enviará copia a las entidades de control pertinentes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 140, '29/11/17');
