INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (393, 'Causales de agravación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Tiene el carácter de grave toda infracción urbanística contemplada en el presente Código, que genere impactos ambientales no mitigables o el deterioro irreparable de los recursos naturales o del patrimonio natural, urbanístico, arquitectónico, arqueológico y cultural. También tiene ese carácter, la repetición en la infracción de normas urbanísticas estructurales del plan de ordenamiento territorial o el incumplimiento de la orden de suspensión y sellamiento de la obra.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 178, 135, '29/11/17');
