INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (396, 'Definición del espacio público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el conjunto de muebles e inmuebles públicos, bienes de uso público, bienes fiscales, áreas protegidas y de especial importancia ecológica y los elementos arquitectónicos y naturales de los inmuebles privados, destinados por su naturaleza, usos o afectación, a la satisfacción de necesidades colectivas que trascienden los límites de los intereses individuales de todas las personas en el territorio nacional.\r\nConstituyen espacio público: el subsuelo, el espectro electromagnético, las áreas requeridas para la circulación peatonal, en bicicleta y vehicular; la recreación pública, activa o pasiva; las franjas de retiro de las edificaciones sobre las vías y aislamientos de las edificaciones, fuentes de agua, humedales, rondas de los cuerpos de agua, parques, plazas, zonas verdes y similares; las instalaciones o redes de conducción de los servicios públicos básicos; las instalaciones y los elementos constitutivos del amoblamiento urbano en todas sus expresiones; las obras de interés público y los elementos históricos, culturales, religiosos, recreativos, paisajísticos y artísticos; los terrenos necesarios para la preservación y conservación de las playas marinas y fluviales; los terrenos necesarios de bajamar, así como sus elementos vegetativos, arenas, corales y bosques nativos, legalmente protegidos; la zona de seguridad y protección de la vía férrea; las estructuras de transporte masivo y, en general, todas las zonas existentes y debidamente afectadas por el interés colectivo manifiesto y conveniente y que constituyen, por consiguiente, zonas para el uso o el disfrute colectivo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 181, 136, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (397, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Para efectos de este Código se entiende por bienes fiscales, además de los enunciados por el artículo 674 del Código Civil, los de propiedad de entidades de derecho público, cuyo uso generalmente no pertenece a todos los habitantes y sirven como medios necesarios para la prestación de las funciones y los servicios públicos, tales como los edificios, granjas experimentales, lotes de terreno destin ados a obras de infraestructura dirigidas a la instalación o dotación de servicios públicos y los baldíos destinados a la explotación económica.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 136, '29/11/17');
