INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (415, 'Medios de Policía', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los medios de Policía son los instrumentos jurídicos con que cuentan las autoridades competentes para el cumplimiento efectivo de la función y actividad de Policía, así como para la imposición de las medid as correctivas contempladas en este Código.\r\nLos medios de Policía se clasifican en inmateriales y materiales.\r\nLos medios inmateriales son aquellas manifestaciones verbales o escritas que transmiten decisiones de las autoridades de Policía.\r\nSon medios inmateriales de Policía:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 191, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (417, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El incumplimiento de la orden de Policía mediante la cual se imponen medidas correctivas configura el tipo penal establecido para el fraude a resolución judicial o administrativa de Policía establecido en el artículo 454 de la Ley 599 de 2000.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (418, 'Permiso excepcional', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el medio por el cual el funcionario público competente, de manera excepcional y temporal, permite la realización de una actividad que la ley o normas de Policía establecen como prohibición de carácter general, de conformidad con las normas que la regulen. El permiso solo se otorgará cuando no altere o represente riesgo a la convivencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 193, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (420, 'Reglamentos', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Son aquellos que dicta el Presidente de la República, el Gobernador o el Alcalde municipal o distrital y las corporaciones administrativas del nivel territorial en el ámbito de su jurisdicción, de conformidad con la ley.\r\nSu finalidad es la de establecer condiciones al ejercicio de una actividad o derecho que perturbe la libertad o derechos de terceros, que no constituyen reserva de ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 194, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (421, 'Autorización', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el acto mediante el cual un funcionario público, de manera temporal, autoriza la realización de una actividad cuando la ley o las normas de Policía subordinen su ejercicio a ciertas condiciones. Dicha actividad no podrá realizarse sin la autorización y cumplimiento de estas.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 195, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (423, 'Mediación Policial', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el instrumento que nace de la naturaleza de la función policial, cuyas principales cualidades son la comunitariedad y la proximidad, a través del cual la autoridad es el canal para que las personas en conflicto decidan voluntariamente resolver sus desacuerdos armónicamente.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 196, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (424, 'Traslado por protección', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Cuando la vida e integridad de una persona o de terceros esté en riesgo o peligro, el personal uniformado de la Policía Nacional, podrá trasladarla para su protección o la de terceros, en los siguientes casos:\r\nCuando deambule en estado de indefensión o de grave alteración del estado de conciencia por aspectos de orden mental, o bajo efectos del consumo de bebidas alcohólicas o sustancias psicoactivas o tóxicas, cuando el traslado sea el único medio disponible para evitar el riesgo a la vida o integridad de la persona o los terceros.\r\nCuando esté involucrado en riña o presente comportamientos agresivos o temerarios, realice actividades peligrosas o de riesgo que pongan en peligro su vida o integridad o la de terceros, o esté en peligro de ser agredido cuando el traslado sea el único medio disponible para evitar el riesgo a la vida o integridad de la persona o los terceros.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 197, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (425, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Cuando el comportamiento señalado en el inciso 3 del presente artículo se presente en contra de una autoridad de Policía, se podrá utilizar este medio.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (426, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Antes del traslado y como primera medida, la autoridad de Policía entregará la persona a un allegado o pariente que asuma la protección; en la ausencia de estos, se trasladara la persona a un centro asistencial o de protección, de salud u hospital o a un lugar especialmente destinado para tal fin por la administración municipal, según sea necesario, o, en cuanto fuera posible, se intentará llevarla a su domicilio. En ningún caso se hará traslados a sitios destinados a la privación de libertad y la duración del mismo no podrá ser mayor a doce (12) horas. Es deber de las Alcaldías definir el lugar al que pueden ser enviadas las personas, separadas en razón del sexo.\r\nEn el centro asistencial o de protección deberá hacer presencia un representante del Ministerio Público.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (427, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La autoridad de Policía que ordena y ejecuta el traslado, deberá informar a la persona trasladada y al superior jerárquico de la unidad policial y elaborar un informe escrito donde consten los nombres e identificación de la persona trasladada por cualquier medio; de quien da la orden y quien la ejecuta, el motivo, el sitio al que se traslada y el nombre del allegado o a quien la persona trasladada informa para ser asistido, de ser ello posible. A la persona, sujeto de la medida, se le deberá entregar copia de dicho informe.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (429, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Cuando se trate de un traslado por alteración del estado de conciencia, porque la persona se encuentra bajo el efecto del consumo de bebidas alcohólicas o sustancias psicoactivas o tóxicas no podrá ser trasladada por el simple hecho de estar consumiendo sino que deben existir motivos fundados y el agente de Policía con fundamento en el principio de proporcionalidad determinará si existen las razones objetivas previstas en este Código.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 293, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (430, 'Retiro de sitio', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en apartar de un lugar público o abierto al público o que siendo privado preste servicios al público, área protegida o de especial importancia ecológica, a la persona que altere la convivencia y desacate una orden de Policía dada para cesar su comportamiento, e impedir el retorno inmediato al mismo, sin perjuicio de la utilización de otros medios, así como de las medidas correctivas a que haya lugar.\r\nEl personal uniformado de la Policía Nacional podrá hacer uso de este medio cuando sea necesario.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 198, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (432, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La autoridad de Policía que ordena y ejecuta el traslado, deberá informar a la persona trasladada y al superior jerárquico de la unidad policial y elaborar un informe escrito donde consten los nombres e identificación de la persona trasladada por cualquier medio, de quien da la orden y quien la ejecuta, el motivo, el sitio al que se traslada, la justificación del tiempo empleado para el traslado y el nombre del allegado o a quien la persona trasladada informa para ser asistido, de ser ello posible. A la persona, sujeto de la medida, se le deberá entregar copia de dicho informe.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (433, 'Registro', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Acción que busca identificar o encontrar elementos, para prevenir o poner fin a un comportamiento contrario a norma de convivencia o en desarrollo de actividad de Policía, la cual se realiza sobre las personas y medios de transporte, sus pertenencias y bienes muebles e inmuebles, de conformidad con lo establecido en la ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 200, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (434, 'Registro a persona', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional podrá registrar personas y los bienes que posee, en los siguientes casos:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 201, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (435, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El registro de personas y sus bienes podrá realizarse en las vías públicas, en los espacios públicos, en establecimientos de comercio o de otra naturaleza abiertos al público, en espacios privados con acceso o con servicios al público, y en las zonas comunes de inmuebles de propiedad horizontal o similares, o dentro de domicilio privado si el propietario, poseedor o inquilino, así lo autoriza.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (437, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El registro de personas por parte de las empresas de servicios de vigilancia y seguridad privada no se realizarán mediante contacto físico, salvo que se trate del registro de ingreso a espectáculos o eventos de conformidad con la reglamentación que para tal efecto establezca el Gobierno nacional, o salvo que el personal uniformado de la Policía Nacional lo solicite, en apoyo a su labor policial.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (438, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional y el personal de las empresas de vigilancia y seguridad privada, podrán utilizar medios técnicos o tecnológicos para el registro de personas y bienes tales como detector de metales, escáner de cuerpo entero, sensores especiales y caninos entrenados para tal fin. El Gobierno nacional reglamentará el uso de ese tipo de medios y sus protocolos.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 292, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (439, 'Registro a medios de transporte', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional podrá efectuar el registro de medios de transporte públicos o privados, terrestres, aéreos, marítimos y fluviales, y de los paraderos, estaciones, terminales de transporte terrestre, aeropuertos, puertos y marinas, de conformidad con las disposiciones que rigen la materia, y en los siguientes casos, para garantizar la convivencia y la seguridad:\r\nPara establecer la identidad de los ocupantes y adelantar el registro de las personas que ocupan el medio y sus bienes, de conformidad con este Código.\r\nPara establecer la titularidad del derecho de dominio del medio de transporte y verificar la procedencia y la legalidad del medio de transporte, y de los bienes y objetos transportados.\r\nPara constatar características o sistemas de identificación del medio de transporte.\r\nCuando se tenga conocimiento o indicio de que el medio de transporte está siendo utilizado o sería utilizado, para la comisión de un comportamiento contrario a la convivencia o una conducta punible.\r\nEn desarrollo de una operación policial ordenada por la institución policial o por mandamiento judicial, en cuyo caso se atenderán los procedimientos establecidos.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 202, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (440, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Se entiende por medio de transporte todo medio que permita la movilización o el desplazamiento de una persona o grupo de personas, de un lugar a otro, independientemente de sus características o tipo de tracción utilizada.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (442, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Se exceptúan del registro señalado los medios de transporte que gozan de inmunidad diplomática, salvo que existan indicios de una suplantación, para lo cual deberá contar con autorización de la misión diplomática.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (444, 'Suspensión inmediata de actividad', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el cese inmediato de una actividad, cuya continuación implique un riesgo inminente para sus participantes y la comunidad en general. Una vez aplicado este medio, la autoridad de Policía informará por escrito y de manera inmediata a la autoridad competente a la que le corresponda imponer la medida correctiva a que hubiere lugar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 203, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (445, 'Ingreso a inmueble con orden escrita', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los alcaldes podrán dictar mandamiento escrito para el registro de domicilios o de sitios abiertos al público, en los siguientes casos:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 204, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (447, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El ingreso a un inmueble deberá realizarse de manera respetuosa, tanto con las personas como con sus bienes. En caso de oposición a la orden de ingreso, la autoridad podrá hacer uso de la fuerza de manera excepcional y proporcional a los actos opuestos.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (449, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Si de manera circunstancial o por descubrimiento inevitable en el procedimiento, se encuentran elementos que justifiquen la iniciación de una acción penal, la autoridad de Policía informará al personal uniformado de la Policía Nacional o a la Policía Judicial para que inicie el procedimiento estipulado en el Código de Procedimiento Penal.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 292, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (452, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional, por razones propias de sus funciones, podrá ingresar sin orden escrita a un bien inmueble cuando esté abierto al público.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (453, 'Incautación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es la aprehensión material transitoria de bienes muebles, semovientes, flora y fauna silvestre que efectúa el personal uniformado de la Policía Nacional, cuya tenencia, venta, oferta, suministro, distribución, transporte, almacenamiento, importación, exportación, porte, conservación, elaboración o utilización, constituya comportamiento contrario a la convivencia y a la ley. El personal uniformado de la Policía Nacional documentará en un acta el inventario de los bienes incautados, las razones de orden legal que fundamentan la incautación, entregará copia a la persona a quien se le incauten y serán puestos a disposición de las autoridades competentes en el término de la distancia y conforme al procedimiento que para tal fin establezca la Policía Nacional o las autoridades pertinentes de conformidad con la normatividad vigente.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 206, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (454, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El Gobierno nacional, dentro del año siguiente a la promulgación de la presenta ley, definirá mediante decreto, la entidad del orden nacional o territorial responsable del traslado, almacenamiento, preservación, depósito, cuidado y administración de los bienes incautados por las autoridades y la asignación de los recursos para tal fin, de conformidad con el régimen de Policía vigente. En el marco de esta facultad, el Gobierno nacional podrá considerar la tercerización, contratación y concesión de dichos servicios.\r\nLos concejos municipales en un plazo de un (1) año a partir de la promulgación de la presente ley, establecerán los cosos (centros de bienestar animal) destinados a albergar los animales domésticos incautados por las autoridades de Policía.\r\nEl Ministerio de Tecnologías de la Información y las Comunicaciones, en un plazo de un (1) año a partir de la promulgación de la presente ley, determinará la dependencia que se encargará de recibir los equipos terminales móviles incautados por la Policía Nacional; mientras tanto, se continuará con el procedimiento vigente al momento de entrada en vigencia de esta ley para los equipos terminales móviles incautados.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 288, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (455, 'Incautación de armas de fuego, no convencionales, municiones y explosivos', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La Policía Nacional tendrá como una de sus funciones la de incautar y decomisar toda clase de armas, accesorios, municiones y explosivos, cuando con estas se infrinjan las normas, y procederá a la toma de muestras, fijación a través de imágenes y la documentación de los mismos.\r\nLos elementos incautados serán destruidos, excepto cuando las armas o municiones sean elementos materiales probatorios dentro de un proceso penal. Una vez finalizado el proceso, estas armas serán devueltas a la Policía Nacional para que procedan de conformidad con el presente artículo. El personal uniformado de la Policía Nacional documentará en un acta el inventario de las armas o municiones incautadas, las razones de orden legal que fundamentan la incautación y entregará copia a la persona a quien se le incaute.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 207, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (456, 'Uso de la fuerza', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el medio material, necesario, proporcional y racional, empleado por el personal uniformado de la Policía Nacional, como último recurso físico para proteger la vida e integridad física de las personas incluida la de ellos mismos, sin mandamiento previo y escrito, para prevenir, impedir o superar la amenaza o perturbación de la convivencia y la seguridad pública, de conformidad con la ley.\r\nEl uso de la fuerza se podrá utilizar en los siguientes casos:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 208, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (457, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional sólo podrá utilizar los medios de fuerza autorizados por ley o reglamento, y al hacer uso de ellos siempre escogerá entre los más eficaces, aquellos que causen menor daño a la integridad de las personas y de sus bienes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (458, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional está obligado a suministrar el apoyo de su fuerza por iniciativa propia o a petición de persona que esté urgida de esa asistencia, para proteger su vida o la de terceros, sus bienes, domicilio y su libertad personal.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (459, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional que dirija o coordine el uso de la fuerza, informará al superior jerárquico y a quien hubiese dado la orden de usarla, una vez superados los hechos que dieron lugar a dicha medida, precisando las circunstancias de tiempo, modo y lugar, y desenlace de los hechos. En caso de que se haga uso de la fuerza que cause daños colaterales, se remitirá informe escrito al superior jerárquico y al Ministerio Público.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (461, 'Aprehensión con fin judicial', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional, podrá aprehender a una persona en sitio público o abierto al público, o privado, cuando sea señalada de haber cometido infracción penal o sorprendida en flagrante delito o cuando un particular haya pedido auxilio o la haya aprehendido, siempre que el solicitante concurra conjuntamente al despacho del funcionario que deba recibir formalmente la denuncia.\r\nEl personal uniformado de la Policía Nacional la conducirá de inmediato a la autoridad judicial competente, a quien le informará las causas de la aprehensión, levantando un acta de dicha diligencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 210, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (462, 'Apoyo urgente de los particulares', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En casos en que esté en riesgo inminente la vida e integridad de una persona, el personal uniformado de la Policía Nacional, podría solicitar y exigir el apoyo de los particulares a las funciones y actividades de Policía y hacer uso inmediato de sus bienes para atender la necesidad requerida. Las personas sólo podrán excusar su a poyo cuando su vida e integridad quede en inminente riesgo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 211, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (463, 'Asistencia militar', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el instrumento legal que puede aplicarse cuando hechos de grave alteración de la seguridad y la convivencia lo exijan, o ante riesgo o peligro inminente, o para afrontar emergencia o calamidad pública, a través del cual el Presidente de la República, podrá disponer, de forma temporal y excepcional de la asistencia de la fuerza militar. No obstante, los gobernadores y Alcaldes Municipales o Distritales podrán solicitar al Presidente de la República tal asistencia, quien evaluará la solicitud y tomará la decisión. La asistencia militar se regirá por los protocolos y normas especializadas sobre la materia y en coordinación con el comandante de Policía de la jurisdicción.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 212, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (465, 'Respeto mutuo', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La relación de las personas y las autoridades de Policía, se basará en el respeto. Las personas tienen derecho a ser tratados de manera respetuosa, con consideración y reconocimiento a su dignidad. El irrespeto a las personas por parte de las autoridades de Policía, será causal de investigación disciplinaria. Las autoridades de Policía a su turno, merecen un trato acorde con su investidura y la autoridad que representan, por tal motivo, es obligación de las personas prestar atención a las autoridades de Policía, reconocer su autoridad, obedecer sus órdenes, y hacer uso de un lenguaje respetuoso. El irrespeto por parte de las personas a las autoridades de Policía, conllevará la imposición de medidas correctivas. La agresión física a las autoridades de Policía se considera un irrespeto grave a la autoridad, sin perjuicio de la acción penal a que haya lugar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 213, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (466, 'Objeto de las medidas correctivas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las medidas correctivas, son acciones impuestas por las autoridades de Policía a toda persona que incurra en comportamientos contrarios a la convivencia o el incumplimiento de los deberes específicos de convivencia. Las medidas correctivas tienen por objeto disuadir, prevenir, superar, resarcir, procurar, educar, proteger o restablecer la convivencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 214, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (467, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las medidas correctivas no tienen carácter sancionatorio. Por tal razón, deberán aplicarse al comportamiento contrario a la convivencia las medidas correctivas establecidas en este código y demás normas que regulen la materia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 141, '29/11/17');
