INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (483, 'Multas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es la imposición del pago de una suma de dinero en moneda colombiana, cuya graduación depende del comportamiento realizado, según la cual varía el monto de la multa. Así mismo, la desobediencia, resistencia, desacato, o reiteración del comportamiento contrario a la convivencia, incrementará el valor de la multa, sin perjuicio de los intereses causados y el costo del cobro coactivo.\r\nLas multas se clasifican en generales y especiales.\r\nLas multas generales se clasifican de la siguiente manera:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 222, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (484, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las multas serán consignadas en la cuenta que para el efecto disponga las administraciones distritales y municipales, y se destinarán a proyectos pedagógicos y de prevención en materia de seguridad, así como al cumplimiento de aquellas medidas correctivas impuestas por las autoridades de Policía cuando su materialización deba ser inmediata, sin perjuicio de las acciones que deban adelantarse contra el infractor, para el cobro de la misma. En todo caso, mínimo el sesenta por ciento (60%) del Fondo deberá ser destinado a la cultura ciudadana, pedagogía y prevención en materia de seguridad.\r\nCuando los Uniformados de la Policía Nacional tengan conocimiento de la ocurrencia de un comportamiento, que admita la imposición de multa general, impondrán orden de comparendo al infractor, evidenciando el hecho.\r\nEs deber de toda persona natural o jurídica, sin perjuicio de su condición económica y social, pagar las multas, salvo que cumpla la medida a través de la participación en programa comunitario o actividad pedagógica de convivencia, de ser aplicable. A la persona que pague la multa durante los cinco (5) días hábiles siguientes a la expedición del comparendo, se le disminuirá el valor de la multa en un cincuenta (50%) por ciento, lo cual constituye un descuento por pronto pago.\r\nA cambio del pago de la Multa General tipo 1 y 2 la persona podrá, dentro de un plazo máximo de cinco (5) días hábiles siguientes a la expedición del comparendo, solicitar a la autoridad de Policía que se conmute la multa por la participación en programa comunitario o actividad pedagógica de convivencia.\r\nSi la persona no está de acuerdo con la aplicación de la multa señalada en la orden de comparendo o con el cumpl imiento de la medida de participación en programa comunitario o actividad pedagógica de convivencia, cuando este aplique, podrá presentarse dentro de los tres (3) días hábiles siguientes ante la autoridad competente, para objetar la medida mediante el procedimiento establecido en este Código.\r\nUna vez liquidadas y comunicadas, si las multas no fueren pagadas dentro del mes siguiente, el funcionario competente deberá reportar la existencia de la deuda al Boletín de Responsables Fiscales de la Contraloría General de la República y así mismo deberá reportar el pago de la deuda.\r\nLa administración distrital o municipal podrá reglamentar la imposición de la medida correctiva de participación en programa pedagógico para los comportamientos contrarios a la convivencia que admitan multa tipo 1 y 2, en reemplazo de la multa.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (485, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Durante el primer año de vigencia de la presente ley, las personas a las que se les imponga una Multa General tipo 3 o 4 podrán obtener un descuento adicional al previsto por el pronto pago de la multa, en un porcentaje del veinticinco por ciento (25%) de su valor total, siempre y cuando soliciten a la autoridad de Policía competente que se les permita participar en programa o actividad pedagógica de convivencia, dentro de los cinco (5) días siguientes a la expedición del comparendo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 288, 141, '29/11/17');
