INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (497, 'Restablecimiento del derecho de servidumbre y reparación de daños materiales', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en permitir en el predio sirviente, el uso de la servidumbre señalada en escritura pública a que tiene derecho y si se causaron daños naturales repararlos a su costa.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 231, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (498, 'Restitución y protección de bienes inmuebles', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en devolver la posesión o tenencia a quien tiene el legítimo derecho sobre los bienes inmuebles de particulares, baldíos, fiscales, de uso público, área protegida y de especial importancia ecológica¸ bienes de empresas destinados a servicios públicos cuando hayan sido ocupadas o perturbadas por vías de hecho.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 232, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (499, 'Inutilización de bienes', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en la inhabilitación total de los bienes empleados para actividades ilícitas que atenten contra los recursos naturales, o ingresen, permanezcan, operen, en áreas protegidas y de especial importancia ecológica.\r\nLo anterior no implica que el infractor, propietario, tenedor o poseedor, impute cualquier responsabilidad patrimonial por acción o por omisión al Estado o a sus agentes.\r\nPara la aplicación de esta medida se documentará la actuación policial y después de la inutilización, se infor mará a las autoridades competentes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 233, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (500, 'Destrucción de bien', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en destruir por motivos de interés general un bien mueble cuando implique un riesgo o amenaza a la convivencia o al ambiente, o sea utilizado de manera ilegal con perjuicio a terceros. El personal uniformado de la Policía Nacional, definirá si la destrucción de bien deberá ser inmediata, en el sitio o si debe ser llevado a un lugar especial para tal fin.\r\nPara la aplicación de esta medida se documentará la actuación policial y después de la destrucción, se informará a las autoridades competentes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 234, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (501, 'Suspensión de construcción o demolición', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en el sellamiento y la suspensión de los trabajos de construcción o demolición de obra, iniciada sin licencia previa, o adelantada con violación de las condiciones de la licencia. La medida será efectiva hasta cuando se supere la razón que dio origen a la misma.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 235, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (503, 'Suspensión de actividad que involucre aglomeración de público compleja', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en impedir el inicio o desarrollo de un evento público o privado por parte de la autoridad que haya expedido el permiso, mediante resolución motivada, por el incumplimiento de lo dispuesto en el Título VI del Libro Segundo de este Código, en el Capítulo IV de la Ley 1493 de 2011, para el caso de espectáculos públicos de las artes escénicas, y/o en las condiciones previstas en el acto administrativo de autorización del evento, o cuando el recinto o lugar donde vaya a realizarse no cumpla con los requisitos exigidos por las normas existentes o exista un riesgo inminente.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 237, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (504, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La suspensión de actividades que involucran aglomeraciones de público complejas se hará a solicitud motivada de la autoridad de Policía. Recibida la solicitud de suspensión por parte de la autoridad de Policía, la autoridad competente suspenderá preventivamente la realización de la actividad que involucre aglomeraciones de público complejas, hasta tanto las condiciones que generaron dicha solicitud sean subsanadas o desaparezcan.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (507, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La medida se mantendrá, aun en los casos de cambio de nomenclatura, razón social o de responsable de la actividad o cuando se traslada la actividad a lugar distinto en la misma edificación o en inmueble colindante. Si se prueba que el cambio de razón social, de responsable o de lugar, es para evadir la medida correctiva se impondrá suspensión definitiva.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (508, 'Suspensión definitiva de actividad', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es el cese definitivo de una actividad económica, formal o informal, con o sin ánimo de lucro, o que siendo privada ofrezca servicios al público a la que está dedicada una persona natural o jurídica; comprende la suspensión definitiva de la autorización o permiso dado a la persona o al establecimiento respectivo, para el desarrollo de la actividad.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 239, 141, '29/11/17');
