INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (514, 'Competencia del gobernador', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', ' El gobernador es la primera autoridad de Policía del departamento y le corresponde garantizar la convivencia y seguridad en su territorio.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 242, 142, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (517, 'Competencia especial del gobernador', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En caso de actos de ocupación o perturbación por vías de hecho a la posesión o tenencia de bienes inmuebles de particulares, fiscales, de uso público, áreas protegidas y de especial importancia ecológica, de empresas de servicios públicos, de utilidad pública o de interés social, o en actividades consideradas de actividad pública o de interés social, en que a las autoridades de Policía distrital o municipal se les dificulte por razones de orden público adelantar un procedimiento policivo o ejecutar las órdenes de restitución, por solicitud del Alcalde Distrital o Municipal, o de quien solicita la intervención de la autoridad de Policía, asumirá la competencia el gobernador o su delegado, para que se proteja o restituya la posesión o tenencia del bien inmueble, o la interrupción de la perturbación de la actividad de utilidad pública o de interés social. También, ejecutará la orden de restitución de tierras ordenada por un juez, cuando por razones de orden público a la autoridad de Policía distrital o municipal se le dificulte materializarla.\r\nDe igual manera, cuando el alcalde distrital o municipal, no se pronuncien dentro de los términos establecidos en las normas que para tal fin expida el Gobierno nacional, a solicitud del accionante, o de oficio, el Gobernador del Departamento o su delegado asumirá la competencia y procederá a hacer efectivo sin perjuicio de las investigaciones disciplinarias a que haya lugar, conforme al Código Disciplinario Único o las normas que lo adicionen, modifiquen o substituyan.\r\nCuando el Gobernador del Departamento ante quien se eleva la solicitud, no asuma la competencia especial en el término establecido en las normas que para tal efecto establezca el Gobierno nacional, este asumirá la competencia a través de su delegado, y pedirá a la Procuraduría General de la Nación, las investigaciones disciplinarias pertinentes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 245, 142, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (518, 'Alcalde distrital o municipal', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El alcalde es la primera autoridad de Policía del Distrito o Municipio. En tal condición, le corresponde garantizar la convivencia y la seguridad en su jurisdicción.\r\nLa Policía Nacional cumplirá con prontitud y diligencia las órdenes que le imparta el Alcalde por conducto del respectivo comandante.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 246, 142, '29/11/17');
