INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (567, 'Costas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En los procesos de Policía no habrá lugar al pago de costas.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 273, 145, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (569, 'Conciliación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La conciliación en materia de convivencia procederá ante la autoridad de Policía que conozca del caso, en cualquier etapa del trámite del procedimiento o en el momento en que se presente el desacuerdo o conflicto de convivencia.\r\nUna vez escuchados quienes se encuentren en desacuerdo o conflicto, la autoridad de Policía o el conciliador, propondrá fórmulas de solución que aquellos pueden acoger o no. De realizarse el acuerdo, se suscribirá el acta de conciliación, donde se consignarán las obligaciones a cargo de cada uno de los interesados, lo cual hará tránsito a cosa juzgada y prestará mérito ejecutivo ante las autoridades judiciales competentes.\r\nLas medidas correctivas de competencia de los comandantes de estación, subestación o centro de atención inmediata de Policía, no son susceptibles de conciliación.\r\nNo son conciliables los comportamientos que infringen o resultan contrarios a las normas urbanísticas, ambientales, sanitarias, del uso del espacio público, del ejercicio de la actividad económica, de la libertad de circulación, de las interacciones entre las personas y las autoridades, los que afectan la integridad de niños, niñas y adolescentes, del ejercicio de la prostitución, y del derecho de reunión.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 275, 146, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (571, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En los procedimientos a que hace referencia el Título VII del Libro II, será obligatorio la invitación a conciliar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 146, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (572, 'Conciliadores y mediadores', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Además de las autoridades de Policía, pueden ser conciliadores o mediadores en el sector urbano o rural, los jueces de paz, las personerías, los centros de conciliación de universidades, las cámaras de comercio del país y demás centros de conciliación del sector privado, siempre que el servicio sea gratuito.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 277, 146, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (574, 'Programa de educación y promoción del Código', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El Gobierno nacional, a través de las autoridades competentes, deberá diseñar programas, actividades y campañas de promoción en todo el territorio nacional, de las disposiciones más relevantes contenidas en el presente Código, especialmente de los comportamientos contrarios a la convivencia y las consecuencias que se derivan de su realización, con el fin de que la ciudadanía conozca y se actualice en torno a los aspectos trascendentales de esta ley.\r\nAsí mismo deberá adelantar jornadas de capacitación y formación del nuevo Código de Policía y convivencia a las autoridades de Policía, a partir de su promulgación.\r\nDe igual forma, a través del Ministerio de Educación, desarrollará programas para el fomento de competencias que fortalezcan la cultura ciudadana y la convivencia así como el respeto por las normas y las autoridades, en concordancia con los lineamientos definidos en la Ley 1013 de 2006 y la Ley 1732 de 2015.\r\nEstos programas serán implementados por las Instituciones Educativas en el marco de su autonomía escolar y su contenido.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 279, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (575, 'Integración de sistemas de vigilancia', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La información, imágenes, y datos de cualquier índole captados y/o almacenados por los sistemas de video o los medios tecnológicos que estén ubicados en el espacio público, o en lugares abiertos al público, serán considerados como públicos y de libre acceso, salvo que se trate de información amparada por reserva legal.\r\nLos sistemas de video y medios tecnológicos, o los que hagan sus veces, de propiedad privada o pública, a excepción de los destinados para la Defensa y Seguridad Nacional, que se encuentren instalados en espacio público, áreas comunes, lugares abiertos al público o que siendo privados trasciendan a lo público, se enlazará de manera permanente o temporal a la red que para tal efecto disponga la Policía Nacional, de acuerdo con la reglamentación que para tal efecto expida el Gobierno nacional.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 280, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (576, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En tratándose de sistemas instalados en áreas comunes, lugares abiertos al público o que siendo privados trasciendan a lo público, se requerirá para el enlace a que hace referencia el presente artículo, la autorización previa por parte de quien tenga la legitimidad para otorgarla.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (578, 'Aplicación de la ley', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los procedimientos por contravenciones al régimen de Policía, así como los procedimientos administrativos sustituidos por la presente ley, que a la fecha de la entrada en vigencia de la misma se estén surtiendo, serán adelantados hasta su finalización, conforme a la legislación vigente a la fecha de ocurrencia de los hechos que motivaron su iniciación.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 282, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (579, 'Concordancias', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las disposiciones en materia de circulación o movilidad, infancia y adolescencia, salud, ambiente, minería, recursos naturales y protección animal establecidas en el presente Código, serán aplicadas en concordancia con la legislación vigente.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 283, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (581, 'Derogatorias', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El presente Código deroga todas las disposiciones que le sean contrarias, en especial el Decreto-ley 1355 de 1970, la Ley 1356 de 2009 excepto los artículos 4° y del 218A al 218L; el Decreto número 522 de 1971; la Ley 232 de 1995; el artículo 108 de la Ley 388 de 1997; los artículos 1° y 2° de la Ley 810 de 2003; artículo 12 numeral 2, artículo 17, 18, 21, 22, 23, 24, 25, 26, 27, 28, 29, 31, 33, 35 y 36 del Decreto número 2876 de 1984; artículo 26 y último inciso o párrafo del artículo 10 de la Ley 679 de 2001, en razón a que se aplicará el proceso verbal abreviado establecido en el presente Código; artículos 5°, 6°, 7° y 12 de la Ley 1259 de 2008; Ley 746 de julio 19 de 2002; artículo 24, 29 e inciso final del artículo 31 de la Ley 1335 de 2009; y los artículos 12 y 13 de la Ley 140 de 1994.\r\nEste Código no modifica ni deroga ninguna norma del Código Penal.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 285, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (582, 'Vigencia', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La presente ley regirá seis (6) meses después de su promulgación.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 286, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (394, 'Principio de favorabilidad', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las infracciones urbanísticas que no hayan originado actos administrativos en firme, a la fecha de expedición de este Código, se decidirán con base en estas normas, en cuanto sean más favorables para el infractor.\r\nLas multas se tasarán en salarios mínimos legales mensuales o diarios vigentes, a la fecha de ocurrencia de los hechos que motivaron la imposición de la misma.\r\nEn cualquiera de los eventos de infracción urbanística, si el presunto infractor probare el restablecimiento del orden urbanístico, antes de que la declaratoria de infractor quede en firme, no habrá lugar a la imposición de multas.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 179, 135, '29/11/17');
