INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (261, 'Informe de registro en Cámaras de Comercio', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las Cámaras de Comercio permitirán el acceso permanente en tiempo real a la administración municipal o distrital correspondiente y a la Policía Nacional a las matrículas mercantiles registradas o modificadas.\r\nCorresponde a la administración municipal o distrital verificar que las actividades económicas estén autorizadas por la reglamentación de las normas de uso del suelo y las que la desarrollen o complementen, de la respectiva jurisdicción.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 126, 120, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (263, 'Control de actividades que trascienden a lo público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las personas jurídicas con o sin ánimo de lucro establecidas o que funcionen bajo la denominación de clubes sociales sin ánimo de lucro cuya actividad pueda afectar la convivencia y el orden público, casas culturales, centros sociales privados o clubes privados o similares, que ofrezcan servicios o actividades de recreación, diversión, expendio o consumo de licor, sala de baile, discoteca, grill, bar, taberna, whiskería, cantina, rockola, karaoke, sala de masajes o cualquier tipo de espectáculo para sus asociados o para el público en general, estarán sujetos a las normas del presente Código.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 127, 120, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (374, 'Control de caninos potencialmente peligrosos en zonas comunales', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En los conjuntos cerrados, urbanizaciones y edificios con régimen de propiedad horizontal, podrá prohibirse la permanencia de ejemplares caninos potencialmente peligrosos, a solicitud de cualquiera de los copropietarios o residentes y por decisión calificada de tres cuartas partes de las asambleas o de las juntas directivas de la copropiedad.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 171, 134, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (375, 'Albergues para caninos potencialmente peligrosos', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las instalaciones de albergues para los ejemplares de razas potencialmente peligrosas, deben tener las siguientes ca racterísticas: las paredes y vallas ser suficientemente altas y consistentes y estar fijadas a fin de soportar el peso y la presión del animal; puertas de las instalaciones resistentes y efectivas como el resto del contorno y con un diseño que evite que los animales puedan desencajar o abrir ellos mismos los mecanismos de seguridad. El recinto estará convenientemente señalizado con la advertencia que hay un perro peligroso en el lugar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 172, 134, '29/11/17');
