INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (355, 'Tenencia de animales domésticos o mascotas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Solo podrán tenerse como mascotas los animales así autorizados por la normatividad vigente. Para estos animales el ingreso o permanencia en cualquier lugar, se sujetará a la reglamentación de los lugares públicos, abiertos al público o edificaciones públicas.\r\nEn las zonas comunes de propiedades horizontales o conjuntos residenciales, los ejemplares caninos deberán ir sujetos por medio de traílla y, en el caso de los caninos potencialmente peligrosos, además irán provistos de bozal y el correspondiente permiso, de conformidad con la ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 158, 132, '29/11/17');
