INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (358, 'Caninos y felinos domésticos o mascotas en el espacio público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En el espacio público, en las vías públicas, en los lugares abiertos al público, y en el transporte público en el que sea permitida su estancia, todos los ejemplares caninos deberán ser sujetos por su correspondiente traílla y con bozal debidamente ajustado en los casos señalados en la presente ley para los ejemplares caninos potencialmente peligrosos y los felinos en maletines o con collares especiales para su transporte.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 159, 132, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (359, 'Albergues para animales domésticos o mascotas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En todos los distritos o municipios se establecerá un lugar seguro, sea este un centro de bienestar animal, coso municipal u hogar de paso público o privado, a donde se llevarán los animales domésticos o mascotas que penetren predios ajenos o vague por sitios públicos y se desconozca quién es el propietario o tenedor del mismo, y que por su condición física o situación de riesgo ameriten la atención o su custodia temporal. Si transcurridos treinta (30) días calendario, el animal no ha sido reclamado por su propietario o tenedor, las autoridades lo declararán en estado de abandono y procederán a promover su adopción o, como última medida, su entrega a cualquier título.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 160, 132, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (361, 'Información', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es deber de la Alcaldía Distrital o Municipal establecer un mecanismo para informar de manera suficiente a la ciudadanía el lugar a donde se llevan los animales que sean sorprendidos en predios ajenos o vagando en el espacio público y establecer un sistema donde se pueda solicitar información y buscar los animales en caso de extravío. La administración distrital o municipal podrá establecer una tarifa diaria de público conocimiento, correspondiente al costo del cuidado y alimentación temporal del animal. En las ciudades capitales y en municipios con población mayor a cien mil habitantes deberá además establecerse un vínculo o un sitio en la página web de la Alcaldía en donde se registre la fotografía de cada animal encontrado para facilitar su búsqueda. La entrega de dichos animales s erá reglamentada por la Administración Municipal correspondiente.\r\nLa información publicada en la página web cumplirá con el estándar dispuesto por el Ministerio de Tecnología de la Información y las Comunicaciones, de datos abiertos y de lenguaje para el intercambio de la misma.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 162, 132, '29/11/17');
