INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (118, 'Poder extraordinario para prevención del riesgo o ante situaciones de emergencia, seguridad y calamidad.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los gobernadores y los alcaldes, podrán disponer acciones transitorias de Policía, ante situaciones extraordinarias que puedan amenazar o afectar gravemente a la población, con el propósito de prevenir las consecuencias negativas ante la materialización de un evento amenazante o mitigar los efectos adversos ante la ocurrencia de desastres, epidemias, calamidades, o situaciones de seguridad o medio ambiente; así mismo, para disminuir el impacto de sus posibles consecuencias, de conformidad con las leyes que regulan la materia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 54, 102, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (120, 'Transitoriedad e informe de la gestión', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las acciones transitorias de Policía señaladas en el artículo anterior, sólo regirán mientras dure la situación de desastre o emergencia. La autoridad que las ejerza dará cuenta inmediata de las medidas que hubiere adoptado a la Asamblea Departamental y/o al Concejo Distrital o Municipal, en sus inmediatas sesiones, según corresponda. En el caso en que se considere necesario darle carácter permanente a las acciones transitorias de Policía dictadas de conformidad con lo establecido en el artículo anterior, los gobernadores y los alcaldes, presentarán ante la Asamblea Departamental o el Concejo Distrital o Municipal, según corresponda, el respectivo proyecto, que será tramitado de la manera más expedita de conformidad con el reglamento interno de cada corporación.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 55, 102, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (122, 'Competencia para expedir reglamentos', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En el ámbito nacional corresponde al Presidente de la República reglamentar las leyes sobre materias de Policía. Cuando las disposiciones de las asambleas o los concejos en asuntos de Policía, requieran reglamentación para aplicarlas, los gobernadores o los alcaldes podrán, según el caso, dictar reglamentos sólo con ese fin. Las autoridades que expiden reglamentos no podrán regular comportamientos, imponer medidas correctivas o crear procedimientos distintos a los establecidos en la norma reglamentada, salvo que esta les otorgue dicha competencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 57, 103, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (123, 'Coordinación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La coordinación entre las autoridades de Policía debe ser permanente, adecuada, eficiente, eficaz y oportuna, con el fin de asegurar las condiciones necesarias para la convivencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 58, 103, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (124, 'Consejos de Seguridad y Convivencia y Comité Civil de Convivencia', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los Consejos de Seguridad y Convivencia son cuerpos consultivos y de decisión para la prevención y reacción ante los problemas relacionados con la seguridad y la convivencia en el nivel nacional, regional, departamental, distrital, municipal o metropolitano. El Gobierno nacional establecerá mediante reglamentación los objetivos, funciones, integrantes y demás aspectos relacionados con el funcionamiento de los Consejos de Seguridad y Convivencia. De manera subsidiaria, las autoridades ejecutivas de los niveles distrital, dep artamental y municipal, considerando su especificidad y necesidad, podrán complementar la regulación hecha por el Gobierno nacional. Créanse los Comités Civiles de Convivencia en cada municipio o distrito, cuyo objeto será analizar hechos y fenómenos que afectan la convivencia así como tramitar las quejas, denuncias, peticiones o reconocimientos reportados en relación con la función y la actividad de Policía en su respectiva jurisdicción priorizando los casos relacionados con actuaciones donde hubieren podido verse afectados los intereses colectivos. Estos Comités podrán emitir recomendaciones para mejorar la función y la actividad de Policía y garantizar la transparencia en el ejercicio de sus funciones. Así mismo, deberán fomentar e incentivar que la ciudadanía presente las denuncias y quejas que correspondan y promoverá campañas de información sobre los derechos, deberes y garantías de los ciudadanos ante las actividades de Policía. Este Comité estará conformado por el Alcalde, el Personero Municipal y el Comandante de Estación del respectivo distrito, municipio o localidad. Estos Comités deberán reunirse, al menos, una vez al mes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 59, 103, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (126, 'Carácter público de las actividades de Policía', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Todo procedimiento policivo podrá ser grabado mediante cualquier medio de las tecnologías de información y comunicación, por lo que le está prohibido a cualquier persona, salvo las restricciones expresas de ley, impedir que sean realizadas dichas grabaciones. La autoridad de Policía que impida la grabación de que trata este artículo sin la justificación legal correspondiente incurrirá en causal de mala conducta.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 61, 103, '29/11/17');
