INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (179, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Para establecer los alcances y resultados del modelo de atención integral, las entidades territoriales serán autónomas en definir los servicios integrales requeridos de acuerdo a los lineamientos que dicte el Ministerio de Salud y teniendo en cuenta la caracterización poblacional de cada municipio. Los entes territoriales deberán definir los equipos interdisciplinarios necesarios y pertinentes, que faciliten en el tiempo y de manera integral la intervención oportuna para el restablecimiento de los derechos de los habitantes de y en calle.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 113, '29/11/17');
