INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (199, 'Colaboración en actividades que involucran aglomeraciones de público complejas y no complejas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La Policía Nacional podrá intervenir para garantizar que los asistentes ingresen con boleta, contraseña o invitación, al lugar donde se celebre un espectáculo o actividad que involucre aglomeraciones de público que así lo requiera y para que el público respete las indicaciones de porteros, acomodadores y personal de logística o apoyo. Asimismo, impedirá el cobro de derechos de entrada distintos a lo legal o reglamentariamente autorizados, según el caso.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 93, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (200, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'De manera excepcional la Policía Nacional podrá prestar servicios de vigilancia y seguridad dentro y fuera de actividades que involucren aglomeraciones de público complejas, cuando existan razones de fuerza mayor, seguridad u orden público que lo aconsejen, y no descuiden o distraigan esfuerzos relacionados con la seguridad y convivencia del municipio o departamento.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (202, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las actividades que involucren aglomeraciones de público complejas y no complejas, convocadas por las entidades públicas, contarán con la presencia de la Policía Nacional, siempre y cuando las situaciones lo exijan, para garantizar la seguridad y el orden público.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (203, 'Ejercicio del derecho de reunión y manifestación pública y pacífica en el espacio público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Toda persona puede reunirse y manifestarse en sitio público con el fin de exponer ideas e intereses colectivos de carácter cultural, político, económico, religioso, social o de cualquier otro fin legítimo.\r\nCon tales fines debe darse aviso por escrito presentado ante la primera autoridad administrativa del lugar o mediante correo electrónico. Tal comunicación o correo debe ser suscrito por lo menos por tres personas.\r\nTal aviso deberá expresar día, hora y sitio de la proyectada reunión y se presentará con 48 horas de anticipación indicando el recorrido prospectado.\r\nToda reunión y manifestación que cause alteraciones a la convivencia podrá ser disuelta.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 94, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (204, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las reuniones y manifestaciones espontáneas de una parte de la población no se considerarán por sí mismas como alteraciones a la convivencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (205, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El que irrespete las manifestaciones y reuniones de las personas en el espacio público, en razón a su etnia, raza, edad, género, orientación sexual, creencias religiosas, preferencias políticas y apariencia personal, será objeto de aplicación de medida correctiva correspondiente a Multa General Tipo 4.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (206, 'Uso de vías para el ejercicio del derecho de reunión o manifestación pública y pacífica en el espacio público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los alcaldes distritales o municipales, salvo circunstancias excepcionales o de fuerza mayor, deberán autorizar el uso temporal de vías dentro de su jurisdicción para actos o eventos de ejercicio del derecho de reunión o manifestación pública y pacífica en el espacio público. En el caso de las vías arterias principales o corredores de transporte público colectivo deberán establecer un plan efectivo de desvíos para la movilización de los ciudadanos que no participan del acto o evento, como medida de protección de los derechos de los demás ciudadanos.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 95, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (208, 'Actuación de la Fuerza Pública en las movilizaciones terrestres', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'De conformidad con los estándares internacionales, es función de la Policía garantizar los derechos de toda la ciudadanía que interviene directa o indirectamente en el ejercicio de la movilización. El uso de la fuerza debe ser considerado siempre el último recurso en la int ervención de las movilizaciones.\r\nLa actuación de la Policía Nacional deberá ser desarrollada en todo momento mediante personal y equipos identificados de tal manera que dicha identificación resulte visible sin dificultades. La fuerza disponible deberá estar ubicada de manera que su actuación pueda hacerse de forma oportuna, pero sin afectar el desarrollo de la movilización que se haga de conformidad con las normas de convivencia.\r\nLos cuerpos de Policía intervendrán sólo cuando se considere que su actuación es necesaria, atendiendo al principio de proporcionalidad y a la garantía de los derechos de los manifestantes y de los demás habitantes que puedan verse afectados por su actuación. Los escuadrones móviles antimotines sólo serán enviados cuando no sea posible por otro medio controlar graves e inminentes amenazas a los derechos.\r\nLas Fuerzas Militares no podrán intervenir en el desarrollo de operativos de control, contención o garantía de la realización de las movilizaciones sociales terrestres, salvo los casos en los que excepcionalmente los autoriza la Constitución y la ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 97, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (209, 'Acompañamiento a las movilizaciones', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los alcaldes distritales o municipales con el apoyo de funcionarios de los entes de control encargados de velar por la protección de los Derechos Humanos, acompañarán el ejercicio del derecho a la movilización pacífica. Cuando se presenten amenazas graves e inminentes a otros derechos, los alcaldes podrán intervenir, por medio de gestores de convivencia de naturaleza civil, para garantizar el goce efectivo de los derechos de la ciudadanía durante el desarrollo de la movilización.\r\nCuando se haya agotado la intervención de los gestores de convivencia y persistan graves amenazas pa ra los derechos a la vida y la integridad, la Policía Nacional podrá intervenir.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 98, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (211, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La seguridad interna y externa en actividades que involucran aglomeraciones de público no complejas será responsabilidad de los organizadores, quienes en caso de ser necesario deberán contratarla con empresas de vigilancia y seguridad privada y/o empresas de logística legalmente constituidas.\r\nLos organizadores o las empresas de seguridad y vigilancia privada y/o empresas de logística podrán designar de manera específica a los encar gados de informar de manera inmediata a las autoridades de Policía sobre aquellas personas que estén contrariando la ley y las normas de convivencia dentro de las actividades que involucran aglomeraciones de público no complejas.\r\nEn casos excepcionales de riesgo grave a la convivencia y a la seguridad ciudadana, la Policía Nacional podrá, sin descuidar sus responsabilidades frente al resto de la población, complementar la seguridad privada en las actividades que involucran aglomeraciones de público no complejas.\r\nLa Policía Nacional podrá, sin embargo, ingresar a las actividades que involucran aglomeraciones de público no complejas, en el cumplimiento de sus funciones.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 117, '29/11/17');
