INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (215, 'Definición de las actividades que involucran aglomeraciones de público complejas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', ' Las actividades que involucran aglomeraciones de público complejas son aquellas que de acuerdo con variables tales como: aforo, tipo de evento, clasificación de edad para el ingreso, lugar donde se desarrolla, infraestructura a utilizar, entorno del lugar, características del público, frecuencia, características de la presentación, carácter de la reunión, den lugar a riesgos de afectación a la comunidad o a los bienes, generando una alta afectación de la dinámica normal del municipio, distrito o del área específica en que se realizan, y que por ello requieren condiciones especiales para su desarrollo, determinadas por el Gobierno nacional de conformidad con el parágrafo del artículo 46 del presente Código.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 101, 118, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (216, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Toda actividad que involucra la aglomeración de público compleja exige la emisión de un permiso, por parte del alcalde o su delegado. El permiso para el desarrollo de actividades que involucran aglomeraciones de público complejas en escenarios habilitados o no habilitados se concederá previo cumplimiento de los requisitos y las normas vigentes para cada tipo de escenario.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 118, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (218, 'Actividades que involucran aglomeraciones de público complejas en establecimientos que desarrollen actividades económicas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El alcalde distrital o municipal reglamentará las actividades que involucran aglomeraciones de público complejas en establecimientos abiertos al público.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 102, 118, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (220, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El Gobierno nacional reglamentará las condiciones especiales de operación de las empresas de vigilancia y seguridad privada y/o empresas de logística, que pretendan prestar el servicio de vigilancia en las actividades que involucran aglomeraciones de público complejas.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 118, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (222, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', ' Para la realización de los eventos que involucren aglomeraciones de público complejas de que trata el presente artículo, el organizador o promotor del mismo deberá solicitar por escrito la autorización al alcalde distrital o municipal del lugar o su delegado. En la solicitud se deben registrar y acreditar los requisitos establecidos en la presente ley, así como también especificar las condiciones de hora, sitio, duración y tipo de evento que se presentará, a fin de adoptar las medidas policiales necesarias para garantizar la convivencia dentro del espectáculo. Esta solicitud se tendrá que presentar con una anticipación no inferior a quince (15) días.\r\nDentro de los cinco (5) días siguientes a la fecha de radicación de la solicitud, el alcalde distrital o municipal mediante resolución motivada, podrá conceder la autorización para realizar el evento, modificar sus condiciones o desaprobarlo. Contra este acto solo procede el recurso de reposición.\r\nEl alcalde o su delegado deberán dar aviso al comandante de Policía respectivo, con cuarenta y ocho (48) horas de anticipación a la realización del evento.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 118, '29/11/17');
