INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (524, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Cada alcaldía tendrá el número de inspectores de Policía que el Alcalde considere necesario, para una rápida y cumpli da prestación de la función de Policía en el municipio.\r\nHabrá inspecciones de Policía permanentes durante veinticuatro (24) horas en las ciudades capitales de departamento, en los distritos, y en los municipios que tengan una población superior a los cien mil habitantes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 142, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (526, 'Las autoridades administrativas especiales de Policía', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las autoridades administrativas en salud, seguridad, ambiente, mineras, de ordenamiento territorial, planeación, vivienda y espacio público y las demás que determinen la ley, las ordenanzas y los acuerdos, conocerán del recurso de apelación de las decisiones proferidas por los inspectores o corregidores de Policía, según la materia.\r\nEn los municipios donde no existan estas autoridades, conocerá del recurso de apelación el alcalde municipal.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 249, 142, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (254, 'El derecho a la protección del domicilio', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Quien se encuentre domiciliado en un inmueble y considere que su derecho ha sido perturbado o alterado ilegalmente, podrá acudir al inspector de Policía, para iniciar querella mediante el ejercicio de la acción de protección, por el procedimiento señalado en este Código.\r\nLa protección del domicilio es una medida de efecto inmediato, cuya única finalidad es mantener el statu quo, mientras el juez ordinario competente decide definitivamente sobre los derechos en controversia y las indemnizaciones correspondientes si a ellas hubiera lugar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 123, 119, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (258, 'Perímetro de impacto de la actividad económica', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'A partir de la expedición del presente Código, alrededor de hospitales, hospicios, centros de salud, centros que ofrezcan el servicio educativo en los niveles de preescolar, básica, media, superior o de educación para el trabajo y desarrollo humano, o centros religiosos, no podrán desarrollarse actividades económicas relacionadas con el ejercicio de la prostitución, juegos de suerte y azar localizados, concursos, o donde se ejecute, por cualquier medio, música o ruidos que afecten la tranquilidad.\r\nCorresponderá a los Concejos Distritales o Municipales a iniciativa de los Alcaldes establecer el perímetro para el ejercicio de las actividades mencionadas en el presente artículo, dentro del año siguiente a la publicación de la presente ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 125, 120, '29/11/17');
