INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (545, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las medidas correctivas por los comportamientos contrarios a la integridad urbanística, o a la organización de eventos que involucren aglomeraciones de público, no se impondrán en orden de comparendo. El personal uniformado de la Policía Nacional pondrá en conocimiento de la autoridad competente, los comportamientos mencionados mediante informe escrito.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 143, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (547, 'Carga de la prueba en materia ambiental y de salud pública', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En los procedimientos que se adelanten por comportamientos que afecten el ambiente, el patrimonio ecológico y la salud pública, se presume la culpa o el dolo del infractor a quién le corresponde probar que no está incurso en el comportamiento contrario a la convivencia correspondiente.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 262, 143, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (550, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En contra de la orden de Policía o la medida correctiva, procederá el recurso de apelación, el cual se concederá en el efecto devolutivo y se remitirá al Inspector de Policía dentro de las veinticuatro (24) horas siguientes. El recurso de apelación se resolverá dentro de los tres (3) días hábiles siguientes al recibo de la actuación y será notificado por medio más eficaz y expedito.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 144, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (552, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Para la imposición de las medidas correctivas de suspensión temporal de actividad, inutilización de bienes, destrucción de bien y disolución de reunión o actividad que involucra aglomeraciones de público no complejas, se deberá levantar acta en la que se documente el procedimiento señalado en el presente artículo, la cual debe estar suscrita por quien impone la medida y el infractor.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 144, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (555, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Casos en que se requiere inspección al lugar. Cuando la autoridad de Policía inicia la actuación y decreta inspección al lugar, fijará fecha y hora para la práctica de la audiencia, y notificará al presunto infractor o perturbador de convivencia y al quejoso personalmente, y de no ser posible, mediante aviso que se fijará en la puerta de acceso del lugar de los hechos o parte visible de este, con antelación no menor a veinticuatro (24) horas, de la fecha y hora de la diligencia.\r\nPara la práctica de la diligencia de inspección, la autoridad de Policía se trasladará al lugar de los hechos, con un servidor público técnico especializado cuando ello fuere necesario y los hechos no sean notorios y evidentes; durante la diligencia oirá a las partes máximo por quince (15) minutos cada una y recibirá y practicará las pruebas que considere conducentes para el esclarecimiento de los hechos.\r\nEl informe técnico especializado se rendirá dentro de la diligencia de inspección ocular. Excepcionalmente y a juicio del inspector de Policía, podrá suspenderse la diligencia hasta por un término no mayor de tres (3) días con el objeto de que el servidor público rinda el informe técnico.\r\nLa autoridad de Policía proferirá la decisión dentro de la misma diligencia de inspección, o si ella hubiere sido suspendida, a la terminación del plazo de suspensión.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 145, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (562, 'Falta disciplinaria de la autoridad de Policía', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La autoridad de Policía que incumpla los términos señalados en este capítulo o que incurra en omisión y permita la caducidad de la acción o de las medidas correctivas, incurrirá en falta disciplinaria grave.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 270, 145, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (564, 'Impedimentos y recusaciones', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las autoridades de Policía podrán declararse impedidas o ser recusadas por las causales establecidas en las disposiciones del Código de Procedimiento Administrativo y de lo Contencioso Administrativo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 272, 145, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (566, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En el caso de los Alcaldes Distritales, Municipales o Locales, resolverá el impedimento o recusación, el personero municipal o distrital en el término de dos (2) días. Cuando se declare el impedimento o recusación, conocerá del asunto, el alcalde de la jurisdicción más cercana.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 145, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (568, 'Mecanismos alternativos de solución de desacuerdos y conflictos de convivencia', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los desacuerdos y los conflictos relacionados con la convivencia pueden ser objeto de conciliación y de mediación, sólo en relación con derechos renunciables y transigibles y cuando no se trate de situaciones de violencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 274, 146, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (570, 'Mediación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La mediación permite que el mediador escuche a las personas que se encuentran en situación de conflicto de convivencia y facilite un camino para encontrar una solución equitativa. De realizarse el acuerdo, se suscribirá el acta de mediación, donde se consignarán las obligaciones a cargo de cada uno de los interesados, la cual hará tránsito a cosa juzgada y prestará mérito ejecutivo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 276, 146, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (573, 'Sistema único para el mejoramiento y prevención de los abusos en la actividad de Policía', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La Policía Nacional, la Procuraduría General de la Nación y el Ministerio de Tecnologías de la Información y las Comunicaciones, dentro del año siguiente a la promulgación del presente Código, establecerá un sistema electrónico único de quejas, sugerencias y reclamos de cobertura nacional que garantice un acceso fácil y oportuno a la ciudadanía.\r\nEl sistema electrónico único deberá reportar en tiempo real las actividades que realicen las autoridades de Policía y el resultado de las mismas en materia de seguridad y convivencia ciudadana, siempre y cuando no se afecten operaciones policiales en desarrollo ni se contravenga la ley.\r\nEl Gobierno nacional reglamentará la implementación del sistema establecido en este artículo para que el mismo pueda arrojar resultados estadísticos sobre la actividad de Policía.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 278, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (577, 'Reglamentación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El presente Código rige en todo el territorio nacional y se complementa con los reglamentos de Policía expedidos por las autoridades competentes, de conformidad con la Constitución Política y la ley. Las disposiciones de la presente ley, prevalecen sobre cualquier reglamento de Policía.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 281, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (580, 'Comisión de seguimiento', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'A partir de la vigencia de la presente ley, las mesas directivas de las Comisiones Primeras de Senado y Cámara conformarán una Comisión en la que tendrán asiento todos los partidos y movimientos políticos representados en las respectivas Comisiones, encargada de efectuar el seguimiento de la aplicación de esta ley, recibir las quejas que se susciten en ocasión de la misma y revisar los informes que se le soliciten al Gobierno nacional.\r\nEl Gobierno deberá presentar informes dentro de los primeros diez (10) días de cada periodo legislativo a las comisiones de que trata este artículo, referidos a la utilización de las atribuciones que se le confieren mediante la presente ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 284, 147, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (229, 'Planes de emergencia y contingencia por temporadas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Cuando los organizadores de las reuniones previstas en el presente título las realicen a lo largo del año, o por temporadas, cuyas variables generadoras de riesgo sean las mismas, anualmente o por temporada, se adoptará un solo plan de emergencia y contingencia, sin perjuicio de la obligación de informarlo. En todo caso, dicho plan de contingencia nunca podrá exceder la vigencia del permiso otorgado para la realización de la actividad que involucre aglomeraciones de público complejas. En el caso de realizarse dicha aglomeración de público compleja en escenarios habilitados de conformidad con la ley y las normas vigentes, el plan de emergencia y contingencia se establecerá por el mismo tiempo que contemple el permiso otorgado a estos escenarios.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 109, 118, '29/11/17');
