INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (249, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La Superintendencia de Notariado y Registro, el Instituto Agustín Codazzi y las administraciones municipales, deberán suministrar la información solicitada, de manera inmediata y gratuita a las autoridades de Policía.\r\nEl recurso de apelación se concederá en efecto devolutivo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 119, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (251, 'Carácter, efecto y caducidad del amparo a la posesión, mera tenencia y servidumbre', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El amparo de la posesión, la mera tenencia y las servidumbres, es una medida de carácter precario y provisional, de efecto inmediato, cuya única finalidad, es mantener el statu quo mientras el juez ordinario competente decide definitivamente sobre la titularidad de los derechos reales en controversia y las indemnizaciones correspondientes, si a ellas hubiere lugar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 121, 119, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (431, 'Traslado para procedimiento policivo', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Como regla general, las medidas correctivas se aplicarán por la autoridad de Policía en el sitio en el que se sucede el motivo.\r\nLas autoridades de Policía solo podrán realizar un traslado inmediato y temporal de la persona cuando sea necesario para realizar el proceso verbal inmediato, y no sea posible realizarlo en el sitio por razones no atribuibles a la autoridad de Policía.\r\nEl procedimiento se realizará inmediatamente y en ningún caso el tiempo de traslado o permanencia en el sitio al que es trasladada la persona podrá exceder de seis (6) horas, de conformidad con las exigencias de las distancias.\r\nLa autoridad de Policía permitirá a la persona que va a ser trasladada comunicarse con un allegado o a quien pueda asistirlo para informarle el motivo y sitio de traslado. Si la persona no tiene los medios para comunicarse, la autoridad se los facilitará.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 199, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (436, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El registro de personas y sus bienes podrá incluir el contacto físico de acuerdo a los protocolos que para tal fin establezca la Policía Nacional. El registro deberá ser realizado por persona del mismo sexo. Si la persona se resiste al registro o al contacto físico, podrá ser conducido a una unidad de Po licía, donde se le realizará el registro, aunque oponga resistencia, cumpliendo las disposiciones señaladas para la conducción.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (441, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Si en el desarrollo del registro se encuentran elementos que justifiquen el inicio de una acción penal, el personal uniformado de la Policía Nacional deberá iniciar los procedimientos establecidos en el Código de Procedimiento Penal.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (443, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En las aguas jurisdiccionales colombianas la actividad de Policía será ejercida por el cuerpo de guardacostas de la Armada Nacional; excepcionalmente podrá hacerlo la Policía Nacional, previa coordinación con la Armada Nacional. En la interfase buque-puerto ejercerán concurrentemente las diferentes autoridades de acuerdo a sus competencias.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 292, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (446, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La orden de ingreso a inmueble deberá ser escrita y motivada. Así mismo, deberá levantarse un acta en la que conste el procedimiento de Policía adelantado. El funcionario que autorizó el ingreso al inmueble deberá enviar de inmediato la orden de ingreso y el acta al Ministerio Público. Podrán utilizarse y enviarse otros medios de documentación del procedimiento.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (448, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Para la práctica de pruebas los gobernadores y alcaldes podrán disponer comisión para el ingreso al inmueble determinado.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 291, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (450, 'Ingreso a inmueble sin orden escrita', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La Policía podrá penetrar en los domicilios, sin mandamiento escrito, cuando fuere de imperiosa necesidad:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 205, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (451, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional que realice un ingreso a inmueble sin orden escrita, de inmediato rendirá informe escrito a su superior, con copia al propietario, poseedor o tenedor del inmueble, donde conste la razón por la cual se realizó el ingreso. Si el propietario, poseedor o tenedor considera que no había razón para el ingreso o que se hizo de manera inapropiada, podrá informar a las autoridades competentes. En todo caso, previo al ingreso al inmueble, las personas podrán exigir la plena identificación de la autoridad a fin de evitar la suplantación, verificación a realizar mediante mecanismos provistos o aceptados por la autoridad policial.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (460, 'Medios de apoyo', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal uniformado de la Policía Nacional podrá utilizar medios de apoyo de carácter técnico, tecnológico o de otra naturaleza, que estén a su alcance, para prevenir y superar comportamientos o hechos contrarios a la convivencia y la seguridad pública. De tratarse de medios de apoyo que puedan afectar físicamente a la persona, deberán ser usados bajo los criterios de necesidad, proporcionalidad y racionabilidad según las circunstancias específicas; su empleo se hará de manera temporal y sólo para controlar a la persona. Cuando el personal uniformado de la Policía haga uso de medios de apoyo deberá informarse por escrito al superior jerárquico.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 209, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (464, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En caso de emergencia, catástrofe o calamidad pública, la asistencia militar se regirá por los procedimientos y normas especializadas, bajo la coordinación de los comités de emergencia y oficinas responsables en la materia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 287, 140, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (470, 'Amonestación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es un llamado de atención en privado o en público con el objetivo de concientizar a la persona de la conducta realizada y de su efecto negativo para la convivencia, en procura de un reconocimiento de la conducta equivocada, el compromiso a futuro de no repetición y el respeto a las normas de convivencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 216, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (472, 'Participación en programa comunitario o actividad pedagógica de convivencia', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es la obligación de participar en una actividad de interés público o programa pedagógico en materia de convivencia, organizado por la administración distrita l o municipal, en todo caso tendrá una duración de hasta seis (6) horas.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 217, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (474, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El programa o actividad pedagógica de convivencia que se aplique como medida correctiva a niños, niñas o adolescentes, deberá contar con el enfoque adecuado para esta población de acuerdo con la legislación vigente.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (476, 'Disolución de reunión o actividad que involucra aglomeraciones de público no complejas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Es la orden de Policía que consiste en notificar o coercer a un grupo de personas con el objeto de terminar una reunión o actividad que involucra aglomeraciones de público no complejas que contraríen la ley.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 218, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (478, 'Prohibición de ingreso a actividad que involucra aglomeraciones de público complejas o no complejas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Consiste en impedir el ingreso a actividad que involucra aglomeraciones de público complejas o no complejas entre seis (6) meses y tres (3) años, para:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 220, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (480, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los bienes muebles utilizados en la comisión de los comportamientos contrarios a la convivencia contenidos en el presente Código, serán decomisados.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 289, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (482, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El Gobierno nacional, dentro del año siguiente a la expedición de la presente ley, definirá la entidad de orden nacional o territorial responsable del traslado, almacenamiento, preservación, depósito, cuidado, administración y destino definitivo de los bienes decomisados por las autoridades y la asignación de los recursos para tal fin, en razón al tipo de bien decomisado, a la especialidad de la entidad, y la destinación. Los bienes decomisados podrán ser donados o rematados de conformidad con la reglamentación. En el marco de esta facultad, el Gobierno nacional podrá considerar la tercerización, contratación y concesión de dichos servicios. Mientras se expida y toman las medidas para la implementación de esa ley, las entidades que a la fecha vienen adelantando esa labor, la continuarán realizando y se mantendrá la destinación actual de dichos bienes.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 288, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (487, 'Consecuencias por mora en el pago de multas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El no pago de la multa dentro del primer mes dará lugar al cobro de intereses equivalentes al interés moratorio tributario vigente. Así mismo se reportará el Registro Nacional de Medidas Correctivas, el cual será consultado por las entidades públicas, de conformidad con las normas vigentes.\r\nSi transcurridos noventa días desde la imposición de la multa sin que esta hubiera sido pagada se procederá al cobro coactivo, incluyendo sus intereses por mora y costos del cobro coactivo.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 224, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (490, 'Registro Nacional de Medidas Correctivas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La Policía Nacional llevará un registro nacional de medidas correctivas que incluirá la identificación de la persona, el tipo de comportamiento contrario a la convivencia, el tipo de medida correctiva y el estado de pago de la multa o cumplimiento de la medida correctiva.\r\nLa Registraduría Nacional del Estado Civil facilitará a las autoridades de Policía el acceso a sus bases de datos para la identificación e individualización de las personas vinculadas a procesos de Policía por comportamientos que afecten la convivencia.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 226, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (494, 'Construcción, cerramiento, reparación o mantenimiento de inmueble', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', ' Es la orden de Policía de mantener, reparar, construir, cerrar o reconstruir un inmueble en mal estado o que amenace ruina, con el fin de regresarlo a su estado original o para que no implique riesgo a sus moradores y transeúntes. Esta orden puede aplicarse a cualquier clase de inmueble. Se incluye en esta medida el mantenimiento y cerramiento de predios sin desarrollo o construcción.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 228, 141, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (191, 'Definición y clasificación de las aglomeraciones de público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Para efectos de las obligaciones relacionadas con el derecho de reunión, entiéndase como aglomeración de público toda reunión de un número plural de personas producto de una convocatoria individual o colectiva. En razón a sus características y requisitos, se establecen tres categorías:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 88, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (193, 'Reglamentación', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las autoridades municipales en concurso con los consejos municipales y distritales de gestión del riesgo, reglamentarán las condiciones y requisitos para la realización de actividades que involucran aglomeraciones de público complejas y no complejas de conformidad con lo expresado en este Código y con sujeción a la reglamentación que para el efecto expida el Gobierno nacional.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 89, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (196, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Los alcaldes podrán prohibir el consumo de bebidas embriagantes en aglomeraciones, cuando existan antecedentes de comportamientos que afectaron la convivencia en eventos similares realizados por los mismos organizadores.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (198, 'Daño y contaminación visual en el espacio público', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'En caso de daños al espacio público que ocurran con ocasión de actividades de aglomeraciones de público complejas y no complejas se impondrá multa especial por contaminación visual y las medidas correctivas de destrucción del bien y reparación de daños materiales de muebles e inmuebles de que trata el presente Código, a los empresarios del espectáculo público y coordinadores logísticos del evento.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 92, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (201, '', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El personal destinado por la organización deberá contar con la capacitación correspondiente en cuanto a la aplicación de protocolos de seguridad en aglomeraciones de público en concordancia con las amenazas identificadas en los eventos a adelantar.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 290, 115, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (207, 'Protección del ejercicio del derecho de reunión y manifestación pública frente a señalamientos infundados', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Con el fin de amparar el ejercicio del derecho a la reunión o movilización pacífica, queda prohibido divulgar mensajes engañosos en torno a quienes convocan o participan en las manifestaciones, así como hacer públicamente señalamientos falsos de la relación de los manifestantes con grupos armados al margen de la ley o deslegitimar por cualquier medio el ejercicio del derecho constitucional de reunión y manifestación pública y pacífica.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 96, 116, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (210, 'Definición de actividades que involucran aglomeraciones de público no complejas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las actividades que involucran aglomeraciones de público no complejas son aquellas que de acuerdo con variables tales como: aforo, tipo de evento, clasificación de edad para el ingreso, lugar donde se desarrolla, infraestructura a utilizar, entorno del lugar, características del público, frecuencia, características de la presentación, carácter de la reunión, den lugar a riesgos bajos o moderados de afectación a la comunidad o a los bienes y con baja probabilidad de ocurrencia, además de no generar afectación de la dinámica normal del municipio, distrito o del área específica en que se realizan y que por ello no requieren condiciones especiales para su desarrollo, determinadas por el Gobierno nacional de conformidad con el parágrafo del artículo 47 del presente Código.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 99, 117, '29/11/17');
