INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (319, 'Ingreso de maquinaria pesada', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Las autoridades aduaneras exigirán la instalación de dispositivos tecnológicos para la identificación y localización de la maquinaria pesada que ingrese o se importe al territorio colombiano. El Gobierno nacional a través del Ministerio de Transporte o de quien haga sus veces establecerá una central de monitoreo para estos efectos.\r\nEl Ministerio de Transporte, en coordinación con el Ministerio de Defensa Nacional y la Policía Nacional, establecerá los mecanismos de control y monitoreo, de conformidad con la reglamentación expedida por el Gobierno nacional.\r\nLa maquinaria que no cumpla con el requisito antes mencionado no podrá ingresar al territorio aduanero nacional.\r\nEn caso de que la maquinaria no cuente con el dispositivo o este no funcione, será inmovilizada hasta que su propietario o tenedor demuestre el efectivo funcionamiento del dispositivo electrónico. En todo caso será objeto de multa equivalente al 10% del valor comercial de la maquinaria. En caso de reiteración la maquinaria será decomisada.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 145, 127, '29/11/17');
