INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (129, 'Contenido', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'El presente libro establece los comportamientos contrarios a la convivencia que no deben ser realizados por las personas que habitan o visitan el territorio nacional. El cumplimiento de los comportamientos favorables a la convivencia y el rechazo de los que le son contrarios, serán promovidos por las entidades estatales y organizaciones de la sociedad civil, y en particular por las autoridades de Policía, quienes exaltarán los primeros y ejercerán un control sobre los segundos.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 65, 148, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (221, 'Requisitos para la programación de actividades que involucran aglomeraciones de público complejas en escenarios habilitados y no habilitados', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'Para la realización de cualquier actividad que involucre aglomeraciones de público complejas, ya sea público o privado, se tendrán en cuenta las siguientes condiciones, que deberán cumplir los escenarios habilitados cada vez que renueven su permiso y los no habilitados para cada actividad con aglomeración de público compleja:', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 104, 118, '29/11/17');
INSERT INTO ARTICULO (ID, TITULO_ESP, TITULO_ENG, ARTICULO_ESP, ARTICULO_ENG, VIGENTE, NIVEL_ID, CAPITULO_ID, FECHA) VALUES (219, 'Participación de la seguridad privada en las actividades que involucran aglomeraciones de público complejas', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 'La seguridad interna y externa en las actividades que involucran aglomeraciones de público complejas será responsabilidad de los organizadores, quienes deberán contratarla con empresas de vigilancia y seguridad privada y/o empresa de logística legalmente constituidas. El servicio de seguridad será prestado desde el montaje o preparación de la actividad hasta su reacondicionamiento.\r\nLas empresas de seguridad y vigilancia privada y/o empresas de logística podrán designar de manera específica a miembros de la empresa para que informen de manera inmediata a las autoridades de Policía sobre aquellas personas que estén contrariando la ley y las normas de convivencia en las actividades que involucran aglomeraciones de público complejas.\r\nEn casos excepcionales de riesgo grave a la convivencia y a la seguridad ciudadana, la Policía Nacional podrá, sin descuidar sus responsabilidades frente al resto de la población, complementar la seguridad privada en las actividades que involucran aglomeraciones de público complejas.\r\nLa Policía Nacional podrá, sin embargo, por iniciativa propia, ingresar en todo momento y bajo cualquier circunstancia a las actividades que involucran aglomeraciones de público complejas, en el cumplimiento de sus funciones.', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', 1, 103, 118, '29/11/17');
