INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1276, 'Portar armas, elementos cortantes, punzantes o semejantes, o sustancias peligrosas, en áreas comunes o lugares abiertos al público. Se exceptúa a quien demuestre que tales elementos o sustancias constituyen una herramienta de su actividad deportiva, oficio, profesión o estudio.', 'Carrying weapons, sharp, sharp or similar elements, or dangerous substances, in common areas or places open to the public. Exceptions which demonstrate that such elements or substances constitute a tool of their sports activity, trade, profession or study.', 1, 302, 134, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1277, 'Portar armas neumáticas, de aire, de fogueo, de letalidad reducida o sprays, rociadores, aspersores o aerosoles de pimienta o cualquier elemento que se asimile a armas de fuego, en lugares abiertos al público donde se desarrollen aglomeraciones de personas o en aquellos donde se consuman bebidas embriagantes, o se advierta su utilización irregular, o se incurra en un comportamiento contrario a la convivencia.', 'Carry pneumatic, air, blanks, reduced lethality weapons or sprays, sprinklers, sprinklers or sprays of pepper or any element that is similar to firearms, in places open to the public where there are agglomerations of people or where consume intoxicating drinks, or notice their irregular use, or incur a behavior against cohabitation', 1, 303, 134, '29/11/17');
