INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1280, 'Arrojar en las redes de alcantarillado, acueducto y de aguas lluvias, cualquier objeto, sustancia, residuo, escombros, lodo, combustibles o lubricantes, que alteren u obstruyan el normal funcionamiento.', 'Throw any object, substance, waste, debris, sludge, fuel or lubricants into the sewerage, aqueduct and rainwater system that alter or obstruct normal operation.', 1, 299, 137, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1281, 'No reparar oportunamente los daños ocasionados a la infraestructura de servicios públicos domiciliarios, cuando estas reparaciones corresponden al usuario.', 'Not repair the damage caused to the infrastructure of public utilities in due course, when these repairs correspond to the user.', 1, 300, 137, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1282, 'Fabricar, tener, portar, almacenar, distribuir, transportar, comercializar, manipular o usar artículos pirotécnicos, fuegos artificiales, pólvora o globos sin el cumplimiento de los requisitos establecidos en la normatividad vigente.', 'Manufacture, have, carry, store, distribute, transport, market, manipulate or use pyrotechnic articles, fireworks, gunpowder or balloons without complying with the requirements established in the current regulations.', 1, 297, 141, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1283, 'Salvo actos circenses, prender o manipular fuego en el espacio público, lugar abierto al público, sin contar con la autorización del alcalde o su delegado o del responsable del sitio, sin cumplir las medidas de seguridad.', 'Except circus acts, ignite or manipulate fire in public space, place open to the public, without the authorization of the mayor or its delegate or the person in charge of the place, without complying with security measures', 1, 298, 141, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1284, 'Prender o manipular fuego, sustancias combustibles o mercancías peligrosas en medio de transporte público.', 'Setting fire or manipulating fire, combustible substances or dangerous merchandise in the middle of public transport.', 1, 299, 141, '29/11/17');
