INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1294, 'Consumir bebidas alcohólicas, sustancias psicoactivas en el espacio público o lugares abiertos al público ubicados dentro del área circundante a la institución o centro educativo de conformidad con el perímetro establecido en el artículo 83 de la presente ley.', 'Consume alcoholic beverages, psychoactive substances in public space or places open to the public located within the area surrounding the institution or educational center in accordance with the perimeter established in the article 83 of this law.', 1, 299, 151, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1295, 'Tener, almacenar, facilitar, distribuir, o expender bebidas alcohólicas, sustancias prohibidas en el espacio público o lugares abiertos al público dentro del perímetro circundante de conformidad con lo establecido en el artículo 84 de la presente ley.', 'Have, store, facilitate, distribute, or sell alcoholic beverages, substances prohibited in public space or places open to the public within the surrounding perimeter in accordance with the provisions of Article 84 of this law.', 1, 300, 151, '29/11/17');
