INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1303, 'Utilizar inadecuadamente el sistema de número único de seguridad y emergencia.', 'Inappropriate use of the security and emergency number system.', 1, 303, 154, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1304, '1. Permitir, auspiciar, tolerar, inducir o constreñir el ingreso de los niños, niñas y adolescentes a los lugares donde: \n a) Se realicen espectáculos o actividades cinematográficas aptas solo para mayores de 18 años; \n b) Se preste el servicio de videojuegos salvo que sean aptos para la edad del niño, niña o adolescente, en las condiciones establecidas por la Ley 1554 de 2012; \n c) Se practiquen actividades peligrosas, de acuerdo con la reglamentación establecida por el Gobierno nacional; \n d) Se realicen actividades sexuales o pornográficas, o se ejerza la prostitución, o la explotación sexual; \n e) Se realicen actividades de diversión destinadas al consumo de bebidas alcohólicas y consumo de cigarrillo, tabaco y sus derivados y sustancias psicoactivas; \n f) Se desarrollen juegos de suerte y azar localizados;', '1. Allow, sponsor, tolerate, induce or constrain the admission of children and adolescents to places where:  n a) Performing movies or activities suitable only for people over 18 years of age;  n b) The videogames service is provided unless they are suitable for the age of the child or adolescent, under the conditions established by Law 1554 of 2012;  n c) Hazardous activities are carried out, in accordance with the regulations established by the national government;  n d) Sexual or pornographic activities, or prostitution, or sexual exploitation;  n e) Fun activities aimed at the consumption of alcoholic beverages and cigarette consumption, tobacco and its derivatives and psychoactive substances;  n f) Localized gambling  are developed;', 1, 297, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1305, 'Inducir, engañar o realizar cualquier acción para que los niños, niñas y adolescentes ingresen o participen de actividades que les están prohibidas por las normas vigentes.', 'To induce, deceive or take any action so that children and adolescents, enter or get into in activities that are prohibited by current regulations', 1, 298, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1306, 'Permitir o inducir a los niños, niñas y adolescentes a utilizar las telecomunicaciones, publicaciones y documentos para acceder a material pornográfico.', 'Allow or induce children and adolescents to use telecommunications, publications and documents to access pornographic material', 1, 299, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1307, 'Emplear o inducir a los niños, niñas y adolescentes a utilizar indebidamente las telecomunicaciones o sistemas de emergencia.', 'Use or inducing children and adolescents to improperly use telecommunications or emergency systems', 1, 300, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1308, 'Facilitar, distribuir, ofrecer, comercializar, prestar o alquilar, cualquiera de los siguientes elementos, sustancias o bebidas, a niños, niñas o adolescentes: \n a) Ma terial pornográfico; \n b) Bebidas alcohólicas, cigarrillo, tabaco y sus derivados, sustancias psicoactivas o cualquier sustancia que afecte su salud; \n c) Pólvora o sustancias prohibidas; \n d) Armas, neumáticas o de aire, o que se asimilen a estas, elementos cortantes, punzantes, contundentes o sus combinaciones;', 'Provide, distribute, offer, market, lend or rent, any of the following elements, substances or beverages, to children or adolescents:  n a) Pornographic material;  n b) Alcoholic beverages, cigarettes, tobacco and its derivatives, psychoactive substances or any substance that affects them health;  n c) Powder or prohibited substances;  n d) Weapons, pneumatic or air, or that are assimilated to these, cutting, sharp, blunt or their combinations;', 1, 301, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1309, '6. Inducir a niños, niñas o adolescentes a: \n a) Consumir bebidas alcohólicas, cigarrillo, tabaco y sus derivados, sustancias psicoactivas o cualquier sustancia que afecte su salud; \n b) Participar en juegos de suerte y azar; \n c) Ingresar a fiestas o eventos similares en los cuales exista previa restricción de edad por parte de las autoridades de Policía, o esté prohibido su ingreso por las normas vigentes. \n d) La explotación laboral.', '6. Induce children or adolescents to:  n a) Consume alcoholic beverages, cigarettes, tobacco and its derivatives, psychoactive substances or any substance that affects their health;  n b) Participate in games of luck and chance;  n c) Enter parties or similar events in which there is a prior restriction of age by the police authorities, or its entry is prohibited by the regulations in force.  n d) Labor exploitation.', 1, 302, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1310, 'Permitir que los niños, niñas y adolescentes sean tenedores de animales potencialmente peligrosos.', 'To allow children and adolescents to be holders of potentially dangerous animals.', 1, 303, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1311, 'Ejercer, permitir, favorecer o propiciar el abuso, los actos y la explotación sexual de niños, niñas o adolescentes.', 'To exert, allow, favor or encourage the abuse, acts and sexual exploitation of children or adolescents.', 1, 304, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1312, 'Utilizar a niños, niñas y adolescentes para evitar el cumplimiento de una orden de Policía.', 'Use children and adolescents to avoid compliance with a police order.', 1, 305, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1313, 'Permitir que los niños, niñas y adolescentes hagan uso de piscinas y estructuras similares, de uso colectivo o de propiedad privada unihabitacional, sin el cumplimiento de los requisitos establecidos por la Ley 1209 de 2008 y las normas que la adicionen o modifiquen.', 'Allow children and adolescents to use swimming pools and similar structures, for collective use or private unihabitational property, without complying with the requirements established by Law 1209 of 2008 and the regulations that add or modify it', 1, 306, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1314, 'Permitir que los niños, niñas y adolescentes sean parte de confrontaciones violentas que puedan derivar en agresiones físicas.', 'Allowing children and adolescents to being part of violent confrontations that might lead to physical aggression.', 1, 307, 162, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1315, 'Comercializar, distribuir, tener, almacenar, portar o consumir sustancias psicoactivas o tóxicas, alcohólicas o demás sustancias estimulantes que puedan afectar su salud o que produzcan dependencia, que estén restringidas para menores de edad.', 'Commercialize, distribute, have, store, carry or consume psychoactive or toxic substances, alcoholic or other stimulating substances that might affect them health or that produce dependence, which are restricted for minors children.', 1, 297, 171, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1316, 'Perpetrar, permitir o inducir abusos o maltrato físico, verbal, psicológico o sexual en lugar público o privado, incluido su lugar de trabajo.', 'perpetrate , allow or induce abuse or physical, verbal, psychological or sexual abuse in a public or private place, including the place of work.', 1, 297, 175, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1317, 'Utilizar a estas personas para obtener beneficio económico o satisfacer interés personal.', 'Use these people to obtain economic benefit or satisfy personal interest.', 1, 298, 175, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1318, 'Omitir dar la prelación a las personas en condición de discapacidad, personas de la tercera edad o adulto mayor, mujeres en estado de embarazo, personas con niños en brazos y personas que por su condición de salud requieran preferencia, especialmente en las filas, en el uso de los vehículos de transporte público, colectivo o individual y en todos los sitios, bienes y lugares públicos o abiertos al público.', 'Omit to give priority to people with disabilities, elderly or elderly people, pregnant women, people with children in arms and people who, due to their health condition, require preference, especially in the queue, in the use of public transport vehicles, collective or individual and in all places, goods and places public or open to the public.', 1, 299, 175, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1319, 'Dificultar, obstruir o limitar información e insumos relacionados con los derechos sexuales y reproductivos de la mujer, del hombre y de la comunidad LGBTI, incluido el acceso de estos a métodos anticonceptivos.', 'To hinder, obstruct or limit information and supplies related to the sexual and reproductive rights of women, men and the GLBT community, including the access to contraceptive methods.', 1, 300, 175, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1320, 'Irrespetar las manifestaciones y reuniones de las personas en el espacio público o en lugares privados, en razón a su etnia, raza, edad, género, orientación sexual, creencias religiosas, preferencias políticas y apariencia personal.', 'Disrespect the manifestations and meetings of people in the public space or in private places, due to their ethnicity, race, age, gender, sexual orientation, religious beliefs, political preferences and personal appearance.', 1, 301, 175, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1321, 'Limitar u obstruir las manifestaciones de afecto público que no configuren actos sexuales, de exhibicionismo en razón a la raza, orientación sexual, género u otra condición similar.', 'Limit or obstruct manifestations of public affection that do not constitute sexual acts, of exhibitionism due to race, sexual orientation, gender or other similar condition.', 1, 302, 175, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1322, 'Obtener para su funcionamiento el concepto sanitario expedido por la Secretaría de Salud o su delegado o quien haga sus veces.', 'Obtain for its operation the sanitary concept issued by the Ministry of Health or its delegate or whoever takes his/her place.', 1, 297, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1323, 'Proveer o distribuir a las personas que ejercen la prostitución y a quienes utilizan sus servicios, preservativos aprobados por las entidades competentes y facilitarles el cumplimiento de las medidas recomendadas por las autoridades sanitarias.', 'Provide or distribute to people who practice prostitution and those who use their services, condoms approved by the competent entities and facilitate compliance with the measures recommended by the health authorities.', 1, 298, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1324, 'Promover el uso del preservativo y de otros medios de protección, recomendados por las autoridades sanitarias, a través de información impresa, visual o auditiva, y la instalación de dispensadores de tales elementos en lugares públicos y privados de dichos establecimientos, inmuebles o lugares.', 'Promote the use of condoms and other means of protection, recommended by health authorities, through printed, visual or auditory information, and the installation of dispensers of such elements in public and private places establishments, buildings or places.', 1, 299, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1325, 'Colaborar con las autoridades sanitarias y de Policía cuando se realicen campañas de inspección y vigilancia y asistir a los cursos que ellas organicen.', 'Collaborate with the health and police authorities when inspection and monitoring campaigns are made and attend the courses they organize', 1, 300, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1326, 'Tratar dignamente a las personas que ejercen la prostitución, evitar su discriminación o rechazo y la violación de sus derechos a la libre movilización y al desarrollo de la personalidad.', 'Treat people who practice prostitution with dignity, avoid their discrimination or rejection and the violation of their rights to free movement and personality development.', 1, 301, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1327, 'No permitir ni propiciar el ingreso de niños, niñas o adolescentes a estos establecimientos, inmuebles o lugares.', 'Not allow or encourage the entrance of children or adolescents to these establishments, buildings or places.', 1, 302, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1328, 'No permitir, ni favorecer o propiciar el abuso y la explotación sexual de menores de edad o de personas con discapacidad .', 'Not allow, or favor or encourage the abuse and sexual exploitation of minors or people with disabilities.', 1, 303, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1329, 'En ningún caso, favorecer, permitir, propiciar o agenciar el maltrato, su utilización para la pornografía, la trata de personas o la Explotación Sexual Comercial de Niños, Niñas y Adolescentes (Escnna).', 'No case, facilitate, allow, promote or manage the abuse, its use for pornography, human trafficking or Sexual Abuse and Commercial Exploitation of children and Adolescents (ESCNNA).', 1, 304, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1330, 'No inducir ni constreñir al ejercicio de la prostitución a las personas o impedir, a quien lo realiza, retirarse del mismo si fuere su deseo.', 'Do not induce or constrain the prostitution practice to people or prevent the person who performs it from withdrawing from it if it is their wish.', 1, 305, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1331, 'No mantener en cautiverio o retener a las personas que ejercen la prostitución.', 'Do not keep in captivity or retain people who practice prostitution.', 1, 306, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1332, 'No realizar publicidad alusiva a esta actividad en la vía pública, salvo la identificación del lugar en su fachada.', 'Do not display advertising allusive to this activity on public space, except for the identification of the place on its facade', 1, 307, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1333, 'Velar por el cumplimiento de los deberes y comportamientos de las personas que ejercen la prostitución.', 'Ensure compliance with the duties and behaviors of people who practice the prostitution.', 1, 308, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1334, 'Proveer los elementos y servicios de aseo necesarios para garantizar el cumplimiento de las medidas recomendadas por las autoridades.', 'Provide the elements and cleaning services necessary to ensure compliance with the measures recommended by the authorities', 1, 309, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1335, 'Intervenir en caso de controversia, entre las personas que utilizan el servicio y las que ejercen la prostitución, para evitar el detrimento de los derechos de estas últimas.', 'Intervene in case of controversy, between the people who use the service and those who practice prostitution, to avoid the detriment of the latter´s rights', 1, 310, 182, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1336, 'Incumplir cualquiera de los requisitos establecidos en la normatividad vigente para los establecimientos, inmuebles o lugares en donde se ejerza la prostitución.', 'Breach with any of the requirements established in the current regulations for establishments, buildings or places where prostitution is practiced.', 1, 297, 183, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1337, 'Ejercer la prostitución o permitir su ejercicio por fuera de las zonas u horarios asignados para ello o contrariando lo dispuesto en las normas o en el reglamento pertinente de carácter distrital o municipal.', 'Practice the prostitution or allow its exercise outside the areas or schedules assigned for this or against to the provisions of the rules or the relevant regulations of a district or municipal nature', 1, 298, 183, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1338, 'Ejercer la prostitución sin el cumplimiento de las medidas sanitarias y de protección requeridas.', 'To practice prostitution without complying with the sanitary and protection measures required.', 1, 299, 183, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1339, 'Realizar actos sexuales o exhibicionistas en la vía pública o en lugares expuestos a esta.', 'Do not commit sexual or exhibitionist acts on public highways or in places exposed to it', 1, 300, 183, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1340, 'Negarse a: \n a) Portar el documento de identidad; \n b) Utilizar los medios de protección y observar las medidas que ordenen las autoridades sanitarias; \n c) Colaborar con las autoridades sanitarias que ejercen la prevención y el control de enfermedades de transmisión sexual y VIH, atender sus indicaciones.', 'Refuse to:  n a) Carry the identity document;  n b) Use the means of protection and observe the measures ordered by the health authorities;  n c) Collaborate with the health authorities that exercise the prevention and control of sexually transmitted diseases and HIV, taking care of their indications.', 1, 301, 183, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1341, 'Irrespetar, agredir o maltratar física o psicológicamente a las personas en el ejercicio de la prostitución, en sus derechos, dignidad o libertad.', 'Disrespect, assault or physically or psychologically mistreat people who practice the prostitution, in their rights, dignity or freedom.', 1, 297, 187, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1342, 'Obligar a las personas en el ejercicio de la prostitución a realizar actividades contrarias a su voluntad.', 'Force people who practice the prostitution to do activities against to their will.', 1, 298, 187, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1343, 'Solicitar o usar los servicios de las personas en ejercicio de la prostitución incumpliendo las condiciones del artículo 45.', 'Request or use the services of people who practice the prostitution in breach of the conditions of Article 45.', 1, 299, 187, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1344, 'Incumplir cualquiera de los requisitos establecidos en la normatividad vigente para los establecimientos, inmuebles o lugares en donde se ejerza la prostitución.', 'Fail to comply with any of the requirements established in the current regulations for establishments, buildings or places where prostitution is practiced.', 1, 297, 189, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1345, 'Propiciar el ejercicio de la prostitución o permitir su ejercicio por fuera de las zonas u horarios asignados para ello o contrariando lo dispuesto en las normas o en el reglamento pertinente de carácter distrital o municipal.', 'To encourage the exercise of prostitution or allow it to be exercised outside the zones or schedule assigned for it or against the provisions of the regulations or the pertinent regulations of a district or municipal nature.', 1, 298, 189, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1346, 'Propiciar cualquiera de los comportamientos previstos en el artículo 44.', 'Encourage any of the behaviors provided in article 44.', 1, 299, 189, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1347, 'Incumplir cualquiera de las condiciones previstas en el artículo 43.', 'Failure to comply with any of the conditions provided in article 43.', 1, 300, 189, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1348, 'Reuniones o manifestaciones públicas y pacíficas en el espacio público.', 'Public and peaceful meetings or manifestations in the public space.', 1, 297, 191, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1349, 'Actividades que involucran aglomeraciones de público no complejas.', 'Activities that involve non-complex public agglomerations.', 1, 298, 191, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1350, 'Actividades que involucran aglomeraciones de público complejas.', 'Activities that involve complex public agglomerations.', 1, 299, 191, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1351, 'El expendio de alcohol se realizará únicamente en las áreas especialmente dispuestas para tales efectos.', 'The sale of alcohol will be  only in areas specially arranged for such purposes.', 1, 297, 194, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1352, 'Las ventas se interrumpirán veinte (20) minutos antes de la finalización del evento.', 'Sales will be interrupted twenty (20) minutes before the end of the event.', 1, 298, 194, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1353, 'Estará prohibido el porte y consumo de bebidas alcohólicas, durante el ingreso, salida o evacuación de los eventos.', 'The carrying and consumption of alcoholic beverages will be prohibited during the entrance, exit or evacuation of the events.', 1, 299, 194, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1354, 'El organizador del evento deberá establecer zonas en las que no se permita el consumo de alcohol, para que los espectadores decidan en que área desean presenciar el espectáculo público.', 'The organizer of the event must establish zones which alcohol consumption is not allowed, in order to the spectators decide in which area they want to witness the public spectacle.', 1, 300, 194, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1355, 'El organizador se abstendrá de vender alcohol a personas que presenten comportamientos o síntomas de intoxicación etílica.', 'The organizer will abstain from selling alcohol to people who show up behaviors or symptoms of alcohol intoxication.', 1, 301, 194, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1356, 'Realizar accion es que constituyan un riesgo para la propia vida o la de terceros, antes, durante o después de tales actividades.', 'Take actions that constitute a risk to one´s own life or of third parties, before, while or after such activities.', 1, 297, 213, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1357, 'No utilizar los implementos de seguridad en la actividad que los exige.', 'Do not use safety equipment in the activity that requires', 1, 298, 213, '29/11/17');
