INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1234, 'Salud Pública: Es la responsabilidad estatal y ciudadana de protección de la salud como un derecho esencial, individual, colectivo y comunitario logrado en función de las condiciones de bienestar y calidad de vida.', 'Public Health: It is the state and citizen responsibility of health protection as an essential right, individual, collective and community achieved according to the conditions of well-being and quality of life.', 1, 300, 105, '29/11/17');
