INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1549, 'No acreditar la inscripción ante la Secretaría de Salud o quien haga sus veces, de la respectiva entidad territorial, para el almacenamiento o expendio de alimentos que lo requieran, así como de carne, productos y derivados cárnicos comestibles, de acuerdo con la normatividad sanitaria vigente.', 'To not accredit the registration before the Ministry of Health or whoever acts as such, of the respective territorial entity, for the storage or sale of food that requires it, as well as of meat, products and edible meat products, in accordance with the current sanitary regulations.', 1, 297, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1550, 'Almacenar o comercializar carne, productos cárnicos comestibles que no provengan de plantas de beneficio animal (mataderos) autorizadas o que no cumplan con las disposiciones o normatividad sanitaria vigente.', 'To store or market meat or edible meat products that does not come from authorized industrial plants of animal benefit (slaughterhouses) or that does not fulfills with the regulations of current sanitary regulations .', 1, 298, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1551, ' Almacenar, transportar o vender derivados cárnicos que no cumplan con las disposiciones de inocuidad establecidas por el Ministerio de Salud y Protección Social y garantizar en todo momento la procedencia de los mismos.', 'To store, transport or sell meat derivatives that does not fulfill with the safety regulations established by the Ministry of Health and Social Protection and guarantee the provenance.', 1, 299, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1552, 'Adquirir alimentos, carne, productos cárnicos comestibles y derivados cárnicos de proveedores que no se encuentren autorizados y registrados ante la autoridad sanitaria competente o que no hayan entregado el producto a la temperatura reglamentada o transportado en vehículos que no garanticen el mantenimiento de la misma.', 'To gain food, meat, edible meat products and meat derivatives from suppliers that are not authorized and registered before the competent health authority or that have not delivered the product to the regulated temperature or transported in vehicles that do not guarantee the maintenance thereof.', 1, 300, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1553, 'No contar con un sistema de refrigeración que garantice el mantenimiento de la temperatura reglamentada para los productos.', 'Not having a refrigeration system that guarantees the maintenance of the regulated temperature for the products.', 1, 301, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1554, 'No mantener en refrigeración a la temperatura reglamentada, la carne o los productos cárnicos o lácteos.', 'Do not keep refrigerated at the controlled temperature, meat or meat or dairy products.', 1, 302, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1555, 'No acreditar la autorización sanitaria de transporte expedido por la Secretaría de Salud de la entidad territorial correspondiente o quien haga sus veces, para el transporte de carne, productos cárnicos comestibles y derivados cárnicos destinados para el consumo humano, de acuerdo con la normatividad sanitaria vigente.', 'To not accredit the sanitary authorization of transport issued by the Ministry of Health of the corresponding territorial entity or whoever acts as such, for the transport of meat, edible meat products and meat derivatives destined for human consumption, in accordance with the current sanitary regulations.', 1, 303, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1556, 'No contar con soporte documental en el cual conste que los productos transportados provienen de un establecimiento registrado, aprobado e inspeccionado. Para la carne, productos cárnicos comestibles y derivados cárnicos, deberán contar con la guía de transporte de producto de acuerdo con lo establecido en la normatividad sanitaria.', 'Do not count on documentary support in which the transported products come from a registered, approved and inspected establishment. For meat, edible meat products and meat products, they must have a product transport guide in accordance with the provisions of the sanitary regulations.', 1, 304, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1557, 'No garantizar el mantenimiento de la cadena de frío del producto y las condiciones de transporte requeridas por la normatividad sanitaria vigente, de manera que se evite la contaminación de los alimentos transportados.', 'Do not guarantee the maintenance of the cold chain of the product and the transport conditions required by current sanitary regulations, in order to avoid contamination of the transported food.', 1, 305, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1558, 'No conservar en el lugar donde se expendan o suministren, sus accesos y alrededores limpios y libres de acumulación de basuras.', 'Not keeping in the place where they are sold or supplied, its access and surroundings clean and free of accumulation of rubbish.', 1, 306, 330, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1559, 'No mantener las superficies del lugar donde se preparan, almacenan, expenden o suministren alimentos debidamente protegidos de cualquier foco de insalubridad.', 'Not keeping surfaces where preparation, storage, dispense or provide properly protected from any source of unhealthy foods.', 1, 307, 330, '29/11/17');
