INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1482, 'Limitar o vetar el acceso a lugares abiertos al público o eventos públicos a personas en razón de su raza, sexo, orientación sexual, identidad de género, condición social o económica, en situación de discapacidad o por otros motivos de discriminación similar.', 'Limit or vetoing access to places open to the public or public events to people because of their race, sex, sexual orientation, gender identity, social or economic status, in a situation of disability or for other reasons of similar discrimination.', 1, 310, 281, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1483, 'Incumplir las normas o disposiciones de seguridad o sanidad, para el desarrollo de la actividad económica, de acuerdo con su objeto social, previo concepto de la autoridad especializada.', 'Breach with health or safety regulations or provisions for the development of economic activities, in accordance with its corporate purpose, prior to the concept of the specialized authority.', 1, 297, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1484, 'No separar en la fuente los residuos sólidos, ni depositarlos selectivamente en un lugar destinado para tal efecto.', 'Do not separate the solid waste at the source, nor deposit it selectively in places destined for that purpose.', 1, 298, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1485, 'Permitir el consumo de tabaco y/o sus derivados en lugares no autorizados por la ley y la normatividad vigente.', 'Allow the consumption of tobacco and / or tobacco products in places where is not authorized by law and current regulations.', 1, 299, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1486, 'Permitir la permanencia de animales de cualquier especie que afecte las condiciones de higiene, salubridad y seguridad o que impida la correcta prestación del servicio de acuerdo con las disposiciones vigentes.', 'Allow the permanence of animals of any species affecting the conditions of hygiene, health and safety or preventing the proper provision of service in accordance with the current regulations', 1, 300, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1487, 'Comercializar en el establecimiento artículos de mala calidad, caducados o adulterados que puedan constituir peligro para la salud pública.', 'Commercialize in the establishment articles of low quality, expired or adulterated that might constitute a danger to public health.', 1, 301, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1488, 'Comercializar, almacenar, poseer o tener especies de flora o fauna que ofrezcan peligro para la integridad y la salud.', 'Commercialize, store, own or have flora or fauna species that pose a danger to health and integrity.', 1, 302, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1489, 'No retirar frecuentemente los residuos de las áreas de producción o depósito y no evacuarlas de manera que se elimine la generación de malos olores, y se impida el refugio y alimento de animales y plagas.', 'Not frequently removing the waste from the production or storage areas and not evacuating it in a way that eliminates the generation of foul smell, in order to prevent being the shelter of animals and pets.', 1, 303, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1490, 'No facilitar la utilización de los servicios sanitarios limpios y desinfectados a las personas que así lo requieran, en los establecimientos abiertos al público y proveer de los recursos requeridos para la higiene personal.', 'Not facilitating the use of clean and disinfected health services to people who require it, in establishments open to the public and provide the resources required for personal hygiene.', 1, 304, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1491, 'No destruir en la fuente los envases de bebidas embriagantes.', 'Not destroying containers of intoxicating beverages.', 1, 305, 288, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1492, 'Comprar, alquilar o usar equipo terminal móvil con reporte de hurto y/o extravío en la base de datos negativa de que trata el artículo 106 de la Ley 1453 de 2011 o equipo terminal móvil cuyo número de identificación físico o electrónico haya sido reprogramado, remarcado, modificado o suprimido.', 'Buy, rent or use mobile terminal equipment with reported stolen and / or lost in the negative database referred to in article 106 of Law 1453 of 2011 or mobile terminal equipment whose physical or electronic identification number has been reprogrammed, highlighted , modified or deleted.', 1, 297, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1493, 'Comercializar equipos terminales móviles sin la respectiva autorización del Ministerio de Tecnologías de la Información y las Comunicaciones o por un proveedor de redes y servicios de telecomunicaciones móviles, de conformidad con la normatividad vigente.', 'Commercialize mobile terminal equipment without the respective authorization of the Ministry of Information Technology and Communications or by a provider of mobile telecommunications networks and services, in accordance with current regulations.', 1, 298, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1494, 'Tener, poseer, y almacenar tarjetas madre o blackboard de manera ilícita en el establecimiento.', 'Having, possessing, and storing mother or blackboard cards illegally in the establishment.', 1, 299, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1495, 'Distribuir, almacenar, transportar u ofrecer al público equipos terminales móviles con reporte de hurto y/o extravío en la base de datos negativa de que trata el artículo 106 de la Ley 1453 de 2011 o equipos terminales móviles o tarjetas madre cuyo n úmero de identificación físico o electrónico haya sido reprogramado, remarcado, modificado o suprimido.', 'Distribute, store, transport or sell mobile equipment to the public which has been stolen and / or lost in the database referred to in article 106 of Law 1453 of 2011 or mobile equipment or motherboards whose identification number physical or electronic has been reprogrammed, modified or deleted.', 1, 300, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1496, 'Vender, alquilar, aceptar, permitir o tolerar la venta, almacenamiento o bodegaje de equipo terminal móvil nuevo o usado, de origen ilícito o que carezca de comprobante de importación, factura de venta o documento equivalente de conformidad con la normatividad vigente o que se encuentre reportado por hurto y/o extravío en la base de datos negativa de que trata el artículo 106 de la Ley 1453 de 2011.', 'Sell, rent, accept, allow or tolerate the sale, or storage of new or used mobile equipment, of illicit origin or lacking proof of import, sales invoice or equivalent document in accordance with current regulations or that has been reported for theft and / or lost in the database that is dealt with in article 106 of Law 1453 of 2011.', 1, 301, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1497, 'Alterar, manipular, borrar, o copiar, no estando autorizado para hacerlo o con un fin no justificado o ilícito, las bases de datos negativa y positiva de equipos terminales móviles.', 'Alter, manipulate, delete, or copy, not being authorized to do so or with an unjustified or unlawful purpose, the negative and positive databases of mobile terminal equipment.', 1, 302, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1498, 'Incumplir las condiciones, requisitos u obligaciones para la importación, exportación, distribución, comercialización, mantenimiento y reparación, establecidas por la normatividad vigente.', 'Breach with the conditions, requirements or obligations for import, export, distribution, commercialization, maintenance and repair, established by current regulations.', 1, 303, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1499, 'No solicitar o no realizar el registro individual de equipo terminal móvil con su número de IMEI al momento de ser importado al país.', 'Not request or make individual registration of mobile with the IMEI number when it is being imported into the country.', 1, 304, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1500, 'No enviar, transmitir, o registrar en la manera y tiempos previstos, la información sobre la importación individualizada de equipo terminal móvil, al responsable de llevar la base de datos positiva.', 'Not sending, transmitting, or recording in the manner and time foreseen, the information on the individualized import of mobile terminal equipment, to the person in charge of keeping the positive database.', 1, 305, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1501, 'Alterar, manipular, borrar, o copiar, no estando autorizado para hacerlo o con un fin no justificado o ilícito, el registro individual de equipo terminal móvil en los procesos de importación.', 'Alter, manipulate, erase, or copy, not being authorized to do so or with an unjustified or unlawful purpose, the individual registration of mobile terminal equipment in the import processes.', 1, 306, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1502, 'Reprogramar, remarcar, modificar o suprimir el número de identificación físico o electrónico asociado a un equipo terminal móvil o facilitar estos comportamientos.', 'Reprogram, remark, modify or delete the electronic identification number associated with a mobile  equipment or facilitate these behaviors.', 1, 307, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1503, 'Activar líneas telefónicas sin que el usuario haya suministrado al prestador del servicio, los datos biográficos en el momento de la activación.', 'Activate telephone lines without the user having provided the service provider with the biographical data at the time of activation.', 1, 308, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1504, 'Activar Sim Card (IMSI) sin que el usuario haya suministrado al prestador del servicio, los datos biográficos en el momento de la activación.', 'Activate Sim Card (IMSI) when the user has not supplied the service provider, the biographical data at the time of activation.', 1, 309, 294, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1505, 'Utilizarla en actividades diferentes a la respectiva autorización ambiental.', 'Used in different activities to the respective environmental authorization.', 1, 297, 307, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1506, 'Arrojar sustancias contaminantes, residuos o desechos a los cuerpos de agua.', 'Throw pollutants, waste or debris to water sources.', 1, 298, 307, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1507, 'Deteriorar, dañar o alterar los cuerpos de agua, zonas de ronda hídrica y zonas de manejo y preservación ambiental en cualquier forma.', 'Deteriorating, damaging or altering water bodies, water round zones and management zones and environmental preservation in any form.', 1, 299, 307, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1508, 'Captar agua de las fuentes hídricas sin la autorización de la autoridad ambiental.', 'Collect water from water sources without authorization from the environmental authority.', 1, 300, 307, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1509, 'Lavar bienes muebles en el espacio público, vía pública, ríos, canales y quebradas.', 'Wash movable property in public space, public road, rivers, water canals and streams.', 1, 301, 307, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1510, 'Realizar cualquier actividad en contra de la normatividad sobre conservación y preservación de humedales, y sobre cananguchales y morichales.', 'Any activity against the regulations on conservation and preservation of wetlands, and on cananguchales and morichales.', 1, 302, 307, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1511, 'Colectar, aprovechar, mantener, tener, transportar, introducir, comercializar, o poseer especies de fauna silvestre (viva o muerta) o sus partes, sin la respectiva autorización ambiental.', 'Collect, seize, hold, carry, introduce, market, or possess wildlife species (living or dead) or parts thereof without the respective environmental authorization.', 1, 297, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1512, 'Aprovechar, recolectar, almacenar, extraer, introducir, mantener, quemar, talar, transportar o comercializar especies de flora silvestre, o sus productos o subproductos, sin la respectiva autorización de la autoridad competente.', 'Seize, collect, store, retrieve, enter, maintain, burning, felling, transporting or marketing species of wild flora, or their products or by-products, without proper authorization of the competent authority.', 1, 298, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1513, 'Movilizar maderas sin el respectivo salvoconducto único de movilización o guía de movilización.', 'Mobilize wood without the respective single safeguards of mobilization or mobilization guide.', 1, 299, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1514, 'Presentar el permiso de aprovechamiento, salvoconducto único de movilización, registro de plantación y guía de movilización para transportar maderas con inconsistencias o irregularidades.', 'Presenting the permit for harvesting, single safe-conduct of mobilization, registration of plantation and mobilization guide to transport wood with inconsistencies or irregularities.', 1, 300, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1515, 'Talar, procesar, aprovechar, transportar, transformar, comercializar o distribuir especies o subproductos de flora silvestre de los parques nacionales o regionales naturales, salvo lo dispuesto para las comunidades en el respectivo instrumento de planificación del parque.', 'Cut down, process, exploit, transport, process, marketing or distributing species of wild flora or byproducts of natural national or regional parks, except as provided for communities in the park planning.', 1, 301, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1516, 'La caza o pesca industrial sin permiso de autoridad competente.', 'Industrial hunting or fishing without the permission of a competent authority.', 1, 302, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1517, 'Contaminar o envenenar recursos fáunicos, forestales o hidrobiológicos.', 'Contaminate or poison faunal, forestry or hydro biological resources.', 1, 303, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1518, 'Experimentar, alterar, mutilar, manipular las especies silvestres sin el permiso de autoridad ambiental competente.', 'Experimenting, altering, mutilating, manipulating wild species without the permission of a competent environmental authority.', 1, 304, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1519, 'Violar los reglamentos establecidos para los períodos de veda en materia de caza y pesca.', 'Violate the regulations established for the hunting periods and fishing.', 1, 305, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1520, 'Tener animales silvestres en calidad de mascotas.', 'Have wild animals as pets.', 1, 306, 309, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1521, 'Realizar quemas de cualquier clase salvo las que de acuerdo con la normatividad ambiental estén autorizadas.', 'Make any kind of fires, except that according with environmental regulations are authorized.', 1, 297, 315, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1522, 'Emitir contaminantes a la atmósfera que afecten la convivencia.', 'Emit air pollutants which affecting coexistence.', 1, 298, 315, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1523, 'Ocupar ilícitamente áreas protegidas, de manera temporal o permanente.', 'Unlawfully occupy protected areas, temporarily or permanently.', 1, 297, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1524, 'Suministrar alimentos a la fauna silvestre.', 'Provide food to wildlife.', 1, 298, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1525, 'Alterar elementos naturales como piedras, rocas, peñascos, árboles, con pintura o cualquier otro medio, que genere marcas.', 'Modify natural elements such as stones, rocks, boulders, trees, with paint or any other means that generates marks.', 1, 299, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1526, 'Transitar con naves o vehículos automotores no autorizados, fuera del horario y ruta establecidos y/o estacionarlos en sitios no señalados para tales fines.', 'To travel with unauthorized vehicles or motor vehicles, outside the established time and route and / or to park them in places not indicated for such purposes.', 1, 300, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1527, 'Vender, comerciar o distribuir productos comestibles de cualquier índole, con excepción de aquellos autorizados expresamente.', 'Sell, trade or distribute edible products of any kind, with the exception of those expressly authorized.', 1, 301, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1528, 'Ingresar sin permiso de la autoridad ambiental competente.', 'Enter without permission from the competent environmental authority.', 1, 302, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1529, 'Permanecer en las áreas del Sistema de Parques Nacionales Naturales más tiempo del autorizado.', 'Staying in the areas of Natural National Park System longer than authorized.', 1, 303, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1530, 'No exhibir ante los funcionarios y autoridades competentes la autorización respectiva cuando se requiera.', 'Do not show the corresponding authorization to the officials and competent authorities when it is required.', 1, 304, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1531, 'Promover, realizar o participar en reuniones o actividades que involucren aglomeración de público no autorizadas por la autoridad ambiental.', 'Promote, arrange or participate in meetings or activities involving public agglomeration which are not authorized by the environmental authority.', 1, 305, 317, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1575, 'Transportar escombros en medios no aptos ni adecuados.', 'Transporting debris on not suitable modes.', 1, 307, 337, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1576, 'No recoger los residuos sólidos en los horarios establecidos por la misma empresa recolectora, salvo información previa debidamente publicitada, informada y justificada.', 'Do not pick up the solid waste in the schedules established by the same collection company, except prior information duly publicized, informed and justified.', 1, 308, 337, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1577, 'Arrojar en las redes de alcantarillado, acueducto y de aguas lluvias, cualquier objeto, sustancia, residuo, escombros, lodos, combustibles y lubricantes, que alteren u obstruyan el normal funcionamiento.', 'Throw in the sewer systems or aqueduct, any object, substance, residue, debris, sludge, fuel and lubricants that modify or obstruct normal operation.', 1, 309, 337, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1578, 'Permitir la presencia de vectores y/o no realizar las prácticas adecuadas para evitar la proliferación de los mismos en predios urbanos.', 'Allow the presence of vectors and / or do not take proper measures to prevent the proliferation thereof in urban land practices.', 1, 310, 337, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1579, 'No permitir realizar campañas de salud pública para enfermedades transmitidas por vectores dentro de los predios mencionados en el anterior inciso.', 'Do not allow public health campaigns for diseases transmitted by vectors within the properties mentioned in the previous subsection.', 1, 311, 337, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1580, 'Registrar los bienes de interés cultural. Para el caso del patrimonio arqueológico el registro se debe realizar ante el Instituto Colombiano de Antropología e Historia de acuerdo a lo establecido en las normas vigentes que regulan la materia, las que las adicionen o modifiquen, y sus decretos reglamentarios.', 'Register the goods of cultural interest. In the case of archaeological heritage, the registration must be made to the Colombian Institute of Anthropology and History according with the current provisions that regulate the subject, those that add or modify them, and their regulatory decrees.', 1, 297, 341, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1581, 'Mantener en buen estado y en un lugar donde no tengan riesgo de deterioro, ruptura o destrucción, los bienes de interés cultural o el patrimonio arqueológico que estén bajo su tenencia, cumpliendo las disposiciones que regulan la materia.', 'Keep in good condition and in a place where there is no risk of deterioration, rupture or destruction, cultural assets or archaeological heritage that are under its possession, complying with the provisions that regulate the matter.', 1, 298, 341, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1582, 'No efectuar una intervención de un bien de interés cultural salvo que se realice con la supervisión de profesionales idóneos en la materia y con la autorización de la autoridad que haya efectuado la declaratoria. Para el caso del patrimonio arqueológico la intervención debe contar con la aprobación de la autorización de intervención arqueológica otorgada por el Instituto Colombiano de Antropología e Historia.', 'Not making an intervention of a cultural interest property unless it is done with the supervision of qualified professionals in the field and with the authorization of the authority that made the declaration. In the case of archaeological heritage, the intervention must have the approval of the authorization of archaeological intervention granted by the Colombian Institute of Anthropology and History.', 1, 299, 341, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1583, 'No realizar la intervención de un bien de interés cultural sin la autorización de la entidad competente que haya efectuado dicha declaratoria; si el bien de interés cultural es arqueológico, deberá cumplirse con los requisitos del numeral anterior.', 'Not performing the intervention of a cultural interest property without the authorization of the competent entity that has made said declaration; If the good of cultural interest is archaeological, the requirements of the previous numeral must be met.', 1, 300, 341, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1584, 'Abstenerse de exportar de manera temporal o definitiva los bienes muebles de interés cultural del ámbito nacional, incluido el patrimonio arqueológico, sin la debida autorización de la autoridad que corresponda. Para el caso de los bienes de interés cultural del ámbito territorial es necesaria la autorización de la entidad territorial respectiva.', 'Abstain from temporarily or permanently exporting movable assets of cultural interest at the national level, including the archaeological heritage, without the due authorization of the corresponding authority. In the case of goods of cultural interest of the territorial scope, the authorization of the respective territorial entity is necessary.', 1, 301, 341, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1585, 'No dar aviso inmediato a las autoridades del hallazgo de bienes del patrimonio arqueológico o no dar aviso sobre bienes de interés cultural y patrimonio cultural adquiridos ilícitamente por terceros, de conformidad con las normas sobre la materia.', 'Not giving immediate notice to the authorities of the discovery of assets of the archaeological heritage or not giving notice about goods of cultural interest and cultural heritage unlawfully acquired by third parties, in accordance with the rules on the matter.', 1, 297, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1586, 'Incumplir las disposiciones sobre conservación, preservación y uso de las áreas e inmuebles de interés cultural de acuerdo con las leyes nacionales y los Planes Especiales de Manejo y Protección (PEMP) aprobados por el Ministerio de Cultura o la autoridad competente, normas que son de superior jerarquía a los Planes de Ordenamiento Territorial.', 'Breach with the provisions on conservation, preservation and use of the areas and buildings of cultural interest in accordance with national laws and the Special Plans of Management and Protection (PEMP) approved by the Ministry of Culture or the competent authority, norms that are superior hierarchy to the Plans of Territorial Ordering.', 1, 298, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1587, 'Intervenir, en los términos establecidos por el numeral segundo del artículo 11 de la Ley 397 de 1997, modificado por el artículo 7° de la Ley 1185 de 2008, un bien de interés cultural o patrimonio arquitectónico, sin la respectiva licencia o autorización de la autoridad que hubiere efectuado la decl aratoria o sin la asesoría en restauración de personal autorizado para ello.', 'To intervene, in the terms established by the second numeral of article 11 of Law 397 of 1997, modified by article 7 of Law 1185 of 2008, a good of cultural interest or architectural heritage, without the respective license or authorization of the authority that has made the declaration or without the advice in restoration of authorized personnel for it.', 1, 299, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1588, 'Destruir, dañar, dar utilización ilícita o explotación ilegal a bienes materiales de interés cultural.', 'Destroying, damaging, giving illegal use or illegal exploitation to material goods of cultural interest.', 1, 300, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1589, 'Exportar bienes de interés cultural sin la autorización de la autoridad cultural competente, sustraerlos, disimularlos u ocultarlos del control aduanero, o no reimportarlos en el término establecido en la autorización de exportación temporal.', 'Export goods of cultural interest without the authorization of the competent cultural authority, subtract them, disguise them or hide them from customs control, or do not reimport them within the term established in the temporary export authorization.', 1, 301, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1820, 'COMPARENDO', 'Subpoena', 1, 351, 0, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1590, 'Llevar a cabo, permitir o facilitar exploraciones, excavaciones o cualquier tipo de intervención de bienes arqueológicos sin la autorización requerida para ello.', 'Make, allow or facilitate exploration, excavations or any type of intervention of archaeological goods without the authorization required for it.', 1, 302, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1591, 'Omitir o no llevar a cabo las acciones necesarias de adecuado mantenimiento que le competan al poseedor, tenedor o propietario de un inmueble o mueble declarado como Bien de Interés Cultural, de tal forma que esto lleve a un deterioro de la estructura del inmueble y puesta en riesgo de los valores culturales, históricos, arquitectónicos, arqueológicos, patrimoniales, culturales, urbanísticos o paisajísticos del inmueble.', 'Omitting or not accomplish the necessary actions of adequate maintenance that belong to the possessor, holder or owner of a property or piece of furniture declared as an Asset of Cultural Interest, in such a way that this leads to a deterioration of the structure of the property and put risk of the cultural, historical, architectural, archaeological, patrimonial, cultural, urban or landscape values of the property.', 1, 303, 347, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1592, 'Promover, participar y patrocinar actividades de apuestas en cualquier recinto, en donde, de manera presencial, se involucren animales, con excepción a lo previsto en la Ley 84 de 1989.', 'Promote, participate and sponsor activities betting on any grounds, where are involved animals, except as provided in Law 84 of 1989.', 1, 297, 351, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1593, 'La venta, promoción y comercialización de animales domésticos en vía pública, en municipios de más de cien mil (100.000) habitantes.', 'Sale, promotion and marketing of pets on public roads in municipalities of more than one hundred thousand (100,000) citizens.', 1, 298, 351, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1594, 'El que permita, en su calidad de propietario, poseedor, tenedor o cuidador que los semovientes deambulen sin control en el espacio público.', 'The one that allows, in his capacity of owner, possessor, holder or caretaker that the livestock move without control in the public space.', 1, 299, 351, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1595, 'Dejar deambular semoviente, animales feroces o dañinos, en espacio público y privado, lugar abierto al público, o medio de transporte público, sin las debidas medidas de seguridad.', 'To leave to wander livestock, ferocious or harmful animals, in public and private space, place open to the public, o public transportation, without the due measures of security.', 1, 297, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1596, 'Impedir el ingreso o permanencia de perros lazarillos que, como guías, acompañen a su propietario o tenedor, en lugares públicos, abiertos al público, sistemas de transporte masivo, colectivo o individual o en edificaciones públicas o privadas.', 'Prevent the entrance or stay of guide dogs, as guides, who accompany their owner or holder, in public places, open to the public, mass public transportation systems, collective or individual or in public or private buildings', 1, 298, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1597, 'Omitir la recogida de los excrementos de los animales, por parte de sus tenedores o propietarios, o dejarlos abandonados después de recogidos, cuando ello ocurra en el espacio público o en áreas comunes.', 'Do not pick up the excrement from animals, by their holders or owners, or leave them abandoned after picking, when this happens in public space or in common areas', 1, 299, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1598, 'Trasladar un canino de raza potencialmente peligrosa en el espacio público, zonas comunes o en los lugares abiertos al público o en el transporte público en que sea permitida su estancia, sin bozal, trailla o demás implementos establecidos por las normas vigentes.', 'Walk down a potentially dangerous canine in the public space, common areas or in places open to the public or in public transport where their stay is allowed, without a muzzle or other implements established by the current regulations.', 1, 300, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1599, 'Incumplir las disposiciones para el albergue de animales.', 'Breach with the provisions for the shelter of animals.', 1, 301, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1600, 'Incumplir la normatividad vigente de impo rtación, registro, posesión, compra, venta, traspaso, donación o cualquier cesión del derecho de propiedad sobre animal clasificado como potencialmente peligroso en la ley.', 'Breach with current regulations on importation, registration, possession, purchase, sale, transfer, donation or any assignment of the right of ownership of an animal classified as potentially dangerous by law.', 1, 302, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1601, 'Tolerar, permitir o inducir por acción u omisión el que un animal ataque a una persona, a un animal o a bienes de terceros.', 'Tolerate, allow or induce by action or omission that an animal attacks a person, an animal or property of third parties.', 1, 303, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1602, 'Entrenar ejemplares caninos para su participación en peleas como espectáculo, para la agresión de las personas, a las cosas u otros animales o establecer asociaciones caninas orientadas para este fin.', 'Train canine specimens for their participation in fights as spectacle, in order to damage people, things or other animals or establish canine associations with this purpose.', 1, 304, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1603, 'Permitir que animales o mascotas esparzan, parcial o totalmente, en el espacio público o zonas comunes, el contenido de las bolsas y recipientes para la basura, una vez puestas para su recolección.', 'Allowing animals or pets spread, partly or wholly, in the public space or common areas, the contents of the bags and containers for trash, once placed for collection.', 1, 305, 364, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1604, 'Caninos que han tenido episodios de agresiones a personas; o le hayan causado la muerte a otros perros.', 'Canines that have had episodes of aggression against people; or have caused other dogs decease.', 1, 297, 369, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1605, 'Caninos que han sido adiestrados para el ataque y la defensa.', 'Canines who have been trained for attack and defense.', 1, 298, 369, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1606, 'Caninos que pertenecen a una de las siguientes razas o a sus cruces o híbridos: American Staffordshire Terrier, Bullmastiff, Dóberman, Dogo Argentino, Dogo de Burdeos, Fila Brasileiro, Mastín Napolitano, Bull Terrier, Pit Bull Terrier, American Pit Bull Terrier, de presa canario, Rottweiler, Staffordshire Terrier, Tosa Japonés y aquellas nuevas razas o mezclas de razas que el Gobierno nacional determine.', 'Canines who are from the following breeds or their crosses or hybrids: American Staffordshire Terrier, Bullmastiff, Doberman, Dogo Argentino, Dogo de Bordeaux, Fila Brasileiro, Neapolitan Mastiff, Bull Terrier, Pit Bull Terrier, American Pit Bull Terrier, prey canary, Rottweiler, Staffordshire Terrier, Japanese Tosa and those new breeds or mixtures of races determined by the national Government.', 1, 299, 369, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1607, 'Nombre del ejemplar canino.', 'Name of the dog specimen.', 1, 297, 372, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1608, 'Identificación y lugar de ubicación de su propietario.', 'Identification and location of its owner.', 1, 298, 372, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1609, 'Una descripción que contemple las características fenotípicas del ejemplar que hagan posible su identificación.', 'A description that includes the copy phenotypic characteristics that enable identification.', 1, 299, 372, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1610, 'El lugar habitual de residencia del animal, con la especificación de si está destinado a convivir con los seres humanos o si será destinado a la guarda, protección u otra tarea específica. Para proceder al registro del animal, su propietario debe aportar póliza de responsabilidad civil extracontractual, la que cubrirá la indemnización de los perjuicios patrimoniales que dichos ejemplares ocasionen a personas, bienes, o demás animales; así como el registro de vacunas del ejemplar, y certificado de sanidad vigente, expedido por la Secretaría de Salud del municipio. Será obligatorio renovar el registro anualmente, para lo cual se deberán acreditar los requisitos establecidos para la primera vez. En este registro se anotarán también las multas o medidas correctivas que tengan lugar, y los incidentes de ataque en que se involucre el animal. Una vez registrado el ejemplar, la autoridad distrital, municipal o local delegada, expedirá el respectivo permiso para poseer esta clase de perros. Este permiso podrá ser requerido en cualquier momento por las autoridades de Policía respectivas.', 'The place of residence of the animal, with the specification of if it is destined to live with humans or if it will be used for guarding, protection or other specific task. In order to proceed with the registration of the animal, its owner must provide a non-contractual civil liability policy, which will cover compensation for patrimonial damage caused to people, property, or other animals; as well as the registration of copies of the copy, and current health certificate, issued by the Ministry of Health of the municipality. It will be mandatory to renew the registration annually, for which the requirements established for the first time must be accredited. In this registry will also be noted the fines or corrective measures that take place, and the incidents of attack in which the animal is involved. Once registered the copy, the district, municipal or local delegated authority, will issue the respective permission to own this class of dogs. This permission may be requested at any time by the respective police authorities.', 1, 300, 372, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1611, 'Dejar deambular caninos potencialmente peligrosos en espacio público y privado, lugar abierto al público, o medio de transporte público.', 'Allow potentially dangerous canines to wander in public and private spaces, open to the public, or public transportation.', 1, 297, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1612, 'Trasladar un ejemplar canino potencialmente peligroso en el espacio público, zonas comunes o en los lugares abiertos al público o en el transporte público en que sea permitida su estancia, sin bozal, trailla o demás implementos establecidos por las normas vigentes.', 'Move out a potentially dangerous canine specimen in the public space, common areas or in places open to the public or in public transport where its stay is allowed, without muzzle or other implements established by the current regulations.', 1, 298, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1613, 'Incumplir las disposiciones establecidas para el albergue de caninos potencialmente peligrosos.', 'Breach with the provisions set for the shelter of potentially dangerous dogs.', 1, 299, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1614, 'Importar o establecer centros de crianza de razas de caninos potencialmente peligrosos sin estar autorizado para ello.', 'Import or establish breeding centers for potentially dangerous canine breeds without being authorized to do so.', 1, 300, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1615, 'Incumplir la normatividad vigente de registro, posesión, compra, venta, traspaso, donación o cualquier cesión del derecho de propiedad sobre caninos potencialmente peligrosos.', 'Breach with current regulations on registration, possession, purchase, sale, transfer, donation or any transfer of ownership of potentially dangerous canines.', 1, 301, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1616, 'Permitir a niños, niñas o adolescentes la posesión, tenencia o transporte de ejemplares caninos potencialmente peligrosos.', 'Allowing children or adolescents the possession, holding or transport of potentially dangerous dogs.', 1, 302, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1617, 'Permitir tener o transportar ejemplares caninos potencialmente peligrosos a personas que tengan limitaciones físicas o sensoriales que les impidan el control del animal.', 'Allow having or transporting potentially dangerous canine specimens to people who have physical or sensory limitations that prevent them from controlling the animal.', 1, 303, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1618, 'Tener o transportar caninos potencialmente peligrosos estando en estado de embriaguez o bajo el influjo de sustancias psicoactivas.', 'Have or transport potentially dangerous canine being intoxicated or under the influence of psychoactive substances.', 1, 304, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1619, 'No contar con póliza de responsabilidad civil extracontractual por la propiedad o tenencia de ejemplares caninos potencialmente peligrosos, una vez el Gobierno nacional expida la reglamentación sobre la materia.', 'Not having a non-contractual civil liability policy for the ownership or possession of potentially dangerous dogs, once the national government issues regulations on this.', 1, 305, 380, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1620, 'Parcelar, urbanizar, demoler, intervenir o construir:', 'Plot, urbanize, demolish, intervene or build:', 1, 343, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1621, 'En áreas protegidas o afectadas por el plan vial o de infraestructura de servicios públicos domiciliarios, y las destinadas a equipamientos públicos.', 'In areas protected or affected by the road plan or infrastructure of public utilities, and those destined to public facilities.', 1, 297, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1622, 'Con desconocimiento a lo preceptuado en la licencia.', 'With ignorance of what is prescribed in the license.', 1, 298, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1623, 'En bienes de uso público y terrenos afectados al espacio público.', 'In property for public use and land affected by public space.', 1, 299, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1624, 'En terrenos aptos para estas actuaciones, sin licencia o cuando esta hubiere caducado;', 'On land suitable for these actions, without a license or when it has expired;', 1, 300, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1625, 'Actuaciones en los inmuebles declarados de conservación e interés cultural, histórico, urbanístico, paisajístico y arquitectónico: ', 'Actions in declared properties of conservation and cultural, historical, urban, landscape and architectural interest:', 1, 344, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1626, 'Demoler sin previa autorización o licencia.', 'Demolishing without permission or license.', 1, 301, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1627, 'Intervenir o modificar sin la licencia.', 'Intervene or modify without the license.', 1, 302, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1628, 'Incumplir las obligaciones para su adecuada conservación.', 'Breach with obligations for its proper conservation.', 1, 303, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1629, 'Realizar acciones que puedan generar impactos negativos en el bien de interés cultural, tales como intervenciones estructurales, arquitectónicas, adecuaciones funcionales, intervenciones en las zonas de influencia y/o en los contextos del inmueble que puedan afectar las características y los valores culturales por los cuales los inmuebles se declararon como bien de interés cultural;', 'Carry out actions that may generate negative impacts on the good of cultural interest, such as structural, architectural interventions, functional adjustments, interventions in the areas of influence and or in the contexts of the property that may affect the characteristics and cultural values for which the properties were declared as good of cultural interest;', 1, 304, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1630, 'Usar o destinar un inmueble a: ', 'Use or assign a property to:', 1, 345, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1631, 'Uso diferente al señalado en la licencia de construcción.', 'Different from that indicated in the construction license.', 1, 305, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1632, 'Ubicación diferente a la señalada en la licencia de construcción.', 'Different location from that indicated in the construction license.', 1, 306, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1633, 'Contravenir los usos específicos del suelo.', 'Infringe the specific land uses.', 1, 307, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1634, 'Facilitar, en cualquier clase de inmueble, el desarrollo de usos o destinaciones del suelo no autorizados en licencia de construcción o con desconocimiento de las normas urbanísticas sobre usos específicos;', 'Facilitate, in any kind of property, the land development of uses or destinations which are not authorized in a construction license or with ignorance of the urban regulations for specific uses;', 1, 308, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1635, 'Incumplir cualquiera de las siguientes obligaciones: ', 'Breach with any of the following obligations:', 1, 346, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1636, 'Destinar un lugar al interior de la construcción para guardar materiales, maquinaria, escombros o residuos y no ocupar con ellos, ni siquiera de manera temporal, el andén, las vías o e spacios públicos circundantes.', 'Allocate a place inside the storage building materials, machinery, debris or waste and not take with them, even temporarily, the platform and surrounding roads or public space.', 1, 309, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1637, 'Proveer de unidades sanitarias provisionales para el personal que labora y visita la obra y adoptar las medidas requeridas para mantenerlas aseadas, salvo que exista una solución viable, cómoda e higiénica en el área.', 'Provide temporary health units for the personnel who work and visit the place and keep them clean, unless that the place has, comfortable and hygienic solution in the area.', 1, 310, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1638, 'Instalar protecciones o elementos especiales en los frentes y costados de la obra y señalización, semáforos o luces nocturnas para la seguridad de quienes se movilizan por el lugar y evitar accidentes o incomodidades.', 'Install protections or special elements on the fronts and sides of the work and signaling, traffic lights or night lights for the safety of those who move around the place and avoid accidents or discomforts.', 1, 311, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1639, 'Limpiar las llantas de los vehículos que salen de la obra para evitar que se arroje barro o cemento en el espacio público.', 'Clean the tires of the vehicles which are leaving the place to avoid that mud or cement will be thrown in the public space.', 1, 312, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1640, 'Limpiar el material, cemento y los residuos de la obra, de manera inmediata, cuando caigan en el espacio público.', 'Clean the material, cement and the residues of the work, immediately, when it has been thrown in the public space.', 1, 313, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1641, 'Retirar los andamios, barreras, escombros y residuos de cualquier clase una vez terminada la obra, cuando esta se suspenda por más de dos (2) meses, o cuando sea necesario por seguridad de la misma.', 'Remove scaffolds, barriers, debris and garbage of any kind once the place is completed, when it has been suspended for more than two (2) months, or when it is necessary for safety of the same', 1, 314, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1642, 'Exigir a quienes trabajan y visitan la obra, el uso de cascos e implementos de seguridad industrial y contar con el equipo necesario para prevenir y controlar incendios o atender emergencias de acuerdo con esta ley.', 'Require people who work and visit the place, the use of helmets and industrial safety equipment and have the necessary equipment to prevent and control fires or respond to emergencies according with this law.', 1, 315, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1643, 'Tomar las medidas necesarias para evitar la emisión de partículas en suspensión, provenientes de materi ales de construcción, demolición o desecho, de conformidad con las leyes vigentes.', 'Take the necessary measures to avoid the emission of particles in suspension, coming from materials of construction, demolition or disposal, according with the current law.', 1, 316, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1644, 'Aislar completamente las obras de construcción que se desarrollen aledañas a canales o fuentes de agua, para evitar la contaminación del agua con materiales e implementar las acciones de prevención y mitigación que disponga la autoridad ambiental respectiva.', 'Completely isolate constructions which are being developed adjacent to water sources to avoid contamination of water supplies and implement mitigation actions available to prevention and the respective environmental authority.', 1, 317, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1645, 'Reparar los daños o averías que en razón de la obra se realicen en el andén, las vías, espacios y redes de servicios públicos.', 'To repair the damages or breakdowns that due to the work are carried out on the platform, the roads, spaces and networks of public services.', 1, 318, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1646, 'Reparar los daños, averías o perjuicios causados a bienes colindantes o cercanos.', 'Repair damage, breakdowns or malfunction caused to neighboring or nearby property.', 1, 319, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1647, 'Demoler, construir o reparar obras en el horario comprendido entre las 6 de la tarde y las 8 de la mañana, como también los días festivos, en zonas residenciales.', 'Demolish, build or repair works in the hours between 6 pm and 8 am, as well as public holidays in residential areas.', 1, 320, 385, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1648, 'Omitir el cuidado y mejoramiento de las áreas públicas mediante el mantenimiento, aseo y enlucimiento de las fachadas, jardines y antejardines de las viviendas y edificaciones de uso privado.', 'Avoid the care and improvement of public areas through the maintenance, cleaning and lining of the facades, gardens and gardens of homes and buildings for private use.', 1, 297, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1649, 'Realizar obras de construcción o remodelación en las vías vehiculares o peatonales, en parques, espacios públicos, corredores de transporte público, o similares, sin la debida autorización de la autoridad competente.', 'Make construction or remodeling works on vehicular or pedestrian roads, in parks, public spaces, public transport corridors, or similar, without the proper authorization of the competent authority.', 1, 298, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1650, 'Alterar, remover, dañar o destruir el mobiliario urbano o rural tales como semáforos, señalización vial, teléfonos públicos, hidrantes, estaciones de transporte, faroles o elementos de iluminación, bancas o cestas de basura.', 'Modify, remove, damage or destroy the urban or rural property such as traffic lights, road signs, public telephones, hydrants, transport stations, lanterns or lighting elements, benches or garbage baskets.', 1, 299, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1651, 'Ocupar el espacio público en violación de las normas vigentes.', 'Occupy public space in violation of current regulations.', 1, 300, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1652, 'Ensuciar, dañar o hacer un uso indebido o abusivo de los bienes fiscales o de uso público o contrariar los reglamentos o manuales pertinentes.', 'Dirtying, damaging or making improper or abusive use of fiscal or public use goods or against to regulations or manuals.', 1, 301, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1653, 'Promover o facilitar el uso u ocupación del espacio público en violación de las normas y jurisprudencia constitucional vigente.', 'Promote or facilitate the use or occupation of public space in violation of current constitutional norms and jurisprudence.', 1, 302, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1654, 'Consumir bebidas alcohólicas, sustancias psicoactivas o prohibidas en estadios, coliseos, centros deportivos, parques, hospitales, centros de salud y en general, en el espacio público, excepto en las actividades autorizadas por la autoridad competente.', 'Consume alcoholic beverages, psychoactive or prohibited substances in stadiums, coliseums, sports centers, parks, hospitals, health centers and in general, in public space, except in the activities authorized by the competent authority.', 1, 303, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1655, 'Portar sustancias prohibidas en el espacio público.', 'Carry on prohibited substances in public space.', 1, 304, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1656, 'Escribir o fijar en lugar público o abierto al público, postes, fachadas, antejardines, muros, paredes, elementos físicos naturales, tales como piedras y troncos de árbol, de propiedades públicas o privadas, leyendas, dibujos, grafitis, sin el debido permiso, cuando este se requiera o incumpliendo la normatividad vigente.', 'Write or post in a public place or open to the public, posts, facades, gardens, walls, natural physical elements, such as stones and tree trunks, public or private properties, legends, drawings, graffiti, without permission, when this is required or in breach of current regulations.', 1, 305, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1657, 'Drenar o verter aguas residuales al espacio público, en sectores que cuentan con el servicio de alcantarillado de aguas servidas y en caso de no contar con este, hacerlo incumpliendo la indicación de las autoridades.', 'Drain or discharge sewage into the public space, in sectors that have sewage service and, in the event of not having it, do in violation of the authorities´ instructions.', 1, 306, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1658, 'Realizar neces idades fisiológicas en el espacio público.', 'Perform physiological needs in the public space.', 1, 307, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1659, 'Fijar en espacio público propaganda, avisos o pasacalles, pancartas, pendones, vallas o banderolas, sin el debido permiso o incumpliendo las condiciones establecidas en la normatividad vigente.', 'Place in public space propaganda, notices, banners, fences , without the proper permission or breaching with the conditions established in the current regulations.', 1, 308, 399, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1660, 'Obstruir por cualquier medio la ciclo ruta o carril exclusivo para las bicicletas.', 'Obstruct by any means the bike route or exclusive lane for bicycles.', 1, 297, 407, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1661, 'Arrinconar, obstruir, o dificultar la libre movilidad del usuario de bicicleta.', 'Cornering, obstructing, or hindering the free mobility of the bicycle user.', 1, 298, 407, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1662, 'Realizar o permitir el control informal de los tiempos durante el rodamiento del vehículo, mientras se encuentran estos en circulación.', 'Do or allow informal control of the times during the rolling of the vehicle, while they are in circulation.', 1, 297, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1663, 'Impedir el ingreso o salida prioritaria a mujer embarazada, adulto mayor, persona con niños o niñas, o personas con discapacidad.', 'Block the entrance or exit priority to pregnant woman, elderly, people with children or with disabilities.', 1, 298, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1664, 'Transportar mascotas en vehículos de transporte público incumpliendo la reglamentación establecida para tales efectos por la autoridad competente.', 'Carrying pets in public transportation vehicles against the regulations established for such purposes by the competent authority.', 1, 299, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1665, 'Irrespetar la enumeración y los turnos establecidos en estos medios, así como el sistema de sillas preferenciales, y no ceder el lugar a otra persona por su condición vulnerable .', 'Disrespecting the enumeration and turns established, as well as the system of preferential chairs, and not give way to someone else for their vulnerable condition.', 1, 300, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1666, 'Agredir, empujar o irrespetar a las demás personas durante el acceso, permanencia o salida de estos.', 'Attack, push or disrespect other people during entrance, permanence or exit from it.', 1, 301, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1667, 'Consumir alimentos, bebidas o derivados del tabaco o sustancias cuando estén prohibidas.', 'Consume foods, beverages or derivatives of tobacco or substances when they are prohibited.', 1, 302, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1668, 'Evadir el pago de la tarifa, validación, tiquete o medios que utilicen los usuarios para acceder a la prestación del servicio esencial de transporte público de pasajeros, en cualquiera de sus modalidades.', 'Evade the payment of the rate, validation, ticket or means that users use to access  service of public transportation of passengers, in any of its modalities.', 1, 303, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1669, 'Destruir, obstruir, alterar o dañar los sistemas de alarma o emergencia de los vehículos destinados al transporte público o sus señales indicativas.', 'Destroying, obstructing, modifying or damaging the alarm or emergency systems of vehicles used for public transport or their indicative signals.', 1, 304, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1670, 'Obstaculizar o impedir la movilidad o el flujo de usuarios en estos sistemas.', 'Hinder or impede mobility or the flow of users in these systems.', 1, 305, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1671, 'Poner en peligro la seguridad operacional de los sistemas de transporte masivo, colectivo o individual, aéreo, fluvial o terrestre, con los siguientes comportamientos: \n a) Operar durante el vuelo o sus fases preparatorias, teléfonos móviles, radios transmisores o receptores portátiles, computadoras y demás equipos electrónicos, que puedan interferir con los sistemas de vuelo, comunicaciones o navegación aérea, contrariando las indicaciones de la tripulación; \n b) Transitar, sin autorización de la autoridad aeronáutica, por las pistas de los aeropuertos, rampas o calles de rodaje; \n c) Introducir, sin autorización de las autoridades aeronáuticas, bienes muebles a las pistas, rampas o calles de rodaje de los aeropuertos; \n d) Operar, sin autorización de la autoridad aeronáutica, vehículos aéreos ultralivianos en aeropuertos controlados, parapentes, aeromodelos, paracaídas, cometas tripuladas o no, y demás artefactos de aviación deportiva cerca de las cabeceras de las pistas o dentro de sus zonas de aproximación; \n e) Sustraer, o hacer mal uso de los chalecos salvavidas y demás equipos para la atención en los sistemas de transporte público; \n f) Resistirse a los procesos de seguridad en los filtros de los sistemas de transporte público; \n g) Introducir al medio de transporte cualquier sustancia o elemento que pueda poner en peligro la salud de los tripulantes y demás pasajeros; \n h) Contravenir las obligaciones que se determinen en los reglamentos y/o manuales de uso y operación, que establezcan las autoridades encargadas al respecto;', 'Endanger the operational safety of mass transport systems, collective or individual, air, fluvial or terrestrial, with the following behaviors:  na) Operate during the flight or its preparatory phases, mobile phones, transmitting radios or portable receivers, computers and other electronic equipment, which may interfere with flight systems, communications or air navigation, contrary to the instructions of the crew;  n b) Travel, without authorization from the aeronautical authority, through airport runways, ramps or taxiways;  n c) Introduce, without the authorization of the aeronautical authorities, movable property to the runways, ramps or taxiways of the airports;  nd) Operate, without authorization from the aeronautical authority, ultra light aerial vehicles in controlled airports, Para gliders, model airplanes, parachutes, manned and unmanned kites, and other sports aviation devices near the runway headers or within their approach zones ;  n e) Subtract, or misuse, lifejackets and other equipment for attention in public transport systems;  n f) Resist security processes in the filters of public transport systems;  n g) Introduce to the means of transport any substance or element that could endanger the health of the crew and other passengers;  n h) Contrary the obligations that are determined in the regulations and / or manuals of use and operation, established by the authorities in charge of the matter;', 1, 306, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1672, 'Perturbar en los medios de transporte públicos, la tranquilidad de los demás ocupantes mediante cualquier acto molesto;', 'Disrupt in public transportation, the tranquility of the occupants by any annoying act;', 1, 307, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1673, 'Ingresar y salir de las estaciones o portales por sitios distintos a las puertas designadas para el efecto.', 'Enter and leave the stations or portals through different sites than the designated gates.', 1, 308, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1674, 'Alterar, manipular, deteriorar, destruir o forzar, las puertas de las estaciones o de los buses articulados, metro, tranvía, vehículo férreo, cable aéreo, o de los diferentes medios de transporte de los sistemas de servicio público urbano de transporte masivo de pasajeros, impedir su uso y funcionamiento normal, salvo en situaciones de emergencia.', 'Altering, manipulating, deteriorating, destroying or forcing, the doors of the stations or the articulated buses, metro, tram, railroad vehicle, cablecar, or of the different means of transport of the urban mass transit public service systems , preventing its use and normal operation, except in emergency situations.', 1, 309, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1675, 'Omitir, por parte de las empresas prestadoras del servicio de transporte, el deber de mantener los vehículos de transporte público en condiciones de aseo óptimas para la prestación del servicio.', 'Ignore, on the part of the companies providing the transport service, the duty to maintain the public transport vehicles in optimal conditions of cleanliness for the provision of the service.', 1, 310, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1676, 'Perturbar en los medios de transporte públicos, la tranquilidad de los demás ocupantes mediante cualquier acto obsceno.', 'Disrupt in public transportation, the tranquility of the other occupants by any obscene act.', 1, 311, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1677, 'Irrespetar a las autoridades del sistema.', 'Disrespect the authorities of the system.', 1, 312, 410, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1678, 'Orden de Policía.', 'Police Order.', 1, 297, 415, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1679, 'Permiso excepcional.', 'Exceptional permission', 1, 298, 415, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1680, 'Reglamentos.', 'Regulations', 1, 299, 415, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1681, 'Autorización.', 'Authorization.', 1, 300, 415, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1682, 'Mediación policial. \n Los medios materiales son el conjunto de instrumentos utilizados para e l desarrollo de la función y actividad de Policía. \n Son medios materiales de Policía: \n 1. Traslado por protección. \n 2. Retiro del sitio.  n 3. Traslado para procedimiento policivo. \n 4. Registro. \n 5. Registro a persona. \n 6. Registro a medios de transporte. \n 7. Suspensión inmediata de actividad. \n 8. Ingreso a inmueble con orden escrita. \n 9. Ingreso a inmueble sin orden escrita. \n 10. Incautación. \n 11. Incautación de armas de fuego, no convencionales,  municiones y explosivos. \n 12. Uso de la fuerza. \n 13. Aprehensión con fin judicial. \n 14. Apoyo urgente de los particulares. \n 15. Asistencia militar.', 'Police mediation  n The material means are the set of instruments used for the development of the function and activity of the Police.  n They are material means of Police:  n 1. Transfer for protection.  n 2. Withdrawal from the site.  n 3. Transfer for police procedure.  n 4. Registration.  n 5. Registration to person.  n 6. Registration to means of transport.  n 7. Immediate suspension of activity.  n 8. Entry to property with written order.  n 9. Entry to property without written order.  n 10. Seizure.  n 11. Seizure of firearms, unconventional weapons, ammunition and explosives.  n 12. Use of force.  n 13. Apprehension with judicial purpose.  n 14. Urgent support from individuals.  n 15. Military assistance.', 1, 301, 415, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1683, 'Para establecer la identidad de una persona cuando la persona se resista a aportar la documentación o cuando exista duda sobre la fiabilidad de la identidad.', 'To establish the identity of a person when the person refuses to provide documentation or when there is doubt about the reliability of the identity.', 1, 297, 434, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1684, 'Para establecer si la persona porta armas, municiones, explosivos, elementos cortantes, punzantes, contundentes o sus combinaciones, que amenacen o causen riesgo a la convivencia.', 'To establish if the person carries weapons, ammunition, explosives, cutting, sharp, blunt or combinations thereof that threaten or cause risk to coexistence.', 1, 298, 434, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1685, 'Para establecer si la persona tiene en su poder un bien hurtado o extraviado, o verificar que sea el propietario de un bien que posee, existiendo dudas al respecto.', 'To establish if the person has in his possession a stolen or lost property, or to verify that he is the owner of a property he owns, when there are doubts about it.', 1, 299, 434, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1686, 'Para establecer que la persona no lleve drogas o sustancias prohibidas, de carácter ilícito, contrarios a la ley.', 'To establish that the person does not carry drugs or prohibited substances, of an illicit nature, against the law.', 1, 300, 434, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1687, 'Para prevenir la comisión de una conducta punible o un comportamiento contrario a la convivencia.', 'To prevent the execution of punishable behavior or behavior against coexistence.', 1, 301, 434, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1688, 'Para garantizar la seguridad de los asistentes a una actividad compleja o no compleja o la identidad de una persona que desea ingresar a un lugar.', 'To ensure the safety of those attending a complex or non-complex activity or the identity of a person who wishes to enter a place.', 1, 302, 434, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1689, 'Para aprehender a persona con enfermedad mental que se encuentre en un episodio de la enfermedad de crisis o alteración que pueda considerarse peligrosa o enfermo contagioso.', 'To apprehend a person with mental illness who is in an episode of the crisis, illness or alteration that may be considered dangerous or contagious.', 1, 297, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1690, 'Para inspeccionar algún lugar por motivo de salubridad pública o transgresión de las normas ambientales.', 'To inspect any place for reasons of public health or transgression of environmental regulations.', 1, 298, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1691, 'Para obtener pruebas, cuando existan motivos fundados, sobre la existencia de casas de juego o establecimiento que funcione contra la ley o reglamento.', 'To obtain evidence, when there are well-founded reasons, about the existence of gambling houses or establishments that work against the law or regulation.', 1, 299, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1692, 'Para practicar inspección ordenada en procedimiento de Policía.', 'To practice orderly inspection in Police procedure.', 1, 300, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1693, 'Para examinar instalaciones de energía eléctrica y de gas, chimeneas, hornos, estufas, calderas, motores y máquinas en general y almacenamiento de sustancias inflamables o explosivas con el fin de prevenir accidente o calamidad, cuando existan indicios de riesgo o peligro.', 'To examine electrical and gas installations, chimneys, furnaces, stoves, boilers, engines and machines in general and storage of flammable or explosive substances in order to prevent accidents or calamities, when there are signs of risk or danger.', 1, 301, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1694, 'Verificar que no exista maltrato, abuso o vulneración a los derechos de los niños, niñas y adolescentes, mujeres y adultos mayores y discapacitados.', 'Verify that there is no abuse or violation to the rights of children and adolescents, women, elderly and disabled.', 1, 302, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1695, 'Verificar el desarrollo de actividades económicas, comerciales, industriales, de prestación, venta o depósito de bienes o servicios contrarios a la ley o reglamento.', 'Verify the development of economic, commercial, industrial activities, of provision, sale or deposit of goods or services against the law or regulation.', 1, 303, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1696, 'Cuando se adelante obra en un inmueble, para determinar el cumplimiento de las normas en materia de usos de suelo, obras o urbanismo.', 'When work is carried out in a building, to determine compliance with the rules on land use, construction or urban planning.', 1, 304, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1697, 'En establecimientos públicos o de comercio o en inmuebles donde se estén desarrollando obras o actividades económicas, cuando se requiera practicar diligencia o prueba ordenada en un procedimiento de Policía, para utilizar un medio o para ejecutar una medida correctiva de Policía.', 'In public or commercial establishments or in buildings where works or economic activities are being carried out, when it is required to practice diligence or orderly test in a Police procedure, to use a mean or to execute a corrective measure of Police.', 1, 305, 445, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1698, 'Para socorrer a alguien que de alguna manera pida auxilio.', 'To help someone who in some way asks for help.', 1, 297, 450, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1699, 'Para extinguir incendio o evitar su propagación o remediar inundación o conjurar cualquier otra situación similar de peligro.', 'To extinguish fire or prevent its spread or remediate flood or conjure up any other similar dangerous situation.', 1, 298, 450, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1700, 'Para dar caza a animal rabioso o feroz.', 'To hunt rabid or ferocious animal.', 1, 299, 450, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1701, 'Para proteger los bienes de personas ausentes, cuando se descubra que un extraño ha penetrado violentamente o por cualquier otro medio al domicilio de estas personas.', 'To protect the property of absent persons, when it is discovered that a stranger has penetrated violently or by any other means to the domicile of these persons.', 1, 300, 450, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1702, 'Cuando desde el interior de una casa o edificio se proceda por la vía de hecho contra persona o propiedad que se halle fuera de estos.', 'When from the inside of a house or building, a de facto proceeding is executed acts against people or property that is outside of these.', 1, 301, 450, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1703, 'Para proteger la vida e integridad de las personas, si en el interior del inmueble o domicilio se están manipulando o usando fuegos pirotécnicos, juegos artificiales, pólvora o globos sin el debido cumplimiento de los requisitos establecidos en la ley.', 'To protect the life and integrity of people, if inside the property or home are being manipulated or using fireworks, gunpowder or balloons without due compliance with the requirements established by law.', 1, 302, 450, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1704, 'Para prevenir la inminente o actual comisión de comportamientos contrarios a la convivencia, de conformidad con lo dispuesto en el régimen de Policía y en otras normas.', 'To prevent the imminent or current commission of behaviors against coexistence, in accordance with the provisions of the Police regime and other regulations.', 1, 297, 456, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1705, 'Para hacer cumplir las medidas correctivas contempladas en este Código, las decisiones judiciales y obligaciones de ley, cuando exista oposición o resistencia.', 'To enforce the corrective measures contemplated in this Code, judicial decisions and obligations of law, when there is opposition or resistance.', 1, 298, 456, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1706, 'Para defenderse o defender a otra persona de una violencia actual o inminente contra su integridad y la de sus bienes, o protegerla de peligro inminente y grave.', 'To defend oneself or defend another person from current or imminent violence against their integrity and that of their property, or to protect it from imminent and serious danger.', 1, 299, 456, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1707, 'Para prevenir una e mergencia o calamidad pública o evitar mayores peligros, daños o perjuicios, en caso de haber ocurrido la emergencia o calamidad pública.', 'To prevent an emergency or public calamity or to avoid greater dangers, damages or losses, in the event of an emergency or public calamity.', 1, 300, 456, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1708, 'Para hacer cumplir los medios inmateriales y materiales, cuando se presente oposición o resistencia, se apele a la amenaza, o a medios violentos.', 'To enforce the immaterial and material means, when there is opposition or resistance, appeals to the threat, or to violent means.', 1, 301, 456, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1709, 'Amonestación.', 'Admonition.', 1, 297, 469, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1789, 'Conocer, en única instancia, de la aplicación de las siguientes medidas correctivas: \n a) Reparación de daños materiales de muebles o inmuebles; \n b) Expulsión de domicilio; \n c) Prohibición de ingreso a actividad que involucra aglomeraciones de público complejas o no complejas; \n d) Decomiso. \n 6. Conocer en primera instancia de la aplicación de las siguientes medidas correctivas: \n a) Suspensión de construcción o demolición; \n b) Demolición de obra; \n c) Construcción, cerramiento, reparación o mantenimiento de inmueble; \n d) Reparación de daños materiales por perturbación a la posesión y tenencia de inmuebles; \n e) Restitución y protección de bienes inmuebles, diferentes a los descritos en el numeral 17 del artículo 205; \n f) Restablecimiento del derecho de servidumbre y reparación de daños materiales; \n g) Remoción de bienes, en las infracciones urbanísticas; \n h) Multas; \n i) Suspensión definitiva de actividad. ', 'To know, in a single instance, the application of the following corrective measures:  n a) Repair of material or real property damage;  n b) Expulsion of address;  n c) Prohibition of entry into activity that involves complex or non-complex public agglomerations;  n d) Confiscation.  n 6. Know in the first instance the application of the following corrective measures:  n a) Suspension of construction or demolition;  n b) Demolition work;  n c) Construction, closing, repair or maintenance of property;  n d) Repair of material damages due to disturbance to the possession and possession of real estate;  n e) Restitution and protection of immovable property, different from those described in numeral 17 of article 205;  n f) Restoration of right of easement and repair of material damage;  n g) Removal of property, in urban infractions;  n h) Fines;  n i) Definitive suspension of activity.', 1, 301, 522, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1790, 'Los comportamientos contrarios a la convivencia.', 'Behaviors against coexistence.', 1, 297, 528, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1791, 'Conocer en primera instancia de la aplicación de las siguientes medidas: \n a) Amonestación; \n b) Remoción de bienes que obstaculizan el espacio público; \n c) Inutilización de bienes; \n d) Destrucción de bien; \n e) Disolución de reunión o actividad que involucra aglomeraciones de público no complejas; \n f) Participación en programa comunitario o actividad pedagógica de convivencia.', 'Know in the first instance of the application of the following measures:  n a) Warning;  n b) Removal of property that obstructs public space;  N c) disablement of goods;  n d) Destruction of good;  n e) Dissolution of meeting or activity that involves non-complex public gatherings;  n f) Participation in a community program or educational activity of coexistence.', 1, 298, 528, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1792, 'Conocer en primera instancia la aplicación de la medida de suspensión temporal de la actividad.', 'Know in the first instance the application of the measure of temporary suspension of the activity.', 1, 299, 528, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1793, 'Los comportamientos contrarios a la convivencia.', 'Behaviors against coexistence.', 1, 297, 529, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1794, 'Conocer en primera instancia la aplicación de las siguientes medidas de conformidad con el proceso verbal inmediato de Policía contenido en el presente Código: \n a) Amonestación; \n b) Participación en Programa comunitario o Actividad Pedagógica de Convivencia; \n c) Remoción de Bienes que Obstaculizan el Espacio Público; \n d) Inutilización de bienes; \n e) Destrucción de bien.', 'Know in the first instance the application of the following measures in accordance with the immediate verbal process of Police contained in this Code:  n a) Warning;  n b) Participation in a Community Program or Pedagogical Coexistence Activity;  n c) Removal of Goods that Obstruct Public Space;  N d) disablement of goods;  n e) Destruction of good.', 1, 298, 529, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1795, 'Presentar conceptos ante las autoridades de Policía sobre la legalidad o constitucionalidad de los actos o procedimientos realizados por estas.', 'Present concepts before the police authorities on the legality or constitutionality of the acts or procedures performed by them.', 1, 297, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1796, 'Solicitar motivadamente la suspensión de actividades o decisiones de las autoridades de Policía, en defensa de los derechos humanos o del orden constitucional y legal y bajo su estricta responsabilidad.', 'Motivate request the suspension of activities or decisions of the police authorities, in defense of human rights or the constitutional and legal order and under their strict responsibility.', 1, 298, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1797, 'Asistir o presenciar cualquier actividad de Policía y manifestar su desacuerdo de manera motivada, así como interponer las acciones constitucionales o legales que corresponda.', 'Attend or witness any police activity and express their disagreement in a motivated manner, as well as interpose the corresponding constitutional or legal actions.', 1, 299, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1798, 'Realizar actividades de vigilancia especial a los procedimientos policivos, a solicitud de parte o en defensa de los intereses colectivos.', 'Carry out special surveillance activities for police procedures, at the request of a party or in defense of collective interests.', 1, 300, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1799, 'Vigilar la conducta de las autoridades de Policía y poner en conocimiento de la autoridad competente cualquier conducta que viole el régimen penal o disciplinario.', 'Monitor the conduct of the police authorities and inform the competent authority of any conduct that violates the criminal or disciplinary system.', 1, 301, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1800, 'Recibir verbalmente o por escrito las quejas o denuncias que formulen los ciudadanos contra los abusos, faltas o delitos cometidos por las autoridades de Policía.', 'Receive verbally or in writing grievance or complaints made by citizens against abuses, misdemeanors or crimes committed by police authorities.', 1, 302, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1801, 'Las demás que establezcan las autoridades municipales, departamentales, distritales o nacionales en el ámbito de sus competencias.', 'The others that establish the municipal, departmental, district or national authorities in the scope of their competences.', 1, 303, 532, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1802, 'En el caso de los comportamientos que incluyen multa como medida correctiva: \n El incumplimiento de las medidas correctivas o la reiteración del comportamiento darán lugar a la imposición de la multa correspondiente al comportamiento aumentada en un cincuenta por ciento (50%). \n La reiteración del comportamiento dentro del año siguiente a la imposición de la mencionada multa, dará lugar a la imposición de la multa correspondiente al comportamiento aumentada en un setenta y cinco por ciento (75%).', 'In the case of behaviors that include a fine as a corrective measure:  n Failure to comply with the corrective measures or repetition of the behavior will result in the imposition of the fine corresponding to the behavior increased by fifty percent (50%).  n The repetition of the behavior within the year following the imposition of the aforementioned fine, will result in the imposition of the fine corresponding to the behavior increased by seventy-five percent (75%).', 1, 297, 534, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1803, 'El incumplimiento de la medida correctiva de participación en programa comunitario o actividad pedagógica de convivencia, dará lugar a la imposición de Multa General tipo 1. \n La reiteración del comportamiento que dio lugar a la imposición de la medida correctiva de participación en programa comunitario o actividad pedagógica de convivencia, dentro del año siguiente a la imposición de la mencionada multa, dará lugar a la imposición de Multa General tipo 1 aumentada en un setenta y cinco por ciento (75%).', 'Failure to comply with the corrective measure of participation in a community program or educational activity of coexistence will lead to the imposition of a General Fine Type 1.  n The repetition of the behavior that led to the imposition of the corrective measure of participation in a community program or Pedagogic activity of coexistence, within the year following the imposition of the aforementioned fine, will result in the imposition of a General Type 1 fine increased by seventy-five percent (75%).', 1, 298, 534, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1804, 'El informe de Policía.', 'The police report.', 1, 297, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1805, 'Los documentos.', 'Documents.', 1, 298, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1806, 'El testimonio.', 'The testimony.', 1, 299, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1807, 'La entrevista.', 'The interview.', 1, 300, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1808, 'La inspección.', 'The inspection.', 1, 301, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1809, 'El peritaje.', 'The expertise', 1, 302, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1810, 'Los demás medios consagrados en la Ley 1564 de 2012 \n La práctica de los medios probatorios se ceñirá a los términos y procedimientos establecidos en el presente Código.', 'The other means enshrined in Law 1564 of 2012  n The practice of probative means will adhere to the terms and procedures established in this Code.', 1, 303, 542, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1811, 'Se podrá iniciar de oficio o a petición de quien tenga interés directo o acuda en defensa de las normas de convivencia.', 'It may be initiated ex officio or at the request of someone who has a direct interest or comes in defense of the rules of coexistence.', 1, 297, 549, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1812, 'Una vez identificado el presunto infractor, la autoridad de Policía lo abordará en el sitio donde ocurran los hechos, si ello fuera posible o, en aquel donde lo encuentren, y le informará que su acción u omisión configura un comportamiento contrario a la convivencia.', 'Once the alleged offender is identified, the police authority will address him at the place where the events occur, if possible or, where he is found, and inform him that his action or omission forms a behavior against coexistence.', 1, 298, 549, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1813, 'El presunto infractor deberá ser oído en descargos.', 'The alleged offender must be heard on defense.', 1, 299, 549, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1814, 'La autoridad de Policía hará una primera ponderación de los hechos y procurará una mediación policial entre las partes en conflicto. De no lograr la mediación, impondrá la medida correctiva a través de la orden de Policía.', 'The police authority will first evaluate the facts and seek police mediation between the parties of the conflict. If the mediation is not successful, it will impose the corrective measure through the Police order.', 1, 300, 549, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1815, 'Iniciación de la acción. La acción de Policía puede iniciarse de oficio o a petición de la persona que tenga interés en la aplicación del régimen de Policía, contra el presunto infractor. Cuando la autoridad conozca en flagrancia del comportamiento contrario a la convivencia, podrá iniciar de inmediato la audiencia pública.', 'Initiation of the action. The police action can be initiated ex officio or at the request of the person who has an interest in the application of the police regime, against the alleged offender. When the authority knows in flagrancy of the behavior against coexistence, it may immediately initiate the public hearing.', 1, 297, 553, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1816, 'Citación. Las mencionadas autoridades, a los cinco (5) días siguientes de conocida la querella o el comportamiento contrario a la convivencia, en caso de que no hubiera sido posible iniciar la audiencia de manera inmediata, citará a audiencia pública al quejoso y al presunto infractor, mediante comunicación escrita, correo certificado, medio electrónico, medio de comunicación del que disponga, o por el medio más expedito o idóneo, donde se señale dicho comportamiento.', 'Citation. The aforementioned authorities, within five (5) days of the well-known complaint or the behavior against coexistence, in case it would not have been possible to initiate the hearing immediately, they shall summon the complainant and the alleged offender to a public hearing, by means of written communication, certified mail, electronic means, means of communication at your disposal, or by the most expeditious or suitable means, where said behavior is indicated.', 1, 298, 553, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1710, 'Participación en programa comunitario o actividad pedagógica de convivencia.', 'Participation in community program or pedagogical activity of coexistence.', 1, 298, 469, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1711, 'Disolución de reunión o actividad que involucra aglomeraciones de público no complejas.', 'Dissolution of meeting or activity that involves non-complex public agglomerations.', 1, 299, 469, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1712, 'Expulsión de domicilio.', 'Expulsion of address.', 1, 300, 469, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1713, 'Prohibición de ingreso a actividad que involucra aglomeraciones de público complejas o no complejas.', 'Prohibition of entry into activity that involves complex or non-complex public agglomerations.', 1, 301, 469, '29/11/17');
INSERT INTO NUMERAL (ID, NUMERAL_ESP, NUMERAL_ENG, VIGENTE, NIVEL_ID, ARTICULO_ID, FECHA) VALUES (1714, 'Decomiso.', 'Confiscation', 1, 302, 469, '29/11/17');
